INSERT INTO `post_lists` (`id`, `title`, `content`, `quotes_content`, `author_id`, `post_category_id`, `status`, `image_avatar`, `view`, `created_at`, `updated_at`) VALUES
(19, 'Phát triển quan hệ Đà Nẵng - Sakai lên tầm cao mới', '<p>S&aacute;ng 28-7, tiếp đo&agrave;n c&ocirc;ng t&aacute;c Hội đồng th&agrave;nh phố Sakai (tỉnh Osaka, Nhật Bản) do b&agrave; Noriko Yamaguchi, Chủ tịch Hội đồng th&agrave;nh phố Sakai l&agrave;m trưởng đo&agrave;n, Chủ tịch HĐND th&agrave;nh phố Nguyễn Nho Trung cho biết, Đ&agrave; Nẵng đang ph&aacute;t triển để xứng tầm l&agrave; th&agrave;nh phố động lực của miền Trung, l&agrave; đầu t&agrave;u kinh tế, trung t&acirc;m giao lưu văn h&oacute;a, hợp t&aacute;c của khu vực.</p>\r\n\r\n<p>Về quan hệ hợp t&aacute;c Đ&agrave; Nẵng - Sakai, Chủ tịch HĐND th&agrave;nh phố Nguyễn Nho Trung nhấn mạnh, cả hai th&agrave;nh phố triển khai nhiều chương tr&igrave;nh hợp t&aacute;c về kinh tế, thu h&uacute;t đầu tư, giao lưu văn h&oacute;a quan trọng như: chương tr&igrave;nh &ldquo;Đại sứ nh&acirc;n d&acirc;n&rdquo;, tổ chức nhiều hoạt động t&igrave;m hiểu đầu tư tăng cường kết nối doanh nghiệp, tham gia Lễ hội giao lưu văn h&oacute;a Việt - Nhật tại Đ&agrave; Nẵng.</p>\r\n\r\n<p>Chủ tịch HĐND th&agrave;nh phố Nguyễn Nho Trung mong muốn trong thời gian tới hai b&ecirc;n sẽ tiếp tục triển khai nhiều chương tr&igrave;nh hợp t&aacute;c cụ thể tr&ecirc;n c&aacute;c lĩnh vực như du lịch, trao đổi nh&acirc;n lực giữa c&aacute;c cơ quan ch&iacute;nh quyền, văn h&oacute;a - thể thao, gi&aacute;o dục.</p>\r\n\r\n<p>Tại buổi l&agrave;m việc, l&atilde;nh đạo c&aacute;c sở, ng&agrave;nh th&agrave;nh phố đề xuất với ch&iacute;nh quyền th&agrave;nh phố Sakai một số lĩnh vực hợp t&aacute;c trong thời gian tới, như: chia sẻ kinh nghiệm với ch&iacute;nh quyền Đ&agrave; Nẵng trong thực hiện 17 mục ti&ecirc;u ph&aacute;t triển bền vững của Li&ecirc;n Hợp Quốc đến năm 2030; giao lưu, hợp t&aacute;c giữa c&aacute;c doanh nghiệp vừa v&agrave; nhỏ của hai địa phương nhằm g&oacute;p phần ph&aacute;t triển ng&agrave;nh c&ocirc;ng nghiệp hỗ trợ; hợp t&aacute;c ph&aacute;t triển dịch vụ logistics theo hướng xanh, th&acirc;n thiện với m&ocirc;i trường.</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Chủ tịch HĐND thành phố Nguyễn Nho Trung (bên trái) tặng quà lưu niệm cho bà Noriko Yamaguchi, Chủ tịch Hội đồng thành phố Sakai. Ảnh: Q.KHẢI\" src=\"http://baodanang.vn/dataimages/201807/original/images1461999_Sa_kai.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Chủ tịch HĐND th&agrave;nh phố Nguyễn Nho Trung (b&ecirc;n tr&aacute;i) tặng qu&agrave; lưu niệm cho b&agrave; Noriko Yamaguchi, Chủ tịch Hội đồng th&agrave;nh phố Sakai. Ảnh: Q.KHẢI</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>B&agrave; Noriko Yamaguchi, Chủ tịch Hội đồng th&agrave;nh phố Sakai b&agrave;y tỏ ấn tượng trước sự ph&aacute;t triển kh&ocirc;ng ngừng của th&agrave;nh phố Đ&agrave; Nẵng trong thời gian qua; ng&agrave;y c&agrave;ng c&oacute; nhiều du kh&aacute;ch quốc tế chọn Đ&agrave; Nẵng l&agrave; nơi tham quan, nghỉ dưỡng ưa th&iacute;ch.</p>\r\n\r\n<p>B&agrave; Noriko Yamaguchi hy vọng, th&ocirc;ng qua chuyến thăm sẽ gi&uacute;p c&aacute;c đại biểu của Hội đồng th&agrave;nh phố Sakai c&oacute; c&aacute;i nh&igrave;n to&agrave;n diện về triển vọng hợp t&aacute;c giữa hai b&ecirc;n, từ đ&oacute; xem x&eacute;t việc th&agrave;nh lập v&agrave; th&ocirc;ng qua nghị quyết về hợp t&aacute;c, kết nghĩa giữa th&agrave;nh phố Sakai với Đ&agrave; Nẵng.</p>\r\n\r\n<p>Về những đề xuất hợp t&aacute;c của c&aacute;c sở, ng&agrave;nh th&agrave;nh phố Đ&agrave; Nẵng, b&agrave; Noriko Yamaguchi cho biết Hội đồng th&agrave;nh phố Sakai sẽ quan t&acirc;m, nghi&ecirc;n cứu v&agrave; sớm c&oacute; trao đổi cụ thể.</p>\r\n\r\n<p>Li&ecirc;n quan đến việc Hội đồng th&agrave;nh phố Sakai đề nghị xem x&eacute;t n&acirc;ng cấp quan hệ hữu nghị v&agrave; hợp t&aacute;c giữa Sakai v&agrave; Đ&agrave; Nẵng trở th&agrave;nh quan hệ địa phương kết nghĩa, Chủ tịch HĐND th&agrave;nh phố Nguyễn Nho Trung cho biết HĐND, UBND th&agrave;nh phố đ&aacute;nh gi&aacute; cao v&agrave; sẵn s&agrave;ng ủng hộ để quan hệ hữu nghị v&agrave; hợp t&aacute;c giữa hai địa phương được n&acirc;ng tầm, ng&agrave;y c&agrave;ng đi v&agrave;o chiều s&acirc;u v&agrave; hiệu quả.</p>', 'Sáng 28-7, tiếp đoàn công tác Hội đồng thành phố Sakai (tỉnh Osaka, Nhật Bản) do bà Noriko Yamaguchi, Chủ tịch Hội đồng thành phố Sakai làm trưởng đoàn, Chủ tịch HĐND thành phố Nguyễn Nho Trung cho biết, Đà Nẵng đang phát triển để xứng tầm là thành phố động lực của miền Trung, là đầu tàu kinh tế, trung tâm giao lưu văn hóa, hợp tác của khu vực.', '4', '4', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/PO8wIgUjBWZBUkhPw2MglOS1E8EOpaq4dyMPAugU.jpeg', 1, '2018-07-29 21:06:29', '2018-08-03 04:35:07'),
(20, 'Ban Tuyên huấn Khu ủy 5: Đón nhận danh hiệu Anh hùng lực lượng vũ trang nhân dân', '<p>S&aacute;ng 29-7, tại th&agrave;nh phố Tam Kỳ, tỉnh Quảng Nam, Ban Tuy&ecirc;n gi&aacute;o Trung ương tổ chức trọng thể lễ đ&oacute;n nhận danh hiệu Anh h&ugrave;ng lực lượng vũ trang nh&acirc;n d&acirc;n cho Ban Tuy&ecirc;n huấn Khu ủy 5. Đến dự c&oacute; Thủ tướng Ch&iacute;nh phủ Nguyễn Xu&acirc;n Ph&uacute;c, Ph&oacute; Thủ tướng Thường trực Ch&iacute;nh phủ Trương H&ograve;a B&igrave;nh, Trưởng ban Tuy&ecirc;n gi&aacute;o Trung ương V&otilde; Văn Thưởng.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Thủ tướng Chính phủ Nguyễn Xuân Phúc (thứ 4, từ phải sang) trao danh hiệu Anh hùng lực lượng vũ trang nhân dân cho đại diện Ban Tuyên huấn Khu ủy 5. Ảnh: L.Phương\" src=\"http://baodanang.vn/dataimages/201807/original/images1461998_Phuc.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Thủ tướng Ch&iacute;nh phủ Nguyễn Xu&acirc;n Ph&uacute;c (thứ 4, từ phải sang) trao danh hiệu Anh h&ugrave;ng lực lượng vũ trang nh&acirc;n d&acirc;n cho đại diện Ban Tuy&ecirc;n huấn Khu ủy 5. Ảnh: L.Phương</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ph&aacute;t biểu tại buổi lễ, Trưởng ban Tuy&ecirc;n gi&aacute;o Trung ương V&otilde; Văn Thưởng ch&uacute;c mừng đội ngũ l&agrave;m c&ocirc;ng t&aacute;c tuy&ecirc;n gi&aacute;o n&oacute;i chung v&agrave; Ban Tuy&ecirc;n huấn Khu ủy 5 n&oacute;i ri&ecirc;ng nhận phần thưởng cao qu&yacute; của Đảng v&agrave; Nh&agrave; nước trao tặng; đồng thời biểu dương th&agrave;nh t&iacute;ch, c&ocirc;ng lao v&agrave; những đ&oacute;ng g&oacute;p to lớn của Ban Tuy&ecirc;n huấn Khu ủy 5 đối với sự nghiệp c&aacute;ch mạng của Đảng trong c&ocirc;ng cuộc kh&aacute;ng chiến chống Mỹ, cứu nước.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; nguồn động vi&ecirc;n, kh&iacute;ch lệ những người l&agrave;m c&ocirc;ng t&aacute;c tuy&ecirc;n huấn Khu ủy 5 trước đ&acirc;y, cũng như đội ngũ những người l&agrave;m c&ocirc;ng t&aacute;c tuy&ecirc;n gi&aacute;o của Đảng hiện nay.</p>\r\n\r\n<p>Th&aacute;ng 5-1960, Thường vụ Khu ủy 5 ra quyết định th&agrave;nh lập Ban Tuy&ecirc;n huấn Khu ủy 5. Với số lượng ban đầu l&agrave; 3 c&aacute;n bộ, Ban Tuy&ecirc;n huấn Khu ủy 5 đ&atilde; ph&aacute;t triển lực lượng l&ecirc;n đến hơn 1.000 c&aacute;n bộ, gi&aacute;o vi&ecirc;n, nh&acirc;n vi&ecirc;n. Ban Tuy&ecirc;n huấn Khu ủy 5 c&oacute; nhiệm vụ theo d&otilde;i, nắm bắt t&igrave;nh h&igrave;nh ch&iacute;nh trị, tư tưởng, dư luận x&atilde; hội tr&ecirc;n chiến trường Khu 5, trải d&agrave;i từ Quảng Nam - Đ&agrave; Nẵng đến Ninh Thuận v&agrave; c&aacute;c tỉnh T&acirc;y Nguy&ecirc;n; tham mưu, đề xuất với Khu ủy 5 về c&ocirc;ng t&aacute;c ch&iacute;nh trị, tư tưởng; gi&aacute;o dục, r&egrave;n luyện về ch&iacute;nh trị, tư tưởng cho c&aacute;n bộ, chiến sĩ, n&acirc;ng cao cảnh gi&aacute;c c&aacute;ch mạng cho quần ch&uacute;ng nh&acirc;n d&acirc;n, cổ vũ mọi tầng lớp nh&acirc;n d&acirc;n ở miền Trung - T&acirc;y Nguy&ecirc;n đứng l&ecirc;n đấu tranh giữ vững th&agrave;nh quả c&aacute;ch mạng. C&ugrave;ng với đ&oacute;, Ban Tuy&ecirc;n huấn Khu ủy 5 t&iacute;ch cực tham gia c&ocirc;ng t&aacute;c binh vận, l&agrave;m tốt c&ocirc;ng t&aacute;c tuy&ecirc;n truyền đối ngoại, tố c&aacute;o tội &aacute;c của Mỹ - ngụy, tạo được sự đồng t&igrave;nh ủng hộ của dư luận tiến bộ tr&ecirc;n thế giới, g&oacute;p phần đưa cuộc chiến đấu chống ngoại x&acirc;m của qu&acirc;n v&agrave; d&acirc;n ta đi đến thắng lợi. C&aacute;n bộ, chiến sĩ của Ban Tuy&ecirc;n huấn Khu ủy 5 kh&ocirc;ng chỉ l&agrave; chiến sĩ tr&ecirc;n mặt trận tư tưởng - văn h&oacute;a của Đảng m&agrave; c&ograve;n tham gia chiến đấu dũng cảm tr&ecirc;n chiến trường &aacute;c liệt, bảo vệ an to&agrave;n căn cứ. Trong 1.058 c&aacute;n bộ của Ban Tuy&ecirc;n huấn Khu ủy 5, c&oacute; 128 c&aacute;n bộ anh dũng hy sinh, h&agrave;ng trăm người bị thương hoặc nhiễm chất độc da cam&hellip;</p>\r\n\r\n<p>Tại buổi lễ, thay mặt l&atilde;nh đạo Đảng v&agrave; Nh&agrave; nước, Thủ tướng Ch&iacute;nh phủ Nguyễn Xu&acirc;n Ph&uacute;c trao danh hiệu Anh h&ugrave;ng lực lượng vũ trang nh&acirc;n d&acirc;n cho Ban Tuy&ecirc;n huấn Khu ủy 5 v&igrave; những th&agrave;nh t&iacute;ch đặc biệt xuất sắc trong cuộc đấu tranh chống Mỹ, cứu nước. Đ&acirc;y l&agrave; sự ghi nhận của Đảng v&agrave; Nh&agrave; nước đối với sự đ&oacute;ng g&oacute;p to lớn của c&aacute;n bộ, chiến sĩ Ban Tuy&ecirc;n huấn Khu ủy 5.</p>', 'Sáng 29-7, tại thành phố Tam Kỳ, tỉnh Quảng Nam, Ban Tuyên giáo Trung ương tổ chức trọng thể lễ đón nhận danh hiệu Anh hùng lực lượng vũ trang nhân dân cho Ban Tuyên huấn Khu ủy 5. Đến dự có Thủ tướng Chính phủ Nguyễn Xuân Phúc, Phó Thủ tướng Thường trực Chính phủ Trương Hòa Bình, Trưởng ban Tuyên giáo Trung ương Võ Văn Thưởng.', '4', '4', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/bwRmZXrMqOvv5cBy54IYuEisOqHn2Q4pgGUWAKmU.jpeg', 0, '2018-07-29 21:08:22', '2018-08-03 01:31:11'),
(21, 'Bộ Chính trị cách chức các tướng Bùi Văn Thành, Trần Việt Tân', '<p>Ng&agrave;y 28-7-2018, tại Trụ sở Trung ương Đảng, Bộ Ch&iacute;nh trị, Ban B&iacute; thư đ&atilde; họp dưới sự chủ tr&igrave; của đồng ch&iacute; Tổng B&iacute; thư Nguyễn Ph&uacute; Trọng.</p>\r\n\r\n<p>Sau khi xem x&eacute;t đề nghị của Uỷ ban Kiểm tra Trung ương tại Tờ tr&igrave;nh số 130-TTr/UBKTTW, ng&agrave;y 27-7-2018 về việc đề nghị xem x&eacute;t, thi h&agrave;nh kỷ luật Trung tướng B&ugrave;i Văn Th&agrave;nh, Uỷ vi&ecirc;n Ban Chấp h&agrave;nh Đảng bộ C&ocirc;ng an Trung ương, Thứ trưởng Bộ C&ocirc;ng an, Bộ Ch&iacute;nh trị nhận thấy &ocirc;ng B&ugrave;i Văn Th&agrave;nh, với cương vị Uỷ vi&ecirc;n Ban Chấp h&agrave;nh Đảng bộ C&ocirc;ng an Trung ương, Thứ trưởng Bộ C&ocirc;ng an trực tiếp phụ tr&aacute;ch Tổng cục Hậu cần - Kỹ thuật, nguy&ecirc;n Uỷ vi&ecirc;n Ban Thường vụ Đảng uỷ, nguy&ecirc;n Ph&oacute; Tổng cục trưởng Tổng cục Hậu cần - Kỹ thuật c&ugrave;ng chịu tr&aacute;ch nhiệm về những vi phạm, khuyết điểm của Ban Thường vụ Đảng uỷ Tổng cục Hậu cần - Kỹ thuật.</p>\r\n\r\n<p>C&aacute; nh&acirc;n &ocirc;ng Th&agrave;nh đ&atilde; vi phạm nguy&ecirc;n tắc tập trung d&acirc;n chủ, thiếu tr&aacute;ch nhiệm, bu&ocirc;ng lỏng l&atilde;nh đạo, quản l&yacute;, thiếu kiểm tra, gi&aacute;m s&aacute;t, để xảy ra nhiều vi phạm, khuyết điểm tại Tổng cục Hậu cần - Kỹ thuật; vi phạm c&aacute;c quy định về bảo vệ b&iacute; mật nh&agrave; nước v&agrave; Quy chế l&agrave;m việc của Bộ C&ocirc;ng an; k&yacute; văn bản của Bộ C&ocirc;ng an đề xuất b&aacute;n chỉ định một số cơ sở nh&agrave;, đất an ninh kh&ocirc;ng đ&uacute;ng quy định ph&aacute;p luật; k&yacute; một số văn bản kh&ocirc;ng thuộc tr&aacute;ch nhiệm được ph&acirc;n c&ocirc;ng; tự &yacute; k&yacute; quyết định cho Phan Văn Anh Vũ tham gia đo&agrave;n đi nước ngo&agrave;i v&agrave; đề nghị cấp hộ chiếu ngoại giao cho Phan Văn Anh Vũ kh&ocirc;ng đ&uacute;ng đối tượng, kh&ocirc;ng đ&uacute;ng ti&ecirc;u chuẩn.</p>\r\n\r\n<p>Những vi phạm của Trung tướng B&ugrave;i Văn Th&agrave;nh g&acirc;y hậu quả rất nghi&ecirc;m trọng, ảnh hưởng rất xấu đến uy t&iacute;n của tổ chức đảng, ng&agrave;nh C&ocirc;ng an v&agrave; c&aacute; nh&acirc;n đồng ch&iacute;, g&acirc;y dư luận xấu v&agrave; bức x&uacute;c trong x&atilde; hội.</p>\r\n\r\n<p>Căn cứ Quy chế l&agrave;m việc của Ban Chấp h&agrave;nh Trung ương, Bộ Ch&iacute;nh trị, Ban B&iacute; thư kho&aacute; XII; căn cứ Quy định số 102-QĐ/TW của Bộ Ch&iacute;nh trị về xử l&yacute; đảng vi&ecirc;n vi phạm, Bộ Ch&iacute;nh trị quyết định thi h&agrave;nh kỷ luật &ocirc;ng B&ugrave;i Văn Th&agrave;nh bằng h&igrave;nh thức C&aacute;ch chức tất cả c&aacute;c chức vụ trong Đảng (Uỷ vi&ecirc;n Ban Chấp h&agrave;nh Đảng bộ C&ocirc;ng an Trung ương nhiệm kỳ 2016 - 2021; Uỷ vi&ecirc;n Ban Chấp h&agrave;nh Đảng bộ Tổng cục Hậu cần - Kỹ thuật Bộ C&ocirc;ng an nhiệm kỳ 2010 - 2015).</p>\r\n\r\n<p>Bộ Ch&iacute;nh trị giao Ban c&aacute;n sự đảng Ch&iacute;nh phủ chỉ đạo thực hiện quy tr&igrave;nh xử l&yacute; kỷ luật về h&agrave;nh ch&iacute;nh v&agrave; gi&aacute;ng cấp bậc h&agrave;m đối với &ocirc;ng B&ugrave;i Văn Th&agrave;nh, bảo đảm đồng bộ, kịp thời, tương xứng với kỷ luật của Đảng theo quy định.&nbsp;</p>\r\n\r\n<table align=\"center\" style=\"width:490px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Ông Bùi Văn Thành và ông Trần Việt Tân bị Bộ Chính trị cách chức trong Đảng và sẽ bị giáng cấp bậc hàm. Ông Phương Minh Hoà (ngoài cùng bên phải) bị Ban Bí thư kỷ luật cảnh cáo.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461838_bui_van_thanh_sggf.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&Ocirc;ng B&ugrave;i Văn Th&agrave;nh v&agrave; &ocirc;ng Trần Việt T&acirc;n bị Bộ Ch&iacute;nh trị c&aacute;ch chức trong Đảng v&agrave; sẽ bị gi&aacute;ng cấp bậc h&agrave;m. &Ocirc;ng Phương Minh Ho&agrave; (ngo&agrave;i c&ugrave;ng b&ecirc;n phải) bị Ban B&iacute; thư kỷ luật cảnh c&aacute;o.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Sau khi xem x&eacute;t Tờ tr&igrave;nh số 131-TTr/UBKTTW, ng&agrave;y 27-7-2018 của Uỷ ban Kiểm tra Trung ương về việc đề nghị xem x&eacute;t, thi h&agrave;nh kỷ luật Thượng tướng Trần Việt T&acirc;n, nguy&ecirc;n Uỷ vi&ecirc;n Ban Chấp h&agrave;nh Đảng bộ C&ocirc;ng an Trung ương, nguy&ecirc;n Thứ trưởng Bộ C&ocirc;ng an, Bộ Ch&iacute;nh trị nhận thấy Thượng tướng Trần Việt T&acirc;n, trong thời gian giữ cương vị Uỷ vi&ecirc;n Ban Chấp h&agrave;nh Đảng bộ C&ocirc;ng an Trung ương, Thứ trưởng Bộ C&ocirc;ng an, đ&atilde; thiếu tr&aacute;ch nhiệm, bu&ocirc;ng lỏng l&atilde;nh đạo, chỉ đạo, thiếu kiểm tra, gi&aacute;m s&aacute;t, đ&atilde; k&yacute; một số văn bản vi phạm quy định về bảo vệ b&iacute; mật nh&agrave; nước, g&acirc;y hậu quả rất nghi&ecirc;m trọng, ảnh hưởng rất xấu đến uy t&iacute;n của ng&agrave;nh C&ocirc;ng an v&agrave; c&aacute; nh&acirc;n đồng ch&iacute;, g&acirc;y dư luận xấu trong x&atilde; hội.</p>\r\n\r\n<p>Căn cứ Quy chế l&agrave;m việc của Ban Chấp h&agrave;nh Trung ương, Bộ Ch&iacute;nh trị, Ban B&iacute; thư kho&aacute; XII; căn cứ Quy định số 102-QĐ/TW của Bộ Ch&iacute;nh trị về xử l&yacute; đảng vi&ecirc;n vi phạm, Bộ Ch&iacute;nh trị quyết định thi h&agrave;nh kỷ luật &ocirc;ng Trần Việt T&acirc;n bằng h&igrave;nh thức c&aacute;ch chức Uỷ vi&ecirc;n Ban Chấp h&agrave;nh Đảng bộ C&ocirc;ng an Trung ương nhiệm kỳ 2011 - 2016. Giao Ban c&aacute;n sự đảng Ch&iacute;nh phủ chỉ đạo thực hiện quy tr&igrave;nh xử l&yacute; kỷ luật về h&agrave;nh ch&iacute;nh v&agrave; gi&aacute;ng cấp bậc h&agrave;m đối với &ocirc;ng Trần Việt T&acirc;n bảo đảm đồng bộ, kịp thời, tương xứng với kỷ luật của Đảng theo quy định.</p>\r\n\r\n<p>C&ugrave;ng ng&agrave;y 28-7-2018, sau khi xem x&eacute;t Tờ tr&igrave;nh số 129-TTr/UBKTTW, ng&agrave;y 27-7-2018 của Uỷ ban Kiểm tra Trung ương về việc đề nghị xem x&eacute;t, thi h&agrave;nh kỷ luật Thượng tướng Phương Minh Ho&agrave;, nguy&ecirc;n Uỷ vi&ecirc;n Trung ương Đảng, nguy&ecirc;n Ph&oacute; Chủ nhiệm Tổng cục Ch&iacute;nh trị Qu&acirc;n đội nh&acirc;n d&acirc;n Việt Nam; nguy&ecirc;n B&iacute; thư Đảng uỷ - Ch&iacute;nh uỷ, nguy&ecirc;n Ph&oacute; B&iacute; thư Đảng uỷ - Tư lệnh Qu&acirc;n chủng Ph&ograve;ng kh&ocirc;ng - Kh&ocirc;ng qu&acirc;n, Ban B&iacute; thư nhận thấy Thượng tướng Phương Minh Ho&agrave; trong thời gian giữ cương vị B&iacute; thư Đảng uỷ - Ch&iacute;nh uỷ v&agrave; Ph&oacute; B&iacute; thư Đảng uỷ - Tư lệnh Qu&acirc;n chủng Ph&ograve;ng kh&ocirc;ng - Kh&ocirc;ng qu&acirc;n, chịu tr&aacute;ch nhiệm ch&iacute;nh về những vi phạm, khuyết điểm của Ban Thường vụ Đảng uỷ nhiệm kỳ 2010 - 2015; đ&atilde; vi phạm nguy&ecirc;n tắc tập trung d&acirc;n chủ v&agrave; Quy chế l&agrave;m việc; chấp h&agrave;nh kh&ocirc;ng nghi&ecirc;m quyết định của Qu&acirc;n uỷ Trung ương v&agrave; Bộ Quốc ph&ograve;ng trong quản l&yacute;, sử dụng đất quốc ph&ograve;ng v&agrave; đất quy hoạch cho mục đ&iacute;ch quốc ph&ograve;ng nhưng chưa sử dụng ngay cho mục đ&iacute;ch quốc ph&ograve;ng; thiếu tr&aacute;ch nhiệm, bu&ocirc;ng lỏng l&atilde;nh đạo, quản l&yacute;, thiếu kiểm tra, gi&aacute;m s&aacute;t để nhiều tổ chức đảng v&agrave; đảng vi&ecirc;n vi phạm bị xử l&yacute; kỷ luật, c&oacute; trường hợp nghi&ecirc;m trọng phải xử l&yacute; h&igrave;nh sự; &ocirc;ng Ho&agrave; đ&atilde; trực tiếp k&yacute; nhiều văn bản về giao đất v&agrave; ph&ecirc; duyệt phương &aacute;n l&agrave;m kinh tế kh&ocirc;ng đ&uacute;ng quy định.</p>\r\n\r\n<p>Vi phạm, khuyết điểm của Thượng tướng Phương Minh Ho&agrave; l&agrave; nghi&ecirc;m trọng, l&agrave;m ảnh hưởng đến uy t&iacute;n của tổ chức đảng v&agrave; Qu&acirc;n đội. Căn cứ Quy định số 102-QĐ/TW của Bộ Ch&iacute;nh trị về xử l&yacute; kỷ luật đảng vi&ecirc;n vi phạm, Ban B&iacute; thư quyết định thi h&agrave;nh kỷ luật Thượng tướng Phương Minh Ho&agrave; bằng h&igrave;nh thức Cảnh c&aacute;o.&nbsp;</p>', 'Thứ trưởng Bộ công an Bùi Văn Thành, nguyên Thứ trưởng Bộ công an Trần Việt Tân và nguyên Tư lệnh Quân chủng PK-KQ bị Bộ Chính trị, Ban Bí thư kỷ luật', '4', '4', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/vWgQ98VcaFSxv3j0VJlqW6zavnpsgsD6vfY0BAS6.jpeg', 0, '2018-07-29 21:09:44', '2018-08-03 01:31:16'),
(22, 'Lý do hai tướng Thứ trưởng Công an bị đề nghị xử lý kỷ luật', '<p>Như VOV.VN đ&atilde; đưa tin, tại kỳ họp thứ 28 (từ ng&agrave;y 24 đến 26-7-2018, tại H&agrave; Nội), Ủy ban Kiểm tra Trung ương đ&atilde; đề nghị Bộ Ch&iacute;nh trị xem x&eacute;t, thi h&agrave;nh kỷ luật đối với Trung tướng B&ugrave;i Văn Th&agrave;nh, Ủy vi&ecirc;n Đảng ủy C&ocirc;ng an Trung ương, Thứ trưởng Bộ C&ocirc;ng an v&agrave; Thượng tướng Trần Việt T&acirc;n, nguy&ecirc;n Ủy vi&ecirc;n Đảng ủy C&ocirc;ng an Trung ương, nguy&ecirc;n Thứ trưởng Bộ C&ocirc;ng an.</p>\r\n\r\n<table align=\"center\" style=\"width:490px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Trung tướng Bùi Văn Thành- Thứ trưởng Bộ Công an\" src=\"http://baodanang.vn/dataimages/201807/original/images1461793_vov_bui_van_thanh_aoyt.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Trung tướng B&ugrave;i Văn Th&agrave;nh- Thứ trưởng Bộ C&ocirc;ng an</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trung tướng B&ugrave;i Văn Th&agrave;nh, Ủy vi&ecirc;n Đảng ủy C&ocirc;ng an Trung ương, Thứ trưởng Bộ C&ocirc;ng an trực tiếp phụ tr&aacute;ch Tổng cục Hậu cần &ndash; Kỹ thuật, Bộ C&ocirc;ng an (Tổng cục IV), nguy&ecirc;n Ủy vi&ecirc;n Ban Thường vụ Đảng ủy, nguy&ecirc;n Ph&oacute; Tổng cục trưởng Tổng cục IV c&ugrave;ng chịu tr&aacute;ch nhiệm về những vi phạm, khuyết điểm của Ban Thường vụ Đảng ủy Tổng cục IV.<br />\r\nUBKT Trung ương x&aacute;c định: Ban Thường vụ Đảng ủy Tổng cục Hậu cần - Kỹ thuật (Tổng cục IV) đ&atilde; vi phạm nguy&ecirc;n tắc tập trung d&acirc;n chủ v&agrave; quy chế l&agrave;m việc; thiếu tr&aacute;ch nhiệm, bu&ocirc;ng lỏng l&atilde;nh đạo, chỉ đạo; thiếu kiểm tra, gi&aacute;m s&aacute;t, để xảy ra nhiều vi phạm trong quản l&yacute;, sử dụng đất an ninh v&agrave; quản l&yacute; t&agrave;i sản c&ocirc;ng; b&aacute;n chỉ định t&agrave;i sản tr&ecirc;n đất, chuyển nhượng quyền sử dụng đất, li&ecirc;n kết kinh doanh, cho thu&ecirc; đất an ninh kh&ocirc;ng đ&uacute;ng quy định ph&aacute;p luật.</p>\r\n\r\n<p>C&aacute; nh&acirc;n &ocirc;ng Th&agrave;nh đ&atilde; vi phạm nguy&ecirc;n tắc tập trung d&acirc;n chủ, thiếu tr&aacute;ch nhiệm, bu&ocirc;ng lỏng l&atilde;nh đạo, quản l&yacute;, thiếu kiểm tra, gi&aacute;m s&aacute;t, để xảy ra nhiều vi phạm, khuyết điểm tại Tổng cục IV.</p>\r\n\r\n<p>Qua kiểm tra, trung tướng Th&agrave;nh c&ograve;n vi phạm c&aacute;c quy định về bảo vệ b&iacute; mật Nh&agrave; nước v&agrave; Quy chế l&agrave;m việc của Bộ C&ocirc;ng an; k&yacute; văn bản của Bộ C&ocirc;ng an đề xuất b&aacute;n chỉ định một số cơ sở nh&agrave;, đất an ninh kh&ocirc;ng đ&uacute;ng quy định ph&aacute;p luật; k&yacute; một số văn bản kh&ocirc;ng đ&uacute;ng thẩm quyền được ph&acirc;n c&ocirc;ng phụ tr&aacute;ch.</p>\r\n\r\n<p>Ủy ban kiểm tra Trung ương kết luận, vi phạm của trung tướng B&ugrave;i Văn Th&agrave;nh l&agrave; rất nghi&ecirc;m trọng, l&agrave;m thất tho&aacute;t t&agrave;i sản Nh&agrave; nước, ảnh hưởng xấu đến uy t&iacute;n của tổ chức Đảng v&agrave; ng&agrave;nh C&ocirc;ng an, g&acirc;y bức x&uacute;c trong x&atilde; hội, đến mức phải xem x&eacute;t, xử l&yacute; kỷ luật.</p>\r\n\r\n<p>Trung tướng B&ugrave;i Văn Th&agrave;nh sinh năm 1958 tại huyện Y&ecirc;n Kh&aacute;nh, Ninh B&igrave;nh, từng giữ chức vụ Ph&oacute; Tổng cục trưởng Tổng cục Kỹ thuật; Ph&oacute; Tổng cục trưởng Tổng cục Hậu cần - Kỹ thuật trước khi được bổ nhiệm Thứ trưởng C&ocirc;ng an v&agrave;o th&aacute;ng 8-2014.</p>\r\n\r\n<table align=\"center\" style=\"width:490px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Nguyên Thứ trưởng Bộ Công an Trần Việt Tân (Ảnh: Congannghean)\" src=\"http://baodanang.vn/dataimages/201807/original/images1461794_ong_tan_iaiu.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nguy&ecirc;n Thứ trưởng Bộ C&ocirc;ng an Trần Việt T&acirc;n (Ảnh: Congannghean)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Thượng tướng Trần Việt T&acirc;n (nguy&ecirc;n Ủy vi&ecirc;n Đảng ủy C&ocirc;ng an Trung ương, nguy&ecirc;n Thứ trưởng Bộ C&ocirc;ng an) bị x&aacute;c định trong thời gian giữ cương vị Ủy vi&ecirc;n Đảng ủy C&ocirc;ng an Trung ương, Thứ trưởng Bộ C&ocirc;ng an đ&atilde; thiếu tr&aacute;ch nhiệm, bu&ocirc;ng lỏng l&atilde;nh đạo, chỉ đạo, thiếu kiểm tra, gi&aacute;m s&aacute;t, k&yacute; một số văn bản vi phạm quy định về bảo vệ b&iacute; mật nh&agrave; nước, g&acirc;y hậu quả rất nghi&ecirc;m trọng.</p>\r\n\r\n<p>Thượng tướng Trần Việt T&acirc;n sinh năm 1955 tại huyện Tiền Hải, tỉnh Th&aacute;i B&igrave;nh. Th&aacute;ng 10/2011, Thủ tướng Ch&iacute;nh phủ k&yacute; Quyết định bổ nhiệm &ocirc;ng v&agrave;o chức vụ Thứ trưởng Bộ C&ocirc;ng an, vẫn ki&ecirc;m chức Tổng cục trưởng Tổng cục T&igrave;nh b&aacute;o cho đến khi Thiếu tướng Đặng Xu&acirc;n Loan được bổ nhiệm l&agrave;m Tổng cục trưởng thay thế.</p>\r\n\r\n<p>&Ocirc;ng Trần Việt T&acirc;n được thăng qu&acirc;n h&agrave;m Thượng tướng v&agrave;o năm 2013 v&agrave; nghỉ hưu từ ng&agrave;y 1 th&aacute;ng 3 năm 2016.</p>', 'Hai Tướng công an bị đề nghị kỷ luật vì vi phạm nguyên tắc tập trung dân chủ, thiếu trách nhiệm, buông lỏng lãnh đạo, quản lý.', '4', '4', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/dhKTCGqvYqCkNYxLumpxY8DdO5Y4KF7osVeSZQT0.jpeg', 0, '2018-07-29 21:10:59', '2018-08-03 01:29:52'),
(23, 'Vinh danh 8 công trình tiêu biểu của đất nước', '<p>Dự Chương tr&igrave;nh c&oacute; c&aacute;c đồng ch&iacute; Ủy vi&ecirc;n Bộ Ch&iacute;nh trị: Ph&oacute; Thủ tướng Ch&iacute;nh phủ Vương Đ&igrave;nh Huệ, Trưởng Ban Tuy&ecirc;n gi&aacute;o Trung ương V&otilde; Văn Thưởng; Ủy vi&ecirc;n Trung ương Đảng, Chủ tịch Tổng Li&ecirc;n đo&agrave;n Lao động Việt Nam B&ugrave;i Văn Cường; đại diện l&atilde;nh đạo c&aacute;c Bộ, ban, ng&agrave;nh, đo&agrave;n thể Trung ương.</p>\r\n\r\n<p>Ph&aacute;t biểu tại Chương tr&igrave;nh, Ph&oacute; Thủ tướng Ch&iacute;nh phủ Vương Đ&igrave;nh Huệ biểu dương những cống hiến v&ocirc; c&ugrave;ng to lớn của tập thể những người đ&atilde; x&acirc;y dựng n&ecirc;n c&aacute;c c&ocirc;ng tr&igrave;nh để lại dấu ấn trong qu&aacute; tr&igrave;nh ph&aacute;t triển đất nước từ giai đoạn đổi mới đến nay.</p>\r\n\r\n<p>C&ocirc;ng cuộc đổi mới của đất nước c&oacute; rất nhiều th&agrave;nh tựu, kinh nghiệm để từ chỗ l&agrave; một nước ngh&egrave;o, thu nhập thấp, Việt Nam đ&atilde; trở th&agrave;nh nước đang ph&aacute;t triển c&oacute; thu nhập trung b&igrave;nh; thế v&agrave; lực của đất nước ta ng&agrave;y c&agrave;ng được củng cố v&agrave; vững mạnh.</p>\r\n\r\n<p>Ph&oacute; Thủ tướng Vương Đ&igrave;nh Huệ nhấn mạnh: Việc t&ocirc;n vinh c&aacute;c c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu của đất nước l&agrave; biểu tượng ch&acirc;n thực, minh chứng sống động cho sự d&aacute;m nghĩ, d&aacute;m l&agrave;m, đ&oacute;ng g&oacute;p cho x&atilde; hội của c&aacute;c tr&iacute; tuệ Việt Nam.</p>\r\n\r\n<p>C&oacute; được th&agrave;nh tựu n&agrave;y c&oacute; c&ocirc;ng kh&ocirc;ng nhỏ của c&aacute;c c&aacute; nh&acirc;n, tập thể được vinh danh trong Chương tr&igrave;nh Vinh quang Việt Nam, cũng như mỗi người d&acirc;n Việt Nam tr&ecirc;n khắp mọi miền tổ quốc.</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng t&ocirc;i cũng tr&acirc;n trọng v&agrave; cảm ơn Tổng Li&ecirc;n đo&agrave;n Lao động Việt Nam đ&atilde; b&igrave;nh chọn ra những c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu của đất nước để vinh danh. Những c&ocirc;ng tr&igrave;nh n&agrave;y vẫn tiếp tục ph&aacute;t huy hiệu quả, phục vụ cho đời sống x&atilde; hội.</p>\r\n\r\n<p>T&ocirc;i mong rằng, c&aacute;c tr&iacute; tuệ Việt Nam sẽ tiếp tục s&aacute;ng tạo ra những c&ocirc;ng tr&igrave;nh c&oacute; &yacute; nghĩa lớn lao, bởi, đ&acirc;y l&agrave; những tấm gương đầy tự h&agrave;o về &yacute; ch&iacute; v&agrave; tr&iacute; tuệ con người Việt Nam&rdquo;, Ph&oacute; Thủ tướng Vương Đ&igrave;nh Huệ nhấn mạnh.</p>\r\n\r\n<p>Thay mặt l&atilde;nh đạo Ch&iacute;nh phủ, Ph&oacute; Thủ tướng đề nghị tổ chức c&ocirc;ng đo&agrave;n, c&aacute;c bộ ng&agrave;nh địa phương tiếp tục đẩy mạnh c&aacute;c phong tr&agrave;o thi đua trong c&aacute;n bộ c&ocirc;ng nh&acirc;n vi&ecirc;n chức, người lao động, nhất l&agrave; phong tr&agrave;o thi đua lao động giỏi, lao động s&aacute;ng tạo, học tập n&acirc;ng cao tr&igrave;nh độ nhằm n&acirc;ng cao năng suất lao động, chất lượng sản phẩm, khả năng cạnh tranh của doanh nghiệp v&agrave; nền kinh tế, đặc biệt l&agrave; trong điều kiện c&aacute;ch mạng c&ocirc;ng nghiệp 4.0.</p>\r\n\r\n<p>Tiếp tục ph&aacute;t hiện biểu dương, nh&acirc;n rộng c&aacute;c điển h&igrave;nh ti&ecirc;u biểu, c&aacute;c nh&acirc;n tố mới, với những gương người tốt, việc tốt, đưa phong tr&agrave;o thi đua y&ecirc;u nước ph&aacute;t triển l&ecirc;n tầm cao mới, g&oacute;p phần x&acirc;y dựng đất nước ta đ&agrave;ng ho&agrave;ng hơn, to đẹp hơn, như B&aacute;c Hồ k&iacute;nh y&ecirc;u hằng mong muốn.</p>\r\n\r\n<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Phó Thủ tướng Vương Đình Huệ và Chủ tịch Tổng Liên đoàn Lao động Việt Nam Bùi Văn Cường trao giải thưởng cho những đại diện của 8 công trình đặc biệt tiêu biểu xuất sắc. Ảnh: VGP\" src=\"http://baodanang.vn/dataimages/201807/original/images1461841_A59A8722_copy.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ph&oacute; Thủ tướng Vương Đ&igrave;nh Huệ v&agrave; Chủ tịch Tổng Li&ecirc;n đo&agrave;n Lao động Việt Nam B&ugrave;i Văn Cường trao giải thưởng cho những đại diện của 8 c&ocirc;ng tr&igrave;nh đặc biệt ti&ecirc;u biểu xuất sắc. Ảnh: VGP</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Chủ tịch Tổng Li&ecirc;n đo&agrave;n Lao động Việt Nam B&ugrave;i Văn Cường cho biết: Chương tr&igrave;nh Vinh quang Việt Nam những năm trước đ&acirc;y nhằm ghi nhận v&agrave; vinh danh những tập thể, c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch đặc biệt xuất sắc, ti&ecirc;u biểu trong sự nghiệp x&acirc;y dựng v&agrave; bảo vệ tổ quốc gắn với những c&ocirc;ng trình t&acirc;̀m vóc, đồng thời khơi dậy l&ograve;ng y&ecirc;u nước, &yacute; ch&iacute; quyết t&acirc;m, tinh thần lao động s&aacute;ng tạo v&igrave; sự ph&aacute;t triển của đất nước, v&igrave; mục ti&ecirc;u &ldquo;D&acirc;n gi&agrave;u nước mạnh, d&acirc;n chủ, c&ocirc;ng bằng, văn minh&rdquo;.</p>\r\n\r\n<p>Từ đầu năm 2018, Tổng Li&ecirc;n đo&agrave;n Lao động Việt Nam đ&atilde; đề xuất &yacute; tưởng b&igrave;nh chọn c&ocirc;ng tr&igrave;nh đặc biệt ti&ecirc;u biểu xuất sắc, ghi dấu ấn đậm n&eacute;t, đ&oacute;ng g&oacute;p to lớn v&agrave;o sự nghiệp x&acirc;y dựng v&agrave; bảo vệ Tổ quốc trong những năm từ 1975 trở lại đ&acirc;y.</p>\r\n\r\n<p>Chủ tịch Tổng Li&ecirc;n đo&agrave;n Lao động Việt Nam khẳng định, trong 8 c&ocirc;ng tr&igrave;nh được t&ocirc;n vinh lần n&agrave;y, c&oacute; những tập thể đ&atilde; được Đảng, Nh&agrave; nước trao tặng nhiều phần thưởng cao qu&yacute;. Đ&acirc;y l&agrave; những điển h&igrave;nh đầy tự h&agrave;o về &yacute; ch&iacute;, nghị lực, bản lĩnh, tr&iacute; tuệ của con người Việt Nam, c&oacute; những đ&oacute;ng g&oacute;p rất quan trọng v&agrave;o sự ph&aacute;t triển kinh tế-x&atilde; hội của đất nước ở những giai đoạn lịch sử.</p>\r\n\r\n<p>C&ocirc;ng tr&igrave;nh Vệ tinh viễn th&ocirc;ng của Việt Nam (VINASAT-1, VINASAT- 2) l&agrave; dấu mốc lịch sử quan trọng, mang tầm v&oacute;c quốc gia từ năm 2008. Từ đ&acirc;y, Việt Nam c&oacute; quyền ch&iacute;nh thức trong việc khai th&aacute;c, sử dụng t&agrave;i nguy&ecirc;n kh&ocirc;ng gian vũ trụ, qua đ&oacute; khẳng định chủ quyền quốc gia tr&ecirc;n bản đồ kh&ocirc;ng gian thế giới, n&acirc;ng cao h&igrave;nh ảnh, uy t&iacute;n của Việt Nam tr&ecirc;n trường quốc tế.</p>\r\n\r\n<p>C&ocirc;ng tr&igrave;nh Đường d&acirc;y 500KV Bắc-Nam được coi l&agrave; kỳ t&iacute;ch của thế kỷ XX. C&ocirc;ng tr&igrave;nh l&agrave; một giải ph&aacute;p cấp thiết, quan trọng để giải quyết b&agrave;i to&aacute;n thiếu điện ở miền Trung, T&acirc;y Nguy&ecirc;n v&agrave; miền Nam, tạo bước ngoặt quan trọng đối với sự ph&aacute;t triển kinh tế - x&atilde; hội của khu vực; g&oacute;p phần duy tr&igrave; được tốc độ tăng trưởng kinh tế ph&iacute;a Nam ở mức cao trong nhiều năm li&ecirc;n tục.</p>\r\n\r\n<p>C&ocirc;ng tr&igrave;nh Thủy điện H&ograve;a B&igrave;nh sản xuất h&agrave;ng tỷ Kwh điện mỗi năm, đ&atilde; giải quyết được t&igrave;nh trạng thiếu điện trầm trọng của đất nước trong những năm 80 của thế kỷ 20 - những năm v&ocirc; c&ugrave;ng kh&oacute; khăn v&igrave; đất nước vừa ra khỏi cuộc chiến tranh khốc liệt chống giặc ngoại x&acirc;m.</p>\r\n\r\n<p>&ldquo;Đường Hồ Ch&iacute; Minh&rdquo; l&agrave; con đường huyền thoại trong cuộc chiến tranh giải ph&oacute;ng d&acirc;n tộc, thống nhất đất nước, mạch giao th&ocirc;ng trục dọc quan trọng thứ hai của quốc gia, đ&oacute;ng g&oacute;p to lớn trong việc th&uacute;c đẩy ph&aacute;t triển kinh tế x&atilde; hội khu vực miền n&uacute;i, bi&ecirc;n giới, T&acirc;y Nguy&ecirc;n, g&oacute;p phần x&oacute;a đ&oacute;i giảm ngh&egrave;o, đảm bảo quốc ph&ograve;ng an ninh khu vực ph&iacute;a t&acirc;y của Tổ quốc.</p>\r\n\r\n<p>C&ocirc;ng tr&igrave;nh &quot;Nghi&ecirc;n cứu chọn tạo v&agrave; ph&aacute;t triển 2 giống l&uacute;a mới OM 6976 v&agrave; OM 5451&rdquo; c&oacute; năng suất v&agrave; chất lượng cao đ&atilde; thể hiện năng lực tiếp cận, ứng dụng, l&agrave;m chủ tiến bộ khoa học kỹ thuật để g&oacute;p phần ph&aacute;t triển nền n&ocirc;ng nghiệp hiện đại, n&acirc;ng cao thu nhập cho n&ocirc;ng d&acirc;n, g&oacute;p phần x&acirc;y dựng n&ocirc;ng th&ocirc;n mới.</p>\r\n\r\n<p>C&ocirc;ng tr&igrave;nh &quot;Từ điển B&aacute;ch khoa To&agrave;n thư&rdquo; do c&ocirc;ng sức to lớn của nhiều nh&agrave; khoa học, nh&agrave; nghi&ecirc;n cứu để tạo n&ecirc;n một kiệt t&aacute;c đồ sộ trong việc giữ g&igrave;n, ph&aacute;t huy c&aacute;c gi&aacute; trị văn h&oacute;a của d&acirc;n tộc.</p>\r\n\r\n<p>C&ocirc;ng tr&igrave;nh &ldquo;Bản đồ địa chất, bản đồ kho&aacute;ng sản Việt Nam tỷ lệ 1:200.000&rdquo; với những tập thể, c&aacute; nh&acirc;n c&oacute; &yacute; ch&iacute; sắt đ&aacute; đ&atilde; vượt qua h&agrave;ng trăm ng&agrave;n km khảo s&aacute;t thực địa, lấy mẫu với mu&ocirc;n v&agrave;n kh&oacute; khăn để ho&agrave;n th&agrave;nh c&ocirc;ng tr&igrave;nh.</p>\r\n\r\n<p>Chương tr&igrave;nh ti&ecirc;m chủng mở rộng đ&atilde; gi&uacute;p đất nước bảo vệ được h&agrave;ng triệu trẻ em v&agrave; phụ nữ kh&ocirc;ng bị mắc bệnh, kh&ocirc;ng bị chết cũng như c&aacute;c di chứng của c&aacute;c bệnh truyền nhiễm nguy hiểm, phổ biến g&oacute;p phần n&acirc;ng cao thể lực, tr&iacute; tuệ con người Việt Nam.&nbsp;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; những c&ocirc;ng tr&igrave;nh mang tầm v&oacute;c thế kỷ, mang dấu ấn thời gian, minh chứng cho sự s&aacute;ng tạo trong lao động, sản xuất của c&aacute;n bộ, c&ocirc;ng nh&acirc;n vi&ecirc;n chức, người lao động Việt Nam. Những c&ocirc;ng tr&igrave;nh được lựa chọn kh&ocirc;ng chỉ l&agrave;m cho đất nước ph&aacute;t triển cả về ch&iacute;nh trị, kinh tế, văn h&oacute;a, x&atilde; hội m&agrave; c&ograve;n l&agrave; c&aacute;i n&ocirc;i đ&agrave;o tạo ra những đội ngũ c&aacute;n bộ quản l&yacute;, c&ocirc;ng nh&acirc;n kỹ thuật c&oacute; tr&igrave;nh độ cao, s&aacute;nh vai với khu vực v&agrave; thế giới trong c&aacute;c lĩnh vực x&acirc;y dựng, giao th&ocirc;ng, năng lượng, địa chất, viễn th&ocirc;ng, y tế, văn h&oacute;a, n&ocirc;ng nghiệp.</p>\r\n\r\n<p>PGS.TS Trần Thị C&uacute;c H&ograve;a, t&aacute;c giả của C&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu chọn tạo v&agrave; ph&aacute;t triển 2 giống l&uacute;a mới b&agrave;y tỏ mong muốn kết quả nghi&ecirc;n cứu của m&igrave;nh được ứng dụng v&agrave;o sản xuất, gi&uacute;p cho n&ocirc;ng d&acirc;n tăng thu nhập, cải thiện đời sống cho nh&acirc;n d&acirc;n. V&igrave; vậy, sau 9 năm ki&ecirc;n tr&igrave; lai tạo, thử nghiệm, ng&agrave;y đ&ecirc;m gắn b&oacute; với đồng ruộng v&agrave; ph&ograve;ng th&iacute; nghiệm đ&atilde; nghi&ecirc;n cứu ra 2 giống l&uacute;a với năng suất cao gấp rưỡi c&aacute;c giống l&uacute;a kh&aacute;c v&agrave; c&oacute; sức chống chọi cao với s&acirc;u bệnh.</p>\r\n\r\n<p>&ldquo;T&ocirc;i kh&ocirc;ng nghĩ đến việc n&agrave;o vất vả, việc n&agrave;o kh&ocirc;ng vất vả, đ&acirc;y l&agrave; c&ocirc;ng việc c&oacute; t&iacute;nh chất ki&ecirc;n tr&igrave;, li&ecirc;n tục nhiều năm. Kh&ocirc;ng c&oacute; g&igrave; vui sướng bằng khi thấy giống l&uacute;a của m&igrave;nh được ứng dụng v&agrave;o sản xuất tr&ecirc;n diện rộng, diện t&iacute;ch năm sau lu&ocirc;n cao năm trước&rdquo;, PGS.TS Trần Thị C&uacute;c Ho&agrave; n&oacute;i.</p>\r\n\r\n<p>Tại Chương tr&igrave;nh, thay mặt L&atilde;nh đạo Đảng v&agrave; Nh&agrave; nước, Ph&oacute; Thủ tướng Vương Đ&igrave;nh Huệ, Trưởng Ban Tuy&ecirc;n gi&aacute;o Trung ương V&otilde; Văn Thưởng, Chủ tịch Tổng Li&ecirc;n đo&agrave;n Lao động Việt Nam B&ugrave;i Văn Cường đ&atilde; trao giải thưởng cho những đại diện của 8 c&ocirc;ng tr&igrave;nh đặc biệt ti&ecirc;u biểu xuất sắc, ghi dấu ấn đậm n&eacute;t, đ&oacute;ng g&oacute;p to lớn v&agrave;o sự nghiệp x&acirc;y dựng v&agrave; bảo vệ Tổ quốc.</p>', 'Chào mừng kỷ niệm 89 năm Ngày thành lập Công đoàn Việt Nam (28-7-1929 - 28-7-2018) và hướng tới Đại hội Công đoàn Việt Nam lần thứ XII, ngày 28-7, tại Hà Nội, Tổng Liên đoàn Lao động Việt Nam tổ chức Chương trình Vinh quang Việt Nam lần thứ 14 với chủ đề “Vinh quang Việt Nam - Dấu ấn những công trình”.', '4', '4', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/ERXDum7oMUUxaMtSH7y2iSZJS0xfxFD9fcBrEE8s.jpeg', 0, '2018-07-29 21:18:47', '2018-08-03 03:14:14'),
(24, 'Campuchia bầu Quốc hội: Hướng đến sự phát triển ổn định và bền vững', '<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Thủ tướng Hun Sen hiệu triệu toàn dân tham gia bỏ phiếu. Ảnh: AFP\" src=\"http://baodanang.vn/dataimages/201807/original/images1462016_29_7__Hun_Sen__AFP.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Thủ tướng Hun Sen hiệu triệu to&agrave;n d&acirc;n tham gia bỏ phiếu. Ảnh: AFP</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ng&agrave;y 29-7, hơn 82,7% trong số 8,3 triệu cử tri Campuchia đăng k&yacute; bầu cử tham gia bỏ phiếu bầu 125 nghị sĩ Quốc hội. Ngay từ s&aacute;ng sớm, Thủ tướng Hun Sen (65 tuổi) c&ugrave;ng phu nh&acirc;n Bun Rany c&oacute; mặt tại điểm bỏ phiếu ở tỉnh Kandal để thực hiện nghĩa vụ c&ocirc;ng d&acirc;n. &Ocirc;ng Hun Sen mỉm cười với c&aacute;c cử tri trước khi bỏ l&aacute; phiếu của m&igrave;nh v&agrave; giơ những ng&oacute;n tay c&ograve;n lem vết mực trước ống k&iacute;nh truyền th&ocirc;ng. C&aacute;c điểm bỏ phiếu đ&oacute;ng cửa l&uacute;c 15 giờ. Kết quả ch&iacute;nh thức sẽ được c&ocirc;ng bố v&agrave;o ng&agrave;y 11-8 tới.</p>\r\n\r\n<p>C&oacute; tới 19 đảng ph&aacute;i ch&iacute;nh trị c&ugrave;ng tranh cử với đảng Nh&acirc;n d&acirc;n Campuchia (CPP) cầm quyền của &ocirc;ng Hun Sen. Song, tất cả đều l&agrave; những đảng nhỏ, chưa tạo được nhiều dấu ấn n&ecirc;n cơ hội chiến thắng với CPP gần như l&agrave; chắc chắn.</p>\r\n\r\n<p>Đảng Cứu quốc (CNRP) - đảng đối lập lớn từng thất bại ở khoảng c&aacute;ch hẹp với CPP trong m&ugrave;a tổng tuyển cử năm 2013 - lần n&agrave;y kh&ocirc;ng được ph&eacute;p tranh cử. Th&aacute;ng 9 năm ngo&aacute;i, c&aacute;c nh&agrave; chức tr&aacute;ch Campuchia bắt giữ thủ lĩnh CNRP Kem Sokha v&agrave; c&aacute;o buộc &ocirc;ng n&agrave;y tội phản quốc. Hai th&aacute;ng sau đ&oacute;, T&ograve;a &aacute;n Tối cao Campuchia tuy&ecirc;n bố giải thể CNRP vốn nắm giữ 55/123 ghế của Quốc hội sau khi ph&aacute;t hiện đảng n&agrave;y &acirc;m mưu lật đổ ch&iacute;nh quyền.</p>\r\n\r\n<p>Trước những c&aacute;o buộc về một số vấn đề li&ecirc;n quan đến bầu cử, ch&iacute;nh phủ của Thủ tướng Hun Sen khẳng định đ&acirc;y l&agrave; cuộc tổng tuyển cử c&ocirc;ng bằng, tự do. &ldquo;Cuộc bầu cử l&agrave; &yacute; ch&iacute; v&agrave; quyền lực của nh&acirc;n d&acirc;n Campuchia. Người d&acirc;n Campuchia quyết t&acirc;m g&igrave;n giữ h&ograve;a b&igrave;nh v&agrave; ổn định&rdquo;, người ph&aacute;t ng&ocirc;n ch&iacute;nh phủ Phay Siphan trả lời phỏng vấn truyền th&ocirc;ng quốc tế. Trong b&agrave;i ph&aacute;t biểu ng&agrave;y 27-7, &ocirc;ng Hun Sen cũng hiệu triệu to&agrave;n d&acirc;n tham gia bỏ phiếu.</p>\r\n\r\n<p>Để bảo đảm t&iacute;nh ch&iacute;nh x&aacute;c, c&ocirc;ng bằng v&agrave; ngăn chặn việc bỏ phiếu nhiều hơn một lần, Ủy ban Bầu cử quốc gia (NEC) sử dụng loại mực kh&ocirc;ng thể x&oacute;a để b&ocirc;i v&agrave;o ng&oacute;n tay của cử tri. C&oacute; khoảng 80.000 quan s&aacute;t vi&ecirc;n, trong đ&oacute; c&oacute; h&agrave;ng chục quan s&aacute;t vi&ecirc;n quốc tế, được ch&iacute;nh phủ Campuchia cấp ph&eacute;p hoạt động trong qu&aacute; tr&igrave;nh bầu cử. Ng&agrave;y 28-7, đ&iacute;ch th&acirc;n Thủ tướng Hun Sen gặp gỡ c&aacute;c quan s&aacute;t vi&ecirc;n quốc tế, trong đ&oacute; c&oacute; c&aacute;c ph&aacute;i đo&agrave;n của Nga, Trung Quốc v&agrave; Indonesia. Mỹ, Li&ecirc;n minh ch&acirc;u &Acirc;u (EU) v&agrave; Nhật Bản từ chối cử đo&agrave;n quan s&aacute;t vi&ecirc;n đến Campuchia.</p>\r\n\r\n<p>B&ecirc;n cạnh những cử tri mong muốn c&oacute; sự đổi mới kh&aacute;c, nhiều cử tri Campuchia vẫn b&agrave;y tỏ sự ngưỡng mộ đối với Thủ tướng Hun Sen, người đ&atilde; mang lại hơn 30 năm h&ograve;a b&igrave;nh v&agrave; ổn định cho đất nước, tạo điều kiện để quốc gia Đ&ocirc;ng Nam &Aacute; n&agrave;y ph&aacute;t triển kinh tế. Nhiều t&ograve;a nh&agrave; chọc trời đ&atilde; mọc l&ecirc;n c&ugrave;ng c&aacute;c dự &aacute;n lớn c&oacute; vốn đầu tư nước ngo&agrave;i được triển khai trong những năm qua. Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Thế giới (WB) v&agrave; Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), Campuchia l&agrave; một trong những quốc gia c&oacute; mức tăng trưởng kinh tế ổn định trong khu vực, với tốc độ tăng trưởng bền vững 7%/năm. Thu nhập b&igrave;nh qu&acirc;n đầu người từ 1.042 USD năm 2013 tăng l&ecirc;n 1.563 USD năm 2018.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của c&aacute;c chuy&ecirc;n gia, trong 5 năm qua, CPP đ&atilde; thực hiện th&agrave;nh c&ocirc;ng c&ocirc;ng cuộc cải tổ, nhất l&agrave; trong lĩnh vực gi&aacute;o dục, h&agrave;nh ch&iacute;nh c&ocirc;ng, ph&ugrave; hợp với sự ph&aacute;t triển của x&atilde; hội v&agrave; mong đợi của người d&acirc;n. Những g&oacute;i an sinh x&atilde; hội được thực hiện như g&oacute;i bảo hiểm y tế d&agrave;nh cho mọi người d&acirc;n, chế độ hưu tr&iacute; được n&acirc;ng cao, g&oacute;i kinh tế d&agrave;nh cho c&ocirc;ng nh&acirc;n, n&ocirc;ng d&acirc;n v&agrave; học sinh, sinh vi&ecirc;n&hellip; Đặc biệt, Campuchia đang hướng đến tầm nh&igrave;n l&agrave; đất nước c&oacute; thu nhập ở mức trung b&igrave;nh cao trong năm 2030, đất nước c&oacute; thu nhập cao trong năm 2050; trở th&agrave;nh quốc gia văn minh, ph&aacute;t triển trong khu vực; x&acirc;y dựng x&atilde; hội h&agrave;i h&ograve;a kh&ocirc;ng chiến tranh, kh&ocirc;ng c&oacute; h&agrave;nh động diệt chủng v&agrave; ph&aacute; hoại nội bộ. Trong những th&agrave;nh tựu đ&oacute;, vai tr&ograve; của Thủ tướng Hun Sen rất quan trọng. &ldquo;&Ocirc;ng Hun Sen đ&atilde; trưởng th&agrave;nh trong gian kh&oacute; n&ecirc;n &ocirc;ng ấy hiểu r&otilde; c&aacute;c vấn đề của ch&uacute;ng t&ocirc;i&rdquo;, anh Sok Dorn - một người l&agrave;m nghề b&aacute;n m&igrave; chia sẻ.</p>', 'Tổng tuyển cử ngày 29-7 tại Campuchia là cuộc bầu cử lần thứ 6 của đất nước Chùa Tháp kể từ năm 1993, theo đó sẽ quyết định sự phát triển ổn định và bền vững của đất nước này trong những năm tới.', '4', '7', '3', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/wrxnp93XzPStBTqahrzsdcfT0IOMMcLFKNu49ssa.jpeg', 11, '2018-07-30 00:49:23', '2018-08-05 23:00:22'),
(25, 'Uy lực công, thủ toàn diện của tàu hộ vệ tàng hình mới gia nhập hải quân Nga', '<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Theo hãng tin TASS, Đô đốc Gorshkov, tàu đầu tiên trong dự án 22.350 chế tạo tàu khu trục tàng hình mới của Nga, được bàn giao cho hải quân nước này vào ngày 28/7 (giờ địa phương). Đây là tàu đã được sử dụng động cơ hoàn toàn mới với công nghệ chế tạo tiên tiến của hãng Saturn (Nga), thay vì các động cơ thế hệ cũ do công ty Ukraine Zorya-Mashprojekt chế tạo.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461979_atu_2_15328326838811272866400.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Theo h&atilde;ng tin TASS, Đ&ocirc; đốc Gorshkov, t&agrave;u đầu ti&ecirc;n trong dự &aacute;n 22.350 chế tạo t&agrave;u khu trục t&agrave;ng h&igrave;nh mới của Nga, được b&agrave;n giao cho hải qu&acirc;n nước n&agrave;y v&agrave;o ng&agrave;y 28/7 (giờ địa phương). Đ&acirc;y l&agrave; t&agrave;u đ&atilde; được sử dụng động cơ ho&agrave;n to&agrave;n mới với c&ocirc;ng nghệ chế tạo ti&ecirc;n tiến của h&atilde;ng Saturn (Nga), thay v&igrave; c&aacute;c động cơ thế hệ cũ do c&ocirc;ng ty Ukraine Zorya-Mashprojekt chế tạo.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Đô đốc Gorshkov được đặt tên theo cựu tư lệnh hải quân Liên Xô, tướng Sergei Gorshkov. Đây là tàu mặt nước lớn nhất được Nga chế tạo từ sau khi Liên Xô sụp đổ năm 1991, theo The Diplomat.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461980_tau_1_1532832683883901028469.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đ&ocirc; đốc Gorshkov được đặt t&ecirc;n theo cựu tư lệnh hải qu&acirc;n Li&ecirc;n X&ocirc;, tướng Sergei Gorshkov. Đ&acirc;y l&agrave; t&agrave;u mặt nước lớn nhất được Nga chế tạo từ sau khi Li&ecirc;n X&ocirc; sụp đổ năm 1991, theo The Diplomat.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Đô đốc Gorshkov có chiều dài 135 m, rộng 16,4 m, lượng giãn nước 4.500 tấn. Nó được trang bị hệ thống động cơ đẩy tuabin khí có công suất 65.000 mã lực. Nó có khả năng mang một trực thăng chống ngầm Kamov Ka-27 hoặc các biến thể tương đương.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461981_tau_3_1532832683885539231567.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đ&ocirc; đốc Gorshkov c&oacute; chiều d&agrave;i 135 m, rộng 16,4 m, lượng gi&atilde;n nước 4.500 tấn. N&oacute; được trang bị hệ thống động cơ đẩy tuabin kh&iacute; c&oacute; c&ocirc;ng suất 65.000 m&atilde; lực. N&oacute; c&oacute; khả năng mang một trực thăng chống ngầm Kamov Ka-27 hoặc c&aacute;c biến thể tương đương.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Tầm hoạt động của tàu là hơn 6.400 km. Tốc độ tối đa của tàu là 54 km/h.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461982_tau_4_1532832683886435064517.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tầm hoạt động của t&agrave;u l&agrave; hơn 6.400 km. Tốc độ tối đa của t&agrave;u l&agrave; 54 km/h.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Các tàu thuộc lớp Đô đốc Gorshkov được trang bị 2 hệ thống phóng đa năng 3S14U1 UKSK, với các ống phóng tên lửa thẳng đứng triển khai, có thể khai hỏa tên lửa Kalibr-NK hay Oniks cũng như tên lửa hạm đối không Poliment Redut và ngay cả tên lửa BrahMos. Đô đốc Gorshkov có thể mang tối đa 16 tên lửa hành trình Kalibr-NK gồm phiên bản chống hạm 3M-54T và phiên bản tấn công mặt đất 3M-14T.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461983_tau_5_1532832683888284167447.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>C&aacute;c t&agrave;u thuộc lớp Đ&ocirc; đốc Gorshkov được trang bị 2 hệ thống ph&oacute;ng đa năng 3S14U1 UKSK, với c&aacute;c ống ph&oacute;ng t&ecirc;n lửa thẳng đứng triển khai, c&oacute; thể khai hỏa t&ecirc;n lửa Kalibr-NK hay Oniks cũng như t&ecirc;n lửa hạm đối kh&ocirc;ng Poliment Redut v&agrave; ngay cả t&ecirc;n lửa BrahMos. Đ&ocirc; đốc Gorshkov c&oacute; thể mang tối đa 16 t&ecirc;n lửa h&agrave;nh tr&igrave;nh Kalibr-NK gồm phi&ecirc;n bản chống hạm 3M-54T v&agrave; phi&ecirc;n bản tấn c&ocirc;ng mặt đất 3M-14T.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Hệ thống tên lửa phòng không Poliment Redut được đặt theo tên lửa Redut và radar Polyment, là hệ thống phóng tên lửa hạm đối không tiên tiến nhất của Nga, với các tầm phóng và độ cao bắn hạ mục tiêu khác nhau, có thể phòng thủ ở tầm ngắn (10-15 km), tầm trung (40-45 km) hoặc tầm xa (120-150 km). Chính vì vậy, Poliment Redut có thể tạo nên tầng tầng lớp lớp phòng không giúp bảo vệ tàu chiến Nga trước mối đe dọa từ các độ cao và khoảng cách khác nhau.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461984_tau_6_1532832683891469499798.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Hệ thống t&ecirc;n lửa ph&ograve;ng kh&ocirc;ng Poliment Redut được đặt theo t&ecirc;n lửa Redut v&agrave; radar Polyment, l&agrave; hệ thống ph&oacute;ng t&ecirc;n lửa hạm đối kh&ocirc;ng ti&ecirc;n tiến nhất của Nga, với c&aacute;c tầm ph&oacute;ng v&agrave; độ cao bắn hạ mục ti&ecirc;u kh&aacute;c nhau, c&oacute; thể ph&ograve;ng thủ ở tầm ngắn (10-15 km), tầm trung (40-45 km) hoặc tầm xa (120-150 km). Ch&iacute;nh v&igrave; vậy, Poliment Redut c&oacute; thể tạo n&ecirc;n tầng tầng lớp lớp ph&ograve;ng kh&ocirc;ng gi&uacute;p bảo vệ t&agrave;u chiến Nga trước mối đe dọa từ c&aacute;c độ cao v&agrave; khoảng c&aacute;ch kh&aacute;c nhau.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Đô đốc Gorshkov còn được trang bị tổ hợp phòng thủ tầm ngắn CIWS 30 mm và pháo hạm Armat A-192M 130mm ở đuôi tàu.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461985_tau_7_1532832683895458807398.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đ&ocirc; đốc Gorshkov c&ograve;n được trang bị tổ hợp ph&ograve;ng thủ tầm ngắn CIWS 30 mm v&agrave; ph&aacute;o hạm Armat A-192M 130mm ở đu&ocirc;i t&agrave;u.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Ngoài ra, Đô đốc Gorshkov được lắp đặt hệ thống vũ khí chống ngầm Medvedka-2, tổ hợp tên lửa phòng thủ đất đối không tầm trung Hurricane.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461986_tau_8_1532832683898266802412.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ngo&agrave;i ra, Đ&ocirc; đốc Gorshkov được lắp đặt hệ thống vũ kh&iacute; chống ngầm Medvedka-2, tổ hợp t&ecirc;n lửa ph&ograve;ng thủ đất đối kh&ocirc;ng tầm trung Hurricane.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Tổng thống Nga Vladimir Putin từng đi thăm tàu Đô đốc Gorshkov hồi năm 2015 và có bài phát biểu trên boong tàu.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461987_tau_9_15328326838991817749437.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tổng thống Nga Vladimir Putin từng đi thăm t&agrave;u Đ&ocirc; đốc Gorshkov hồi năm 2015 v&agrave; c&oacute; b&agrave;i ph&aacute;t biểu tr&ecirc;n boong t&agrave;u.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'Đô đốc Gorshkov, tàu khu trục tàng hình mới gia nhập hải quân Nga, có kích thước không quá đồ sộ nhưng sở hữu dàn vũ khí hiện đại, tiên tiến mang lại sức mạnh công, thủ toàn diện cho tàu chiến này.', '4', '7', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/0K6MhQ3Xp75n3MMBxGNa4gnpGIrN74K0bZ1ydRs5.jpeg', 0, '2018-07-30 00:51:05', '2018-08-03 01:31:29');
INSERT INTO `post_lists` (`id`, `title`, `content`, `quotes_content`, `author_id`, `post_category_id`, `status`, `image_avatar`, `view`, `created_at`, `updated_at`) VALUES
(26, 'Bắt giữ 18 nhân viên công ty dược phẩm hàng đầu Trung Quốc', '<p>Cảnh s&aacute;t th&agrave;nh phố Trường Xu&acirc;n, tỉnh C&aacute;t L&acirc;m, Đ&ocirc;ng Bắc Trung Quốc, đ&atilde; đề nghị cơ quan c&ocirc;ng tố ph&ecirc; chuẩn lệnh bắt giữ 18 người tại C&ocirc;ng ty c&ocirc;ng nghệ sinh học Trường Xu&acirc;n Trường Sinh, c&ocirc;ng ty sản xuất dược phẩm h&agrave;ng đầu của Trung Quốc hiện l&agrave; &quot;t&acirc;m điểm&quot; một vụ b&ecirc; bối vắcxin g&acirc;y chấn động.</p>\r\n\r\n<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Nhân viên thực thi pháp luật thuộc Cơ quan quản lý và giám sát thị trường kiểm tra việc cung ứng vắcxin tại Dung An, khu tự trị dân tộc Choang Quảng Tây, Trung Quốc ngày 23/7 vừa qua. (Ảnh: EPA/TTXVN)\" src=\"http://baodanang.vn/dataimages/201807/original/images1461976_ttxvn_2907vacxin.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nh&acirc;n vi&ecirc;n thực thi ph&aacute;p luật thuộc Cơ quan quản l&yacute; v&agrave; gi&aacute;m s&aacute;t thị trường kiểm tra việc cung ứng vắcxin tại Dung An, khu tự trị d&acirc;n tộc Choang Quảng T&acirc;y, Trung Quốc ng&agrave;y 23/7 vừa qua. (Ảnh: EPA/TTXVN)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trước đ&oacute;, nữ chủ tịch của c&ocirc;ng ty n&agrave;y đ&atilde; bị bắt giữ.</p>\r\n\r\n<p>Đề nghị tr&ecirc;n được đưa ra sau khi Tổng cục Gi&aacute;m s&aacute;t v&agrave; quản l&yacute; thực phẩm v&agrave; dược phẩm quốc gia Trung Quốc (CFDA) triển khai thanh tra hoạt động sản xuất vắcxin tr&ecirc;n to&agrave;n quốc, r&agrave; so&aacute;t to&agrave;n bộ quy tr&igrave;nh v&agrave; d&acirc;y chuyền sản xuất vắcxin.</p>\r\n\r\n<p>Qua kiểm tra tại c&ocirc;ng ty Trường Xu&acirc;n Trường Sinh, cơ quan chức năng x&aacute;c định c&ocirc;ng ty n&agrave;y vi phạm nghi&ecirc;m trọng quy tr&igrave;nh cấp ph&eacute;p sản xuất với mục đ&iacute;ch giảm chi ph&iacute; v&agrave; k&ecirc; khống tỷ lệ th&agrave;nh c&ocirc;ng của vắcxin ph&ograve;ng bệnh dại do c&ocirc;ng ty sản xuất.</p>\r\n\r\n<p>CFDA đ&atilde; thu hồi giấy ph&eacute;p sản xuất loại vắcxin tr&ecirc;n của c&ocirc;ng ty, y&ecirc;u cầu c&ocirc;ng ty n&agrave;y dừng sản xuất v&agrave; b&aacute;n sản phẩm, đồng thời đ&igrave;nh chỉ cấp ph&eacute;p tất cả c&aacute;c sản phẩm kh&aacute;c của c&ocirc;ng ty.</p>\r\n\r\n<p>Theo một nh&agrave; ph&acirc;n phối, trong năm 2017, c&ocirc;ng ty tr&ecirc;n đ&atilde; b&aacute;n tổng cộng 252.000 liều vắcxin DPT ph&ograve;ng bệnh bạch cầu, ho g&agrave; v&agrave; uốn v&aacute;n d&ugrave;ng cho trẻ em.</p>\r\n\r\n<p>Vụ việc đ&atilde; g&acirc;y ra l&agrave;n s&oacute;ng phẫn nộ từ c&aacute;c bậc phụ huynh v&agrave; nhiều kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Ng&agrave;y 23-7 vừa qua, Chủ tịch Trung Quốc Tập Cận B&igrave;nh chỉ thị cơ quan chức năng nước n&agrave;y điều tra v&agrave; nghi&ecirc;m trị những đối tượng chịu tr&aacute;ch nhiệm trong vụ b&ecirc; bối.</p>', 'Cảnh sát thành phố Trường Xuân, tỉnh Cát Lâm, Đông Bắc Trung Quốc, đã đề nghị cơ quan công tố phê chuẩn lệnh bắt giữ 18 người tại Công ty công nghệ sinh học Trường Xuân Trường Sinh, công ty sản xuất dược phẩm hàng đầu của Trung Quốc hiện là \"tâm điểm\" một vụ bê bối vắcxin gây chấn động.', '4', '7', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/YoxvaYGoC8vCE37zxpqwvwkeJOyf6My0W01zxlgV.jpeg', 4, '2018-07-30 00:52:32', '2018-08-03 04:35:28'),
(27, 'Lại xảy ra xả súng kinh hoàng ở Mỹ, 3 người bị bắn chết tại chỗ', '<table align=\"center\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Cảnh sát tại hiện trường vụ xả súng. (Nguồn: cbsnews.com)\" src=\"http://baodanang.vn/dataimages/201807/original/images1461950_xa_sung.JPG\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cảnh s&aacute;t tại hiện trường vụ xả s&uacute;ng. (Nguồn: cbsnews.com)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Sở cảnh s&aacute;t th&agrave;nh phố New Orleans cho hay lực lượng an ninh đ&atilde; c&oacute; mặt tại nơi xảy ra vụ xả s&uacute;ng sau khi nhận được th&ocirc;ng b&aacute;o.</p>\r\n\r\n<p>Ba nạn nh&acirc;n đ&atilde; tử vong ngay tại hiện trường, trong khi những người bị thương đ&atilde; được đưa đi cấp cứu.</p>\r\n\r\n<p>Hiện cảnh s&aacute;t đang thu thập th&ocirc;ng tin, điều tra vụ việc.</p>\r\n\r\n<p>Vụ việc xảy ra trong bối cảnh nước Mỹ vẫn đang tranh c&atilde;i về vấn đề sở hữu s&uacute;ng đạn, khi ng&agrave;y c&agrave;ng c&oacute; nhiều vụ xả s&uacute;ng xảy ra tại nhiều nơi tr&ecirc;n khắp nước Mỹ.</p>\r\n\r\n<p>Theo thống k&ecirc;, kể từ th&aacute;ng 1-2013 đến nay, đ&atilde; c&oacute; hơn 280 vụ xả s&uacute;ng tr&ecirc;n khắp nước Mỹ, trung b&igrave;nh mỗi tuần một vụ.</p>', 'Ít nhất 3 người đã thiệt mạng và 7 người bị thương trong một vụ xả súng xảy ra đêm 28-7 tại thành phố New Orleans, thuộc bang Louisiana của Mỹ.', '4', '7', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/i2ffstP551jrHAN9MyNUzHKMJ2UadNybTqnC5YGR.jpeg', 1, '2018-07-30 00:53:58', '2018-08-03 04:32:26'),
(28, '8,3 triệu cử tri Campuchia tham gia bỏ phiếu bầu cử Quốc hội Khóa VI', '<table align=\"center\" style=\"width:490px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Các cử tri xếp hàng đi bầu cử.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461892_vov_cac_cu_tri_di_bau_cu_lrgv.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>C&aacute;c cử tri xếp h&agrave;ng đi bầu cử.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo th&ocirc;ng b&aacute;o ch&iacute;nh thức của Ủy ban Bầu cử Quốc gia Campuchia (NEC), cuộc bầu cử Quốc hội Campuchia Kh&oacute;a VI sẽ diễn ra từ 7h00 đến 15h00 ng&agrave;y 29-7. Đ&acirc;y l&agrave; cuộc bầu cử ghi lại nhiều dấu ấn nhất trong lịch sử, bởi c&oacute; số đảng tham gia tranh cử đ&ocirc;ng đảo nhất trong c&aacute;c kỳ bầu cử tại Campuchia từ trước tới nay, với tổng cộng 20 đảng ch&iacute;nh trị tham gia cuộc đua v&agrave;o Quốc hội.</p>\r\n\r\n<p>Số ghế nghị sĩ Quốc hội Campuchia kh&oacute;a tới l&agrave; 125 ghế, tăng th&ecirc;m 2 ghế so với Quốc hội kh&oacute;a trước. B&ecirc;n cạnh đ&oacute;, số lượng quan s&aacute;t vi&ecirc;n trong nước v&agrave; quốc tế tham gia gi&aacute;m s&aacute;t cuộc bầu cử lần n&agrave;y cũng đạt con số cao kỷ lục với hơn 90.000 người.</p>\r\n\r\n<p>Những con số biết n&oacute;i tr&ecirc;n phản &aacute;nh đ&uacute;ng thực tế t&igrave;nh h&igrave;nh bầu cử tại Campuchia. Đ&ocirc;ng đảo nh&acirc;n d&acirc;n Campuchia rất mong chờ v&agrave;&nbsp; t&iacute;ch cực tham gia bầu cử để thực hiện quyền v&agrave; nghĩa vụ c&ocirc;ng d&acirc;n, tự m&igrave;nh chọn lựa ra những c&aacute; nh&acirc;n ưu t&uacute; v&agrave; đảng l&atilde;nh đạo xứng đ&aacute;ng nhất để tiếp tục đưa đất nước Campuchia ng&agrave;y c&agrave;ng ph&aacute;t triển gi&agrave;u đẹp.</p>\r\n\r\n<p>Rất nhiều đảng ch&iacute;nh trị hưởng ứng v&agrave; tham gia chạy đua trong cuộc bầu cử lần n&agrave;y, với mục đ&iacute;ch g&oacute;p phần đem lại cuộc sống ấm no, hạnh ph&uacute;c cho nh&acirc;n d&acirc;n. Tiến tr&igrave;nh bầu cử đ&atilde; diễn ra trong kh&ocirc;ng kh&iacute; thực sự tự do, d&acirc;n chủ v&agrave; b&igrave;nh đẳng.</p>\r\n\r\n<p>Mặc d&ugrave; cuộc bầu cử Quốc hội Campuchia kh&oacute;a VI vẫn c&ograve;n gặp một số trở ngại như t&igrave;nh h&igrave;nh thời tiết kh&ocirc;ng thuận lợi g&acirc;y lũ lụt tại một số địa phương, nhưng c&ocirc;ng t&aacute;c bầu cử vẫn&nbsp; diễn ra su&ocirc;n sẻ đ&uacute;ng lịch tr&igrave;nh dự kiến. Gần 23.000 điểm bỏ phiếu tr&ecirc;n khắp cả nước Campuchia đ&atilde; sẵn s&agrave;ng phục vụ đ&ocirc;ng đảo cử tri tới thực hiện quyền c&ocirc;ng d&acirc;n của m&igrave;nh.&nbsp;</p>', 'Sáng nay (29-7), hơn 8,3 triệu cử tri Campuchia bắt đầu đi bỏ phiếu bầu cử Quốc hội khóa VI, bầu ra Chính phủ mới giai đoạn từ nay đến năm 2023.', '4', '7', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/FBbf2m3dqYeYeSOm6d2n7ZbUXcL7MH1cUtgfQVWv.jpeg', 0, '2018-07-30 00:55:11', '2018-08-03 01:31:34'),
(29, 'Ngôi nhà nghệ thuật bên sông Hàn', '<p>Tọa lạc b&ecirc;n bờ s&ocirc;ng H&agrave;n thơ mộng, ở số 305 Trần Hưng Đạo, Art House Coffee l&agrave; một trong những địa chỉ bạn n&ecirc;n gh&eacute; khi đến Đ&agrave; Nẵng. Nơi đ&acirc;y quả l&agrave; một ng&ocirc;i nh&agrave; của nghệ thuật, khi c&oacute; một thực đơn gồm c&agrave;-ph&ecirc; v&agrave; c&aacute;c loại thức uống đa dạng, điểm cộng đầu ti&ecirc;n của Art House Coffee ch&iacute;nh l&agrave; kh&ocirc;ng gian tho&aacute;ng rộng, nhẹ nh&agrave;ng, l&agrave; sự cầu kỳ, tinh tế trong thiết kế nội thất, b&agrave;i tr&iacute; b&agrave;n ghế, chỗ ngồi.</p>\r\n\r\n<p>Đ&acirc;y c&ograve;n l&agrave; ng&ocirc;i nh&agrave; của đồ gốm lưu niệm, của tranh v&agrave; s&acirc;n khấu &acirc;m nhạc cuối tuần. Với những ai kh&ocirc;ng th&iacute;ch sự ồn &agrave;o, x&ocirc; bồ, nhộn nhịp th&igrave; Art House Coffee&nbsp; l&agrave; lựa chọn kh&aacute; l&yacute; tưởng.</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Không gian thoáng đãng, nhẹ nhàng và đầy tính nghệ thuật, Art House Coffee là một trong những địa chỉ bạn có thể ghé thăm khi đến Đà Nẵng.  Ảnh: P.A\" src=\"http://baodanang.vn/dataimages/201807/original/images1461503_Art_house_2.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng, nhẹ nh&agrave;ng v&agrave; đầy t&iacute;nh nghệ thuật, Art House Coffee l&agrave; một trong những địa chỉ bạn c&oacute; thể gh&eacute; thăm khi đến Đ&agrave; Nẵng. Ảnh: P.A</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tầng 1 của Art House Coffee phục vụ thức uống v&agrave; điểm t&acirc;m cho kh&aacute;ch tự do như nhiều qu&aacute;n<br />\r\nc&agrave;-ph&ecirc; kh&aacute;c; tầng 2 c&oacute; hẳn quầy buffet ưu ti&ecirc;n phục vụ kh&aacute;ch lưu tr&uacute; tại kh&aacute;ch sạn Art House ngay cạnh. Kh&aacute;ch lẻ ở ngo&agrave;i c&oacute; nhu cầu c&oacute; thể li&ecirc;n lệ quầy lễ t&acirc;n để mua phiếu ăn buffet với thực đơn kh&aacute; phong ph&uacute;, gi&aacute; chỉ 99.000 đồng.</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"\" src=\"http://baodanang.vn/dataimages/201807/original/images1461504_Art_house.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Mặc d&ugrave; c&oacute; nhiều điểm cộng từ kh&ocirc;ng gian, từ cung c&aacute;ch phục vụ của nh&acirc;n vi&ecirc;n, song, theo nhiều &yacute; kiến, gi&aacute; thức uống (từ 35.000 &ndash; 60.000 đồng) v&agrave; điểm t&acirc;m (từ 45.000 &ndash; 99.000 đồng) l&agrave; hơi cao n&ecirc;n Art House Coffee cho đến nay vẫn kh&aacute; k&eacute;n kh&aacute;ch đến.</p>', 'Tọa lạc bên bờ sông Hàn thơ mộng, ở số 305 Trần Hưng Đạo, Art House Coffee là một trong những địa chỉ bạn nên ghé khi đến Đà Nẵng. Nơi đây quả là một ngôi nhà của nghệ thuật, khi có một thực đơn gồm cà-phê và các loại thức uống đa dạng, điểm cộng đầu tiên của Art House Coffee chính là không gian thoáng rộng, nhẹ nhàng, là sự cầu kỳ, tinh tế trong thiết kế nội thất, bài trí bàn ghế, chỗ ngồi.', '4', '13', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/RZJnh0m1bNF6aImijMa7cMZoLEuIWgGBtFlMhaOD.jpeg', 3, '2018-07-30 19:46:07', '2018-08-05 18:59:54'),
(30, 'Mì Quảng và bánh tráng Túy Loan', '<p>L&agrave;ng T&uacute;y Loan được khai ph&aacute; từ thời vua L&ecirc; Th&aacute;nh T&ocirc;n (1470 - 1497). Sau đ&oacute; đến năm Canh T&yacute; (1900), dưới thời vua Th&agrave;nh Th&aacute;i, h&igrave;nh th&agrave;nh thiết chế văn h&oacute;a l&agrave;ng. L&agrave;ng nằm s&aacute;t quốc lộ 14B thuộc huyện H&ograve;a Vang, c&aacute;ch trung t&acirc;m Đ&agrave; Nẵng khoảng 15km về hướng t&acirc;y. Nơi đ&acirc;y từ l&acirc;u đ&atilde; nổi tiếng với đặc sản m&igrave; Quảng v&agrave; b&aacute;nh tr&aacute;ng với thương hiệu T&uacute;y Loan.</p>\r\n\r\n<p>Đ&agrave; Nẵng cuối tuần giới thiệu một số h&igrave;nh ảnh của NSNA Huy Đằng về c&aacute;c c&ocirc;ng đoạn để h&igrave;nh th&agrave;nh n&ecirc;n hai loại đặc sản n&agrave;y.</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Để làm nên những sợi mì Quảng ngon và những cái bánh tráng thơm lừng, phần lớn là nhờ bàn tay khéo léo của những người phụ nữ.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461376_3A.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Để l&agrave;m n&ecirc;n những sợi m&igrave; Quảng ngon v&agrave; những c&aacute;i b&aacute;nh tr&aacute;ng thơm lừng, phần lớn l&agrave; nhờ b&agrave;n tay kh&eacute;o l&eacute;o của những người phụ nữ.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Trước đây, người thợ xay bột gạo bằng cối đá thủ công (ngày nay bột được xay bằng máy).\" src=\"http://baodanang.vn/dataimages/201807/original/images1461377_2A.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Trước đ&acirc;y, người thợ xay bột gạo bằng cối đ&aacute; thủ c&ocirc;ng (ng&agrave;y nay bột được xay bằng m&aacute;y).</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Bánh tráng sau khi tráng xong được phơi nắng thật khô, để khi nướng mới giòn.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461378_4A.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>B&aacute;nh tr&aacute;ng sau khi tr&aacute;ng xong được phơi nắng thật kh&ocirc;, để khi nướng mới gi&ograve;n.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Tô mì Quảng Túy Loan và các gia vị ăn kèm, tất nhiên không thể thiếu bánh tráng.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461379_5__To_my_quang_tuy_loan_va_nhung_do_an_kemA_.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>T&ocirc; m&igrave; Quảng T&uacute;y Loan v&agrave; c&aacute;c gia vị ăn k&egrave;m, tất nhi&ecirc;n kh&ocirc;ng thể thiếu b&aacute;nh tr&aacute;ng.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Bột gạo được xay mịn rồi trộn ít hạt mè (vừng) là nguyên liệu chính để làm bánh tráng nổi tiếng.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461380_1A.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bột gạo được xay mịn rồi trộn &iacute;t hạt m&egrave; (vừng) l&agrave; nguy&ecirc;n liệu ch&iacute;nh để l&agrave;m b&aacute;nh tr&aacute;ng nổi tiếng.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Nướng bánh tráng Túy Loan là một trong những môn thi trong lễ hội đình làng Túy Loan hằng năm.\" src=\"http://baodanang.vn/dataimages/201807/original/images1461381_7A.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nướng b&aacute;nh tr&aacute;ng T&uacute;y Loan l&agrave; một trong những m&ocirc;n thi trong lễ hội đ&igrave;nh l&agrave;ng T&uacute;y Loan hằng năm.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Bánh tráng Túy Loan được bày bán trong các lễ hội, hội chợ và hàng quán khắp nơi trên địa bàn huyện Hòa Vang và các vùng lân cận.             \" src=\"http://baodanang.vn/dataimages/201807/original/images1461382_8A.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>B&aacute;nh tr&aacute;ng T&uacute;y Loan được b&agrave;y b&aacute;n trong c&aacute;c lễ hội, hội chợ v&agrave; h&agrave;ng qu&aacute;n khắp nơi tr&ecirc;n địa b&agrave;n huyện H&ograve;a Vang v&agrave; c&aacute;c v&ugrave;ng l&acirc;n cận.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'Làng Túy Loan được khai phá từ thời vua Lê Thánh Tôn (1470 - 1497). Sau đó đến năm Canh Tý (1900), dưới thời vua Thành Thái, hình thành thiết chế văn hóa làng. Làng nằm sát quốc lộ 14B thuộc huyện Hòa Vang, cách trung tâm Đà Nẵng khoảng 15km về hướng tây. Nơi đây từ lâu đã nổi tiếng với đặc sản mì Quảng và bánh tráng với thương hiệu Túy Loan.', '4', '13', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/TwJDK6DLVoTG5jDF0GU5JkPpnY5dN6FgzR3u8lH9.jpeg', 2, '2018-07-30 19:47:42', '2018-08-03 04:24:56'),
(32, 'Từ tháng 8-2018: 6 chính sách tiền lương, nghề nghiệp, phụ cấp có hiệu lực', '<table align=\"center\" style=\"width:640px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Tăng mức trợ cấp và phụ cấp ưu đãi người có công, tiêu chuẩn chức danh trong giáo dục nghề nghiệp, mức lương của giáo viên dự bị đại học, phụ cấp với cán bộ, chiến sĩ cảnh vệ, trợ cấp cán bộ xã già yếu nghỉ việc…là những chính sách mới có hiệu lực từ tháng 8-2018.\" src=\"http://baodanang.vn/dataimages/201807/original/images1462310_tien_luong_153299810823171922950.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tăng mức trợ cấp v&agrave; phụ cấp ưu đ&atilde;i người c&oacute; c&ocirc;ng, ti&ecirc;u chuẩn chức danh trong gi&aacute;o dục nghề nghiệp, mức lương của gi&aacute;o vi&ecirc;n dự bị đại học, phụ cấp với c&aacute;n bộ, chiến sĩ cảnh vệ, trợ cấp c&aacute;n bộ x&atilde; gi&agrave; yếu nghỉ việc&hellip;l&agrave; những ch&iacute;nh s&aacute;ch mới c&oacute; hiệu lực từ th&aacute;ng 8-2018.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>1. Tăng mức trợ cấp, phụ cấp ưu đ&atilde;i người c&oacute; c&ocirc;ng</strong></p>\r\n\r\n<p>Từ ng&agrave;y 27-8, Nghị định 99/2018/NĐ-CP quy định mức trợ cấp, phụ cấp ưu đ&atilde;i đối với người c&oacute; c&ocirc;ng với c&aacute;ch mạng c&oacute; hiệu lực thi h&agrave;nh.</p>\r\n\r\n<p>Theo đ&oacute;, mức trợ cấp, phụ cấp ưu đ&atilde;i h&agrave;ng th&aacute;ng được quy định như sau:</p>\r\n\r\n<p>Mức trợ cấp đối với Người hoạt động c&aacute;ch mạng trước ng&agrave;y 1-1-1945: Diện tho&aacute;t ly l&agrave; 1.693.000 đồng; diện kh&ocirc;ng tho&aacute;t ly 2.874.000 đồng.</p>\r\n\r\n<p>Trợ cấp tiền tuất đối với th&acirc;n nh&acirc;n của 1 liệt sĩ 1.515.000 đồng; trợ cấp tiền tuất đối với th&acirc;n nh&acirc;n của 2 liệt sĩ 3.030.000 đồng; trợ cấp tiền tuất đối với th&acirc;n nh&acirc;n của 3 liệt sĩ trở l&ecirc;n 4.545.000 đồng.</p>\r\n\r\n<p>Về trợ cấp ưu đ&atilde;i h&agrave;ng năm được quy định như sau: Liệt sĩ kh&ocirc;ng c&ograve;n người hưởng trợ cấp tiền tuất h&agrave;ng th&aacute;ng th&igrave; người thờ c&uacute;ng liệt sĩ được hưởng trợ cấp thờ c&uacute;ng l&agrave; 500.000 đồng.</p>\r\n\r\n<p>Trợ cấp ưu đ&atilde;i đối với người c&oacute; c&ocirc;ng với c&aacute;ch mạng theo quy định của Ph&aacute;p lệnh Ưu đ&atilde;i người c&oacute; c&ocirc;ng với c&aacute;ch mạng kh&ocirc;ng hưởng lương hoặc sinh hoạt ph&iacute; khi đi học tại cơ sở gi&aacute;o dục nghề nghiệp, cơ sở gi&aacute;o dục đại học l&agrave; 300.000 đồng&hellip;</p>\r\n\r\n<p><strong>2. Vi&ecirc;n chức ng&agrave;nh gi&aacute;o dục nghề nghiệp tu&acirc;n thủ 4 ti&ecirc;u chuẩn</strong></p>\r\n\r\n<p>Th&ocirc;ng tư 03/2018/TT - BLĐTBXH của Bộ LĐ-TB&amp;XH quy định ti&ecirc;u chuẩn chức danh nghề nghiệp vi&ecirc;n chức chuy&ecirc;n ng&agrave;nh gi&aacute;o dục nghề nghiệp c&oacute; hiệu lực từ ng&agrave;y 1-8.</p>\r\n\r\n<p>Theo đó, vi&ecirc;n chức ng&agrave;nh gi&aacute;o dục nghề nghiệp cần đ&aacute;p ứng 4 ti&ecirc;u chuẩn chung về đạo đức nghề nghiệp của vi&ecirc;n chức chuy&ecirc;n ng&agrave;nh gi&aacute;o dục nghề nghiệp : T&acirc;m huyết với nghề, giữ g&igrave;n phẩm chất, danh dự, uy t&iacute;n, lương t&acirc;m nh&agrave; gi&aacute;o&hellip;</p>\r\n\r\n<p>Tận tụy với c&ocirc;ng việc; thực hiện đ&uacute;ng điều lệ, quy chế, nội quy của cơ sở gi&aacute;o dục nghề nghiệp, của ng&agrave;nh.</p>\r\n\r\n<p>C&ocirc;ng bằng trong giảng dạy v&agrave; gi&aacute;o dục, đ&aacute;nh gi&aacute; đ&uacute;ng thực chất năng lực của người học; thực h&agrave;nh tiết kiệm, chống bệnh th&agrave;nh t&iacute;ch, chống tham nhũng, l&atilde;ng ph&iacute;.</p>\r\n\r\n<p>Đ&aacute;p ứng c&aacute;c ti&ecirc;u chuẩn đạo đức nghề nghiệp kh&aacute;c của vi&ecirc;n chức chuy&ecirc;n ng&agrave;nh gi&aacute;o dục nghề nghiệp quy định tại khoản 4 Điều 53 Luật gi&aacute;o dục nghề nghiệp &hellip;</p>\r\n\r\n<p><strong>3. Quy định c&aacute;c mức lương của gi&aacute;o vi&ecirc;n dự bị đại học</strong></p>\r\n\r\n<p>Th&ocirc;ng tư 07/2018/TT - BNV do Bộ Nội vụ ban h&agrave;nh về quy định bổ nhiệm v&agrave; xếp lương gi&aacute;o vi&ecirc;n dự bị đại học n&ecirc;u r&otilde;, từ 1-8, vi&ecirc;n chức được bổ nhiệm v&agrave;o c&aacute;c chức danh nghề nghiệp gi&aacute;o vi&ecirc;n dự bị đại học quy định tại Th&ocirc;ng tư số 06/2017/TT-BNV v&agrave; Th&ocirc;ng tư số 30/2017/TT-BGDĐT được &aacute;p dụng bảng lương tương ứng ban h&agrave;nh k&egrave;m theo Nghị định 204/2004/NĐ-CP về chế độ tiền lương đối với c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức v&agrave; lực lượng vũ trang, như sau:</p>\r\n\r\n<p>Chức danh nghề nghiệp gi&aacute;o vi&ecirc;n dự bị đại học hạng I (m&atilde; số V.07.07.17) được &aacute;p dụng hệ số lương của vi&ecirc;n chức loại A2 (nh&oacute;m A2.1), từ hệ số lương 4,40 đến hệ số lương 6,78.</p>\r\n\r\n<p>Chức danh nghề nghiệp gi&aacute;o vi&ecirc;n dự bị đại học hạng II (m&atilde; số V.07.07.18) được &aacute;p dụng hệ số lương của vi&ecirc;n chức loại A2 (nh&oacute;m A2.2), từ hệ số lương 4,00 đến hệ số lương 6,38.</p>\r\n\r\n<p>Chức danh nghề nghiệp gi&aacute;o vi&ecirc;n dự bị đại học hạng III (m&atilde; số V.07.07.19) được &aacute;p dụng hệ số lương của vi&ecirc;n chức loại A1, từ hệ số lương 2,34 đến hệ số lương 4,98&hellip;</p>\r\n\r\n<p><strong>4. Quy định mức phụ cấp với c&aacute;n bộ, chiến sĩ cảnh vệ</strong></p>\r\n\r\n<p>Từ ng&agrave;y 10-8, Nghị định 90/2018/NĐ-CP quy định chi tiết thi h&agrave;nh một số điều của Luật Cảnh vệ sẽ c&oacute; hiệu lực. Theo đ&oacute;, c&aacute;n bộ, chiến sĩ cảnh vệ được hưởng phụ cấp đặc th&ugrave; từ 15% đến 30% t&iacute;nh tr&ecirc;n mức lương cấp bậc h&agrave;m hoặc phụ cấp cấp bậc h&agrave;m hiện hưởng ngo&agrave;i c&aacute;c chế độ phụ cấp kh&aacute;c (nếu c&oacute;).</p>\r\n\r\n<p>Mức phụ cấp 30% &aacute;p dụng đối với: C&aacute;n bộ, chiến sĩ thực hiện nhiệm vụ: Bảo vệ tiếp cận; bảo vệ kh&aacute;ch quốc tế đến thăm, l&agrave;m việc tại Việt Nam; tuần tra, canh g&aacute;c tại nơi ở, nơi l&agrave;m việc, khu vực trọng yếu, sự kiện đặc biệt quan trọng; C&aacute;n bộ giữ chức vụ l&atilde;nh đạo, chỉ huy; C&aacute;n bộ, chiến sĩ kh&ocirc;ng giữ chức vụ c&oacute; cấp bậc h&agrave;m từ Trung &uacute;y hoặc c&oacute; mức lương tương đương cấp bậc h&agrave;m Trung &uacute;y trở xuống; chiến sĩ hưởng phụ cấp cấp bậc h&agrave;m.</p>\r\n\r\n<p>Mức phụ cấp 25% &aacute;p dụng đối với: C&aacute;n bộ, chiến sĩ thực hiện nhiệm vụ: L&aacute;i xe bảo vệ tiếp cận, l&aacute;i xe nghiệp vụ, dẫn đường, hộ tống; kiểm tra chất nổ, chất ch&aacute;y, chất ph&oacute;ng xạ hoặc vật nguy hiểm kh&aacute;c, kiểm nghiệm độc chất; t&aacute;c chiến; trinh s&aacute;t; th&ocirc;ng tin phục vụ c&ocirc;ng t&aacute;c bảo vệ; đặc nhiệm; cơ động; C&aacute;n bộ, chiến sĩ kh&ocirc;ng giữ chức vụ c&oacute; cấp bậc h&agrave;m Thượng &uacute;y, Đại &uacute;y hoặc c&oacute; mức lương tương đương cấp bậc h&agrave;m Thượng &uacute;y, Đại &uacute;y&hellip;</p>\r\n\r\n<p><strong>5. &Aacute;p dụng mức trợ cấp mới tới c&aacute;n bộ x&atilde; gi&agrave; yếu nghỉ việc</strong></p>\r\n\r\n<p>Th&ocirc;ng tư 08/2018/TT-BNV của Bộ Nội vụ hướng dẫn thực hiện điều chỉnh mức trợ cấp h&agrave;ng th&aacute;ng tới c&aacute;n bộ x&atilde; đ&atilde; nghỉ việc theo QĐ 130-CP ng&agrave;y 20-6-1975 của Hội đồng Ch&iacute;nh phủ v&agrave; QĐ 111-HĐBT ng&agrave;y 13/10/1981 của Hội đồng Bộ trưởng (gọi tắt l&agrave; c&aacute;n bộ x&atilde; gi&agrave; yếu nghỉ việc) sẽ c&oacute; hiệu lực từ ng&agrave;y 15/8.</p>\r\n\r\n<p>Theo đ&oacute;, việc điều chỉnh mức trợ cấp h&agrave;ng th&aacute;ng đối với c&aacute;n bộ x&atilde; gi&agrave; yếu nghỉ việc theo quy định tại Điều 2 Nghị định số 88/2018/NĐ-CP ng&agrave;y 15-6-2018 của Ch&iacute;nh phủ điều chỉnh lương hưu, trợ cấp bảo hiểm x&atilde; hội v&agrave; trợ cấp h&agrave;ng th&aacute;ng tăng th&ecirc;m 6,92% mức trợ cấp h&agrave;ng th&aacute;ng tr&ecirc;n mức trợ cấp được hưởng tại thời điểm th&aacute;ng 6 năm 2018.</p>\r\n\r\n<p>Như vậy, mức trợ cấp h&agrave;ng th&aacute;ng được hưởng từ ng&agrave;y 1-7-2018 của c&aacute;n bộ x&atilde; gi&agrave; yếu nghỉ việc được t&iacute;nh như sau.</p>\r\n\r\n<p>Đối với c&aacute;n bộ nguy&ecirc;n l&agrave; B&iacute; thư Đảng ủy, Chủ tịch Ủy ban nh&acirc;n d&acirc;n x&atilde;: 1.846.000 đồng/th&aacute;ng x 1,0692 = 1.974.000 đồng/th&aacute;ng.</p>\r\n\r\n<p>Đối với c&aacute;n bộ nguy&ecirc;n l&agrave; Ph&oacute; B&iacute; thư, Ph&oacute; Chủ tịch, Thường trực Đảng ủy, ủy vi&ecirc;n thư k&yacute; Ủy ban nh&acirc;n d&acirc;n, Thư k&yacute; Hội đồng nh&acirc;n d&acirc;n x&atilde;, X&atilde; đội trưởng, Trưởng c&ocirc;ng an x&atilde;: 1.786.000 đồng/th&aacute;ng x 1,0692 = 1.910.000 đồng/th&aacute;ng.</p>\r\n\r\n<p>Đối với c&aacute;c chức danh c&ograve;n lại: 1.653.000 đồng/th&aacute;ng x 1,0692 = 1.768.000 đồng/th&aacute;ng.</p>\r\n\r\n<p><strong>6. Hỗ trợ với c&ocirc;ng chức, vi&ecirc;n chức H&agrave; Nội</strong></p>\r\n\r\n<p>Hội đồng nh&acirc;n d&acirc;n th&agrave;nh phố H&agrave; Nội kh&oacute;a XV, kỳ họp thứ 6 đ&atilde; th&ocirc;ng qua Nghị quyết số 04/2018/NQ - HĐNT quy định về việc ban h&agrave;nh nội dung, mức chi đặc th&ugrave; thuộc thẩm quyền của HĐND TP H&agrave; Nội.</p>\r\n\r\n<p>Theo đ&oacute;, từ ng&agrave;y 1-8-2018 - 31-12-2020, c&ocirc;ng chức, vi&ecirc;n chức chuy&ecirc;n tr&aacute;ch về c&ocirc;ng nghệ th&ocirc;ng tin tại H&agrave; Nội sẽ được hỗ trợ 2.780.000 đồng/người/th&aacute;ng, nếu c&oacute; tr&igrave;nh độ đại học trở l&ecirc;n hoặc 2.085.000 đồng/người/th&aacute;ng, nếu c&oacute; tr&igrave;nh độ cao đẳng hoặc trung cấp.</p>\r\n\r\n<p>Nghị quyết 04/2018/NQ - HĐNT cũng n&ecirc;u r&otilde;, trong thời gian tr&ecirc;n, một số khoản chi sẽ được quy định như sau:</p>\r\n\r\n<p>Chi hỗ trợ ban đầu cho c&aacute;n bộ bị thương, tai nạn khi đang l&agrave;m nhiệm vụ đảm bảo trật tự an to&agrave;n giao th&ocirc;ng (TTATGT) l&agrave; 2.000.000 đồng/người.</p>\r\n\r\n<p>Chi hỗ trợ ban đầu cho gia đ&igrave;nh c&aacute;n bộ hy sinh đang l&agrave;m nhiệm vụ đảm bảo TTATGT l&agrave; 5.000.000 đồng/người; Bồi dưỡng th&agrave;nh vi&ecirc;n của Ban An to&agrave;n giao th&ocirc;ng Th&agrave;nh phố 700.000 đồng/người/th&aacute;ng.</p>\r\n\r\n<p>Chi hoạt động kiểm tra, gi&aacute;m s&aacute;t về TTATGT l&agrave; 100.000 đồng/người/ca (04 giờ trở l&ecirc;n); chi bồi dưỡng c&aacute;n bộ Thanh tra giao th&ocirc;ng 1.000.000 đồng/người/th&aacute;ng&hellip;</p>', 'Tăng mức trợ cấp và phụ cấp ưu đãi người có công, tiêu chuẩn chức danh trong giáo dục nghề nghiệp, mức lương của giáo viên dự bị đại học, phụ cấp với cán bộ, chiến sĩ cảnh vệ, trợ cấp cán bộ xã già yếu nghỉ việc…là những chính sách mới có hiệu lực từ tháng 8-2018.', '4', '4', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/ettBbBNWjh0yF6JkuR7m0AeDuNmJQJTImLK8wZhD.jpeg', 0, '2018-07-31 20:21:57', '2018-08-02 21:46:00'),
(33, 'Cục Hàng không nói gì về nghi vấn \'bôi trơn\' đầu vào phi công?', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Ảnh minh họa. (Ảnh: Việt Hùng/Vietnam+)\" src=\"http://baodanang.vn/dataimages/201807/original/images1462386_2.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ảnh minh họa. (Ảnh: Việt H&ugrave;ng/Vietnam+)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Cục H&agrave;ng kh&ocirc;ng xem x&eacute;t đầu ra của c&aacute;c ứng vi&ecirc;n dựa tr&ecirc;n hồ sơ bằng cấp, huấn luyện trong qu&aacute; tr&igrave;nh học ở nước ngo&agrave;i v&agrave; c&aacute;c bằng cấp, chứng chỉ được nh&agrave; chức tr&aacute;ch nơi c&aacute;c học vi&ecirc;n l&agrave;m cơ sở để c&ocirc;ng nhận bằng l&aacute;i t&agrave;u bay.</p>\r\n\r\n<p>Thừa nhận Đại biểu Quốc hội Nguyễn Sỹ Cương c&oacute; &yacute; kiến c&aacute;c trường học phi c&ocirc;ng đa số l&agrave; c&aacute;c trường nhỏ lẻ, chất lượng đ&agrave;o tạo thấp nhưng thực tế Cục H&agrave;ng kh&ocirc;ng nhấn mạnh, Cục đ&atilde; tổ chức đ&aacute;nh gi&aacute;, c&ocirc;ng nhận c&aacute;c trường đ&agrave;o tạo người l&aacute;i t&agrave;u bay đ&atilde; được c&aacute;c Cục H&agrave;ng kh&ocirc;ng của c&aacute;c quốc gia ph&ecirc; chuẩn (hiện nay chủ yếu c&aacute;c trường tại Hoa Kỳ, Newzealand, Australia v&agrave; ch&acirc;u &Acirc;u). Cục tập trung v&agrave;o ph&ecirc; chuẩn chất lượng đ&agrave;o tạo của phi c&ocirc;ng v&agrave; tr&ecirc;n cơ sở ph&ecirc; chuẩn đầu ra của c&aacute;c nh&agrave; chức tr&aacute;ch quốc gia tr&ecirc;n c&ocirc;ng nhận bằng chứng chỉ đ&agrave;o tạo.</p>\r\n\r\n<p>&ldquo;Như vậy, bất kể đầu v&agrave;o huấn luyện như thế n&agrave;o, đầu ra huấn luyện của phi c&ocirc;ng dựa tr&ecirc;n hai điều kiện ch&iacute;nh l&agrave; tổ chức huấn luyện được ph&ecirc; chuẩn bởi quốc gia th&agrave;nh vi&ecirc;n Tổ chức H&agrave;ng kh&ocirc;ng d&acirc;n dụng quốc tế (ICAO) v&agrave; được Cục H&agrave;ng kh&ocirc;ng Việt Nam đ&aacute;nh gi&aacute; tu&acirc;n thủ c&aacute;c quy định của Việt Nam về tổ chức huấn luyện phi c&ocirc;ng; c&aacute; nh&acirc;n c&aacute;c người l&aacute;i t&agrave;u bay phải được Cục H&agrave;ng kh&ocirc;ng của quốc gia tổ chức huấn luyện tổ chức kiểm tra, đ&aacute;nh gi&aacute; để cấp Bằng người l&aacute;i t&agrave;u bay (PPL, CPL/IR),&rdquo; l&atilde;nh đạo Cục H&agrave;ng kh&ocirc;ng cho hay.</p>\r\n\r\n<p>Ph&iacute;a Cục H&agrave;ng kh&ocirc;ng Việt Nam nh&igrave;n nhận, việc huấn luyện, đ&agrave;o tạo v&agrave; c&ocirc;ng nhận bằng phi c&ocirc;ng do c&aacute;c quốc gia th&agrave;nh vi&ecirc;n ICAO l&agrave; thực tiễn đang &aacute;p dụng hiện nay đ&atilde; đảm bảo nguồn đầu v&agrave;o của c&aacute;c phi c&ocirc;ng về Việt Nam sau khi học tại nước ngo&agrave;i.</p>\r\n\r\n<p>Sau khi được đ&agrave;o tạo v&agrave; c&oacute; Bằng l&aacute;i t&agrave;u bay (CPL, IR), c&aacute;c phi c&ocirc;ng cơ bản được huấn luyện chuyển loại c&aacute;c loại t&agrave;u bay (v&iacute; dụ A320). Để đảm bảo chất lượng, c&aacute;c tổ chức huấn luyện loại t&agrave;u bay đều được Cục H&agrave;ng kh&ocirc;ng ph&ecirc; chuẩn theo ti&ecirc;u chuẩn của ICAO, c&aacute;c nh&agrave; sản xuất t&agrave;u bay v&agrave; Bộ Quy chuẩn an to&agrave;n h&agrave;ng kh&ocirc;ng.</p>\r\n\r\n<p>&ldquo;Việc ho&agrave;n th&agrave;nh c&aacute;c nội dung huấn luyện l&agrave; bắt buộc v&agrave; do đ&oacute; t&ugrave;y v&agrave;o nỗ lực của c&aacute;c học vi&ecirc;n c&oacute; thể c&oacute; thời gian huấn luyện kh&aacute;c nhau. Đồng thời, Cục H&agrave;ng kh&ocirc;ng Việt Nam c&oacute; c&aacute;c ti&ecirc;u chuẩn v&agrave; đ&aacute;nh gi&aacute; để đảm bảo đầu ra của c&aacute;c phi c&ocirc;ng đ&aacute;p ứng theo y&ecirc;u cầu của ICAO, Bộ Quy chuẩn an to&agrave;n h&agrave;ng kh&ocirc;ng,&rdquo; l&atilde;nh đạo Cục H&agrave;ng kh&ocirc;ng quả quyết.</p>\r\n\r\n<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Hình chỉ mang tính minh họa. (Nguồn: Vietnam Airlines)\" src=\"http://baodanang.vn/dataimages/201807/original/images1462387_3.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>H&igrave;nh chỉ mang t&iacute;nh minh họa. (Nguồn: Vietnam Airlines)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Li&ecirc;n quan đến tồn tại trong qu&aacute; tr&igrave;nh huấn luyện phi c&ocirc;ng, trong đ&oacute; c&oacute; việc &ldquo;b&ocirc;i trơn&rdquo; h&agrave;ng chục ngh&igrave;n USD như Đại biểu Quốc hội phản &aacute;nh, ph&iacute;a Cục H&agrave;ng kh&ocirc;ng Việt Nam cho rằng, Cục cần c&oacute; thời gian để x&aacute;c minh, đ&aacute;nh gi&aacute; do đ&acirc;y l&agrave; hoạt động của c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng. Tuy nhi&ecirc;n, Cục H&agrave;ng kh&ocirc;ng ho&agrave;n to&agrave;n độc lập với hoạt động của c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng khi tuyển chọn, lựa chọn v&agrave; huấn luyện người l&aacute;i t&agrave;u bay.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Cục H&agrave;ng kh&ocirc;ng c&oacute; hệ thống quy tr&igrave;nh đ&aacute;nh gi&aacute; ho&agrave;n to&agrave;n độc lập v&agrave; kh&aacute;ch quan. Cục đảm bảo tất cả c&aacute;c phi c&ocirc;ng được cấp bằng phải chịu đ&aacute;nh gi&aacute; của Cục H&agrave;ng kh&ocirc;ng.</p>\r\n\r\n<p>&ldquo;C&aacute;c tồn tại do Đại biểu Quốc hội n&ecirc;u ra kh&ocirc;ng ảnh hưởng đến qu&aacute; tr&igrave;nh đ&aacute;nh gi&aacute; độc lập, kh&aacute;ch quan v&agrave; chất lượng của Cục đối với c&aacute;c phi c&ocirc;ng trước khi được cấp bằng v&agrave; cho ph&eacute;p hoạt động khai th&aacute;c,&rdquo; l&atilde;nh đạo Cục H&agrave;ng kh&ocirc;ng khẳng định.</p>\r\n\r\n<p>Về c&ocirc;ng t&aacute;c cấp bằng v&agrave; chấp nhận chuyển đổi nh&agrave; khai th&aacute;c của Cục H&agrave;ng kh&ocirc;ng Việt Nam, theo quy định của Th&ocirc;ng tư số 21/2017/TT-BGTVT quy định về quản l&yacute; nh&acirc;n vi&ecirc;n tr&igrave;nh độ cao, để được chấp nhận chuyển đổi người khai th&aacute;c t&agrave;u bay, nh&acirc;n vi&ecirc;n h&agrave;ng kh&ocirc;ng tr&igrave;nh độ cao (phi c&ocirc;ng) phải đ&aacute;p ứng c&aacute;c y&ecirc;u cầu gồm đ&atilde; chấm dứt hợp đồng lao động v&agrave; thực hiện đầy đủ c&aacute;c nghĩa vụ (nếu c&oacute;) đối với người khai th&aacute;c t&agrave;u bay theo quy định; c&oacute; hợp đồng lao động với người khai th&aacute;c t&agrave;u bay mới.</p>\r\n\r\n<p>Như vậy, một phi c&ocirc;ng muốn chuyển đổi nh&agrave; khai th&aacute;c t&agrave;u bay phải cung cấp đủ bộ hồ sơ gồm hồ sơ chấm dứt hợp đồng lao động với người khai th&aacute;c t&agrave;u bay cũ, hợp đồng lao động mới v&agrave; hồ sơ huấn luyện chuyển đổi nh&agrave; khai th&aacute;c.</p>\r\n\r\n<p>Đối với c&aacute;c điều kiện chấm dứt hợp đồng, c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng n&oacute;i ri&ecirc;ng v&agrave; c&aacute;c tổ chức n&oacute;i chung c&oacute; hợp đồng lao động quy định tr&aacute;ch nhiệm d&acirc;n sự giữa c&aacute; nh&acirc;n v&agrave; tổ chức, Cục H&agrave;ng kh&ocirc;ng hiện đang thực hiện theo đ&uacute;ng quy định của ph&aacute;p luật về nội dung chuyển đổi nh&agrave; khai th&aacute;c v&agrave; kh&ocirc;ng c&oacute; thẩm quyền can thiệp việc thực hiện hợp đồng d&acirc;n sự về tr&aacute;ch nhiệm bồi ho&agrave;n huấn luyện đ&agrave;o tạo của c&aacute;c phi c&ocirc;ng v&agrave; h&atilde;ng h&agrave;ng kh&ocirc;ng.</p>', 'Liên quan đến nghi vấn tiêu cực chất lượng đào tạo, huấn luyện phi công Vietnam Airlines, sau chất vấn của đại biểu Quốc hội Nguyễn Sỹ Cương, Phó Chủ nhiệm Ủy ban Đối ngoại của Quốc hội, Cục Hàng không Việt Nam đã có báo cáo trong đó nhấn mạnh có các tiêu chuẩn và đánh giá để đảm bảo đầu ra của các phi công.', '4', '4', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/OJqzFaG05BUKHAvB07kjN8fGsKR7d3yttzGAqnrV.jpeg', 3, '2018-07-31 20:24:48', '2018-08-03 19:06:32'),
(34, 'Lan tỏa phong trào \"Cựu chiến binh gương mẫu\"', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Cán bộ, hội viên Hội Cựu chiến binh Hòa Khương tham gia cải tạo vườn tạp, giúp hội viên có hoàn cảnh khó khăn phát triển kinh tế gia đình.\" src=\"http://baodanang.vn/dataimages/201807/original/images1462221_Lan_toa.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>C&aacute;n bộ, hội vi&ecirc;n Hội Cựu chiến binh H&ograve;a Khương tham gia cải tạo vườn tạp, gi&uacute;p hội vi&ecirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn ph&aacute;t triển kinh tế gia đ&igrave;nh.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Thăm trang trại của CCB Chu Văn Phong (th&ocirc;n Phước Sơn) đ&uacute;ng l&uacute;c &ocirc;ng đang xuất h&agrave;ng chục con cừu, d&aacute;ng người nhỏ nhắn trong bộ qu&acirc;n phục đ&atilde; ngả m&agrave;u, &ocirc;ng thoăn thoắt giao cừu, t&iacute;nh tiền cho bạn h&agrave;ng.</p>\r\n\r\n<p>Nghỉ tay, lau vội những giọt mồ h&ocirc;i tr&ecirc;n tr&aacute;n, &ocirc;ng kể: &ldquo;C&oacute; được trang trại ng&agrave;y h&ocirc;m nay l&agrave; cả một qu&aacute; tr&igrave;nh. Sau khi xuất ngũ, t&ocirc;i l&agrave;m đủ nghề nhưng thu nhập kh&ocirc;ng đ&aacute;ng kể. Sau nhiều lần thất bại, t&ocirc;i quyết t&acirc;m trở về mảnh đất Phước Sơn n&agrave;y để l&agrave;m gi&agrave;u&rdquo;.</p>\r\n\r\n<p>L&uacute;c đầu, &ocirc;ng Phong chỉ nu&ocirc;i g&agrave; theo hộ gia đ&igrave;nh. Về sau, nhận thấy m&ocirc; h&igrave;nh chăn nu&ocirc;i vừa cho thu nhập ổn định lại kh&ocirc;ng đầu tư nhiều vốn, kh&ocirc;ng tốn nhiều thời gian, &ocirc;ng quyết định ph&aacute;t triển đ&agrave;n g&agrave;.</p>\r\n\r\n<p>Với lợi thế c&oacute; rừng, đồng cỏ, c&ugrave;ng số tiền tiết kiệm, &ocirc;ng mạnh dạn vay th&ecirc;m vốn x&acirc;y dựng trang trại nu&ocirc;i d&ecirc;, cừu. Giờ đ&acirc;y, trang trại của &ocirc;ng rộng hơn 20.000m2, với 3.000 con g&agrave;, h&agrave;ng chục con cừu v&agrave; d&ecirc; c&ugrave;ng 10 hecta rừng. Mỗi năm, sau khi trừ c&aacute;c khoản chi ph&iacute;, &ocirc;ng thu về h&agrave;ng trăm triệu đồng.</p>\r\n\r\n<p>Cũng đa dạng c&aacute;c loại h&igrave;nh kinh tế, CCB Đinh Ngọc Dũng (th&ocirc;n Ph&uacute; Sơn 2) lại chọn m&ocirc; h&igrave;nh kết hợp vườn - ao - chuồng để ph&aacute;t triển kinh tế. Từ những năm th&aacute;ng vất vả l&agrave;m thu&ecirc;, cuốc mướn&hellip;, đến nay, sau hơn 20 năm g&acirc;y dựng trang trại, &ocirc;ng đ&atilde; c&oacute; hơn 5 hecta rừng, trang trại rộng gần 27.000m2 gồm hai hồ c&aacute; lớn kết hợp nu&ocirc;i vịt, ba khu nu&ocirc;i lợn, hai khu nu&ocirc;i g&agrave;.</p>\r\n\r\n<p>Từng l&agrave; người l&iacute;nh nhiều năm c&ocirc;ng t&aacute;c ở đảo C&ugrave; Lao Ch&agrave;m (Quảng Nam), những gian khổ, thiếu thốn &ocirc;ng đều đ&atilde; trải qua. V&igrave; thế, khi trở về qu&ecirc; hương, những kh&oacute; khăn kh&ocirc;ng hề l&agrave;m &ocirc;ng ch&ugrave;n bước. Đ&atilde; c&oacute; l&uacute;c bất lực nhưng rồi &ocirc;ng mạnh dạn vay vốn, học hỏi khoa học kỹ thuật, x&acirc;y dựng trang trại nu&ocirc;i c&aacute;, lợn, g&agrave;&hellip;</p>\r\n\r\n<p>&ldquo;Ng&agrave;y trước, quanh th&ocirc;n chủ yếu l&agrave;m kinh tế nhỏ, nghe chồng bảo đầu tư, ph&aacute;t triển kinh tế, t&ocirc;i lo lắm nhưng cũng ủng hộ. Trở về thời b&igrave;nh nhưng t&aacute;c phong thời chiến của &ocirc;ng vẫn giữ nguy&ecirc;n. &Ocirc;ng ấy nhanh nhẹn, ham học hỏi, sẵn s&agrave;ng đối mặt với kh&oacute; khăn, thử th&aacute;ch, l&agrave; tấm gương cho t&ocirc;i v&agrave; bốn con noi theo&rdquo;, vợ &ocirc;ng Dũng x&uacute;c động n&oacute;i.</p>\r\n\r\n<p>Ph&aacute;t huy phẩm chất &ldquo;Bộ đội Cụ Hồ&rdquo;, gắn phong tr&agrave;o &ldquo;CCB gương mẫu&rdquo; với phong tr&agrave;o &ldquo;CCB gi&uacute;p nhau giảm ngh&egrave;o, l&agrave;m kinh tế giỏi&rdquo;, c&aacute;c CCB x&atilde; H&ograve;a Khương kh&ocirc;ng chỉ vươn l&ecirc;n l&agrave;m kinh tế giỏi, thi đua x&acirc;y dựng c&aacute;c m&ocirc; h&igrave;nh m&agrave; c&ograve;n sẵn s&agrave;ng chia sẻ kinh nghiệm, tạo việc l&agrave;m v&agrave; hỗ trợ nguồn vốn, chuyển giao khoa học kỹ thuật, cải tạo vườn tạp, x&acirc;y dựng tổ hợp t&aacute;c v&agrave; thực hiện c&aacute;c phong tr&agrave;o &ldquo;Nghĩa t&igrave;nh đồng đội&rdquo;, &ldquo;X&oacute;a nh&agrave; tạm, nh&agrave; dột n&aacute;t cho hội vi&ecirc;n&rdquo;, &ldquo;Tư vấn gi&uacute;p nhau giảm ngh&egrave;o&rdquo;&hellip;</p>\r\n\r\n<p>Trong năm 2018, Hội CCB x&atilde; quản l&yacute; 3 tổ tiết kiệm vay vốn, t&iacute;n chấp cho 124 hộ vay, trong đ&oacute; c&oacute; 14 hộ l&agrave; CCB với số tiền 150 triệu đồng. Nhờ mạnh dạn đầu tư vốn, &aacute;p dụng c&aacute;c tiến bộ khoa học, kỹ thuật, chuyển đổi cơ cấu c&acirc;y trồng, con vật nu&ocirc;i, nhiều m&ocirc; h&igrave;nh kinh tế của c&aacute;c CCB ng&agrave;y c&agrave;ng khởi sắc. Năm 2018, Hội ph&aacute;t triển th&ecirc;m 2 m&ocirc; h&igrave;nh trang trại mới (m&ocirc; h&igrave;nh nấm kim chi v&agrave; m&ocirc; h&igrave;nh sản xuất gạch block), n&acirc;ng tổng số m&ocirc; h&igrave;nh của hội hiện c&oacute; l&ecirc;n con số 14.</p>\r\n\r\n<p>&Ocirc;ng Đặng Văn Quang, Chủ tịch Hội CCB x&atilde; H&ograve;a Khương cho biết, song song với nhiệm vụ ph&aacute;t triển kinh tế, gi&uacute;p nhau cải thiện cuộc sống, Hội đẩy mạnh thực hiện cuộc vận động &ldquo;To&agrave;n d&acirc;n đo&agrave;n kết x&acirc;y dựng đời sống văn h&oacute;a ở khu d&acirc;n cư&rdquo;, vận động hội vi&ecirc;n x&acirc;y dựng gia đ&igrave;nh CCB gương mẫu, phối hợp thực hiện phong tr&agrave;o to&agrave;n d&acirc;n bảo vệ an ninh Tổ quốc, ph&ograve;ng chống tội phạm, tệ nạn x&atilde; hội; nhận cảm h&oacute;a, gi&aacute;o dục thanh thiếu ni&ecirc;n hư h&ograve;a nhập cộng đồng...</p>\r\n\r\n<p>Đặc biệt, Hội ch&uacute; trọng c&ocirc;ng t&aacute;c &ldquo;truyền lửa&rdquo; cho thế hệ trẻ; tổ chức h&agrave;nh tr&igrave;nh về nguồn, thắp n&eacute;n tri &acirc;n v&agrave;o ng&agrave;y rằm v&agrave; m&ugrave;ng 1 hằng th&aacute;ng; tuy&ecirc;n truyền, gi&aacute;o dục truyền thống đấu tranh c&aacute;ch mạng của qu&ecirc; hương cho lớp trẻ. B&ecirc;n cạnh đ&oacute;, Hội tập hợp hội vi&ecirc;n, vận động gia đ&igrave;nh v&agrave; cộng đồng thường xuy&ecirc;n tham gia phong tr&agrave;o Ng&agrave;y chủ nhật xanh - sạch - đẹp, ra qu&acirc;n dọn vệ sinh đường l&agrave;ng, ng&otilde; x&oacute;m, nạo v&eacute;t k&ecirc;nh mương...</p>\r\n\r\n<p>&nbsp;&ldquo;C&aacute;c hội vi&ecirc;n&nbsp; đ&atilde; tập trung ph&aacute;t triển kinh tế bằng sức lực của m&igrave;nh v&agrave; sự năng nổ, nhạy b&eacute;n, học hỏi khoa học kỹ thuật. Với bản chất của anh &ldquo;Bộ đội cụ Hồ&rdquo;, c&aacute;c CCB lu&ocirc;n đi đầu, gương mẫu tr&ecirc;n mọi mặt để c&aacute;c thế hệ con ch&aacute;u noi theo&rdquo;, &ocirc;ng Đặng Văn Quang chia sẻ.</p>', 'Những năm qua, phong trào thi đua “Cựu chiến binh (CCB) gương mẫu” đã khơi dậy tinh thần sáng tạo, nghị lực vượt khó, phát huy truyền thống “Bộ đội Cụ Hồ” của các CCB ở xã Hòa Khương (huyện Hòa Vang).', '4', '4', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/Uqsj7h8RdBpXDvUYcYvmnX7JkWyUzXVJ7z8oWxDt.jpeg', 0, '2018-07-31 20:26:23', '2018-08-03 01:32:12');
INSERT INTO `post_lists` (`id`, `title`, `content`, `quotes_content`, `author_id`, `post_category_id`, `status`, `image_avatar`, `view`, `created_at`, `updated_at`) VALUES
(35, 'Lễ hội pháo hoa hấp dẫn, giàu cảm xúc', '<p>M&ugrave;a ph&aacute;o hoa năm nay được đ&aacute;nh gi&aacute; th&agrave;nh c&ocirc;ng rực rỡ, ghi dấu chặng đường 10 năm ph&aacute;o hoa Đ&agrave; Nẵng đi từ những bỡ ngỡ đến n&acirc;ng dần t&iacute;nh chuy&ecirc;n nghiệp trong kh&acirc;u tổ chức, trở th&agrave;nh lễ hội tr&igrave;nh diễn nghệ thuật vươn tầm quốc tế, thu h&uacute;t đ&ocirc;ng đảo du kh&aacute;ch trong v&agrave; ngo&agrave;i nước đến với th&agrave;nh phố Đ&agrave; Nẵng.</p>\r\n\r\n<p>Tham dự đ&ecirc;m chung kết DIFF 2018 c&oacute; Ph&oacute; Chủ tịch nước Đặng Thị Ngọc Thịnh; Chủ tịch Ủy ban Trung ương MTTQ Việt Nam Trần Thanh Mẫn; Bộ trưởng Bộ Văn h&oacute;a, Thể thao v&agrave; Du lịch Nguyễn Ngọc Thiện v&agrave; kh&aacute;ch quốc tế.</p>\r\n\r\n<p>Về ph&iacute;a l&atilde;nh đạo th&agrave;nh phố c&oacute; Ủy vi&ecirc;n Trung ương Đảng, B&iacute; thư Th&agrave;nh ủy, Trưởng đo&agrave;n Đại biểu Quốc hội th&agrave;nh phố Trương Quang Nghĩa; Ph&oacute; B&iacute; thư Thường trực Th&agrave;nh ủy V&otilde; C&ocirc;ng Tr&iacute;; Chủ tịch UBND th&agrave;nh phố Huỳnh Đức Thơ.</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Màn trình diễn của đội Marterello (Ý) trong đêm chung kết.    	   										     Ảnh: HẢI ĐĂNG\" src=\"http://baodanang.vn/dataimages/201807/original/images1453537_doi_y_1.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>M&agrave;n tr&igrave;nh diễn của đội Marterello (&Yacute;) trong đ&ecirc;m chung kết. Ảnh: HẢI ĐĂNG</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>Lắng đọng cảm x&uacute;c</strong></p>\r\n\r\n<p>Với chủ đề &ldquo;Nhịp cầu hữu nghị&rdquo;, đội Hoa Kỳ mở đầu đ&ecirc;m tr&igrave;nh diễn bằng giai điệu du dương kết hợp những b&ocirc;ng ph&aacute;o được bắn ra nhẹ nh&agrave;ng, uyển chuyển. M&agrave;n tr&igrave;nh diễn ph&aacute;o hoa của đội Mỹ khắc họa những c&acirc;y cầu mở lối ước mơ, kh&aacute;t vọng tốt đẹp của c&aacute;c d&acirc;n tộc t&igrave;m đến với nhau v&agrave; tạo n&ecirc;n sức mạnh gắn kết ngay giữa mảnh đất Đ&agrave; Nẵng y&ecirc;n b&igrave;nh v&agrave; mến kh&aacute;ch.</p>\r\n\r\n<p>Trong khi đ&oacute;, &ldquo;đương kim v&ocirc; địch&rdquo; &Yacute; lựa chọn bản giao hưởng v&ocirc; c&ugrave;ng l&atilde;ng mạn v&agrave; sống động để thể hiện những chuyến t&agrave;u chở ước mơ, sự lạc quan v&agrave; t&igrave;nh hữu nghị. Điểm chung của hai đội l&agrave; đều sử dụng những bản nhạc da diết, lắng đọng cảm x&uacute;c hơn so với những đ&ecirc;m thi trước m&agrave; họ thể hiện.</p>\r\n\r\n<p>Chia sẻ cảm x&uacute;c sau khi thưởng thức c&aacute;c phần tr&igrave;nh diễn, anh Kent Wright (du kh&aacute;ch Anh) cho rằng: &ldquo;M&agrave;n tr&igrave;nh diễn của đội &Yacute; g&acirc;y ấn tượng bởi vẻ đẹp l&atilde;ng mạn, tinh tế đặc trưng của xứ sở b&ecirc;n bờ Địa Trung Hải. Họ biết c&aacute;ch tiết chế mọi yếu tố &acirc;m thanh, &aacute;nh s&aacute;ng&hellip; ở mức vừa đủ để khơi gợi cảm x&uacute;c của kh&aacute;n giả, rồi &ldquo;chừa chỗ&rdquo; để kh&aacute;n giả thăng hoa. T&ocirc;i cũng đ&aacute;nh gi&aacute; cao phần tr&igrave;nh diễn ho&agrave;n hảo của đội Mỹ&rdquo;.</p>\r\n\r\n<p>Sau m&agrave;n tr&igrave;nh diễn ấn tượng, đội ph&aacute;o hoa Marterello (&Yacute;) đ&atilde; bảo vệ th&agrave;nh c&ocirc;ng chức v&ocirc; địch (DIFF 2017) để bước l&ecirc;n bục cao nhất tại DIFF 2018 với phần thưởng l&agrave; C&uacute;p v&ocirc; địch v&agrave; 10.000 USD. &Ocirc;ng Ermes Martarello, Đội trưởng đội ph&aacute;o hoa Marterello (&Yacute;)&nbsp;t&acirc;m sự, bản th&acirc;n &ocirc;ng c&ugrave;ng to&agrave;n đội rất x&uacute;c động v&agrave; sung sướng khi một lần nữa được bước l&ecirc;n ng&ocirc;i đầu tại DIFF 2018.</p>\r\n\r\n<p>&ldquo;T&ocirc;i đ&atilde; c&oacute; những ng&agrave;y thật vui với nhiều trải nghiệm tuyệt vời tại th&agrave;nh phố Đ&agrave; Nẵng xinh đẹp. Nếu c&oacute; cơ hội, t&ocirc;i hy vọng c&oacute; thể quay trở lại v&agrave; gặp c&aacute;c bạn v&agrave;o năm sau&rdquo;, &ocirc;ng Ermes Martarello n&oacute;i.</p>\r\n\r\n<p>Ph&aacute;t biểu tại đ&ecirc;m chung kết, Ph&oacute; Chủ tịch UBND th&agrave;nh phố Trần Văn Mi&ecirc;n, Trưởng Ban tổ chức DIFF 2018 khẳng định: &ldquo;DIFF 2018 đ&atilde; th&agrave;nh c&ocirc;ng tốt đẹp khi cống hiến cho kh&aacute;n giả những m&agrave;n tr&igrave;nh diễn quyến rũ, ho&agrave;nh tr&aacute;ng của 8 đội thi đến từ c&aacute;c quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ: Ba Lan, Ph&aacute;p, Mỹ, &Yacute;, Hồng K&ocirc;ng - Trung Quốc, Thụy Điển, Bồ Đ&agrave;o Nha v&agrave; đội chủ nh&agrave; Đ&agrave; Nẵng -Việt Nam.</p>\r\n\r\n<p>B&ecirc;n cạnh ph&aacute;o hoa, những chương tr&igrave;nh nghệ thuật đặc sắc quy tụ c&aacute;c ng&ocirc;i sao h&agrave;ng đầu c&ugrave;ng những hoạt động đồng h&agrave;nh s&ocirc;i nổi, đầy sắc m&agrave;u đ&atilde; đem đến cho nh&acirc;n d&acirc;n th&agrave;nh phố v&agrave; du kh&aacute;ch một kh&ocirc;ng kh&iacute; lễ hội s&ocirc;i động, n&aacute;o nhiệt suốt hai th&aacute;ng qua; g&oacute;p phần khẳng định hơn nữa thương hiệu sự kiện của th&agrave;nh phố b&ecirc;n bờ s&ocirc;ng H&agrave;n thơ mộng&rdquo;.</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Nhà vô địch DIFF 2018, đội pháo hoa Marterello (Ý).\" src=\"http://baodanang.vn/dataimages/201807/original/images1453536_Italia.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nh&agrave; v&ocirc; địch DIFF 2018, đội ph&aacute;o hoa Marterello (&Yacute;).</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>Nơi hội tụ đa sắc m&agrave;u văn h&oacute;a</strong></p>\r\n\r\n<p>Qua 10 năm với 9 lần tổ chức, DIFF đ&atilde; quy tụ h&agrave;ng chục đội ph&aacute;o hoa đến từ nhiều quốc gia v&agrave; nền văn h&oacute;a tr&ecirc;n thế giới. Từ những ng&agrave;y đầu với chỉ 4 đội tham gia, đến nay, DIFF đ&atilde; c&oacute; 8 đội tham gia với những m&agrave;n tranh t&agrave;i kịch t&iacute;nh.</p>\r\n\r\n<p>Với 10 năm gắn b&oacute; c&ugrave;ng ph&aacute;o hoa Đ&agrave; Nẵng, b&agrave; Nadiah Wong Abdullah, Gi&aacute;m đốc điều h&agrave;nh C&ocirc;ng ty Global 2000, chia sẻ:</p>\r\n\r\n<p>&ldquo;Đ&oacute; l&agrave; một h&agrave;nh tr&igrave;nh d&agrave;i với những ng&agrave;y đầu ti&ecirc;n c&ograve;n nhiều kh&oacute; khăn v&agrave; h&agrave;ng loạt vấn đề cần được khắc phục. Qua mỗi năm, DIFC trước đ&acirc;y v&agrave; DIFF 2017, 2018 ng&agrave;y c&agrave;ng trưởng th&agrave;nh để vươn m&igrave;nh l&ecirc;n tầm lễ hội tr&igrave;nh diễn nghệ thuật ph&aacute;o hoa hấp dẫn v&agrave; độc đ&aacute;o.</p>\r\n\r\n<p>Mỗi đội ph&aacute;o hoa m&agrave; ch&uacute;ng t&ocirc;i mời tới tham dự đều được lựa chọn kỹ lưỡng với bảng th&agrave;nh t&iacute;ch ấn tượng tr&ecirc;n khắp thế giới. Từ ch&acirc;u &Aacute; như Hồng K&ocirc;ng (Trung Quốc), Malaysia&hellip; đến ch&acirc;u &Acirc;u như: Ph&aacute;p, Thụy Điển, Canada, Bồ Đ&agrave;o Nha&hellip; v&agrave; cả ch&acirc;u Mỹ như đội Hoa Kỳ.</p>\r\n\r\n<p>Suốt 10 năm qua, cứ đến dịp h&egrave;, cả th&agrave;nh phố Đ&agrave; Nẵng lại h&aacute;o hức đ&oacute;n đợi cuộc thi ph&aacute;o hoa với sự hội tụ của những anh t&agrave;i nổi tiếng trong ng&agrave;nh c&ocirc;ng nghiệp ph&aacute;o hoa thế giới&rdquo;.</p>\r\n\r\n<p>Năm 2017, DIFF kho&aacute;c l&ecirc;n m&igrave;nh tấm &aacute;o mới với việc chuyển sang h&igrave;nh thức tổ chức bằng x&atilde; hội h&oacute;a khi C&ocirc;ng ty CP Tập đo&agrave;n Mặt Trời (Sun Group) nhận đăng cai đầu tư, tổ chức. Qua hai năm 2017 v&agrave; 2018, DIFF được chuẩn bị chu đ&aacute;o v&agrave; chuy&ecirc;n nghiệp hơn.</p>\r\n\r\n<p>C&ugrave;ng với sự kiện ph&aacute;o hoa, th&agrave;nh phố v&agrave; Tập đo&agrave;n Sun Group đ&atilde; tổ chức nhiều hoạt động hấp dẫn như: carnaval diễu h&agrave;nh xe hoa, ng&agrave;y hội ẩm thực ở C&ocirc;ng vi&ecirc;n Ch&acirc;u &Aacute;, chương tr&igrave;nh &ldquo;Đ&agrave; Nẵng &ndash; Điểm hẹn m&ugrave;a h&egrave;&rdquo;, &ldquo;M&ugrave;a du lịch biển&rdquo;&hellip;, g&oacute;p phần tạo kh&ocirc;ng kh&iacute; lễ hội trải d&agrave;i hơn 2 th&aacute;ng tr&ecirc;n khắp phố phường. Trong sự ph&aacute;t triển nhanh ch&oacute;ng của du lịch Đ&agrave; Nẵng, DIFF đ&atilde; trở th&agrave;nh sản phẩm độc đ&aacute;o, l&agrave; điểm nhấn ấn tượng đối với du kh&aacute;ch trong v&agrave; ngo&agrave;i nước mỗi khi nhắc tới th&agrave;nh phố b&ecirc;n bờ s&ocirc;ng H&agrave;n thơ mộng n&agrave;y.</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Khán đài chật kín khán giả trong đêm chung kết DIFF 2018. Ảnh: KHÁNH HÒA\" src=\"http://baodanang.vn/dataimages/201807/original/images1453535_Khan_gia.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kh&aacute;n đ&agrave;i chật k&iacute;n kh&aacute;n giả trong đ&ecirc;m chung kết DIFF 2018. Ảnh: KH&Aacute;NH H&Ograve;A</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>&ldquo;Th&agrave;nh phố ph&aacute;o hoa&rdquo; mới của ch&acirc;u &Aacute;</strong></p>\r\n\r\n<p>Theo &ocirc;ng Trần Ch&iacute; Cường, Ph&oacute; Gi&aacute;m đốc Sở Du lịch th&agrave;nh phố, trong hai th&aacute;ng diễn ra DIFF 2018, lượng kh&aacute;ch du lịch đến Đ&agrave; Nẵng đ&atilde; c&aacute;n mốc hơn 1,581 triệu lượt, tăng 25,54% so với c&ugrave;ng kỳ năm 2017.</p>\r\n\r\n<p>Lượng kh&aacute;ch tăng cao, c&ocirc;ng suất ph&ograve;ng kh&aacute;ch sạn trong thời gian diễn ra lễ hội lu&ocirc;n đạt từ 70-90%, d&ugrave; Đ&agrave; Nẵng đ&atilde; c&oacute; th&ecirc;m 7.355 ph&ograve;ng kh&aacute;ch sạn phục vụ du kh&aacute;ch so với c&ugrave;ng kỳ năm 2017. Nhờ nỗ lực của c&aacute;c cơ quan, đơn vị tr&ecirc;n địa b&agrave;n th&agrave;nh phố, đặc biệt l&agrave; đơn vị thực hiện - Tập đo&agrave;n Sun Group, c&ocirc;ng t&aacute;c tổ chức DIFF 2018 đ&atilde; được tiến h&agrave;nh chuy&ecirc;n nghiệp, bảo đảm an ninh, an to&agrave;n v&agrave; vệ sinh m&ocirc;i trường.</p>\r\n\r\n<p>G&oacute;p phần l&agrave;m n&ecirc;n th&agrave;nh c&ocirc;ng của DIFF 2018 l&agrave; sự nỗ lực của gần 4.300 c&aacute;n bộ, nh&acirc;n vi&ecirc;n của c&aacute;c sở, ban, ng&agrave;nh th&agrave;nh phố, chiến sĩ an ninh, c&ocirc;ng an nh&acirc;n nh&acirc;n d&acirc;n, lực lượng ph&ograve;ng ch&aacute;y chữa ch&aacute;y, bộ đội bi&ecirc;n ph&ograve;ng, b&aacute;c sĩ, nh&acirc;n vi&ecirc;n ng&agrave;nh viễn th&ocirc;ng, ng&agrave;nh điện, lực lượng d&acirc;n ph&ograve;ng, nghệ sĩ, diễn vi&ecirc;n v&agrave; c&aacute;n bộ, nh&acirc;n vi&ecirc;n của Tập đo&agrave;n Sun Group c&ugrave;ng h&agrave;ng trăm nh&acirc;n vi&ecirc;n c&aacute;c nh&agrave; thầu.</p>\r\n\r\n<p>Nhận định về DIFF 2018, &ocirc;ng Huỳnh Văn H&ugrave;ng, Gi&aacute;m đốc Sở Văn h&oacute;a - Thể thao th&agrave;nh phố n&oacute;i: &ldquo;Đ&acirc;y l&agrave; sự kiện văn h&oacute;a, giải tr&iacute;, du lịch đặc sắc, g&oacute;p phần đ&aacute;ng kể trong việc thay đổi bộ mặt của du lịch Đ&agrave; Nẵng.</p>\r\n\r\n<p>Với quy m&ocirc; tổ chức ng&agrave;y c&agrave;ng chuy&ecirc;n nghiệp, đẳng cấp của DIFF, ch&uacute;ng t&ocirc;i hy vọng Đ&agrave; Nẵng sẽ x&uacute;c tiến nhanh c&aacute;c thủ tục cần thiết để sớm c&oacute; khu vực tr&igrave;nh diễn ph&aacute;o hoa ri&ecirc;ng với kh&aacute;n đ&agrave;i lớn hơn, đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng cao của du kh&aacute;ch trong những năm tới, cũng như tạo kh&ocirc;ng gian đủ rộng v&agrave; an to&agrave;n tuyệt đối để c&aacute;c đội ph&aacute;o hoa thỏa sức s&aacute;ng tạo, cống hiến những m&agrave;n tr&igrave;nh diễn ngoạn mục hơn nữa cho kh&aacute;n giả&rdquo;.</p>\r\n\r\n<p>&ldquo;Với t&acirc;m huyết được đ&oacute;ng g&oacute;p cho sự ph&aacute;t triển của kinh tế - x&atilde; hội Đ&agrave; Nẵng n&oacute;i chung v&agrave; du lịch Đ&agrave; Nẵng n&oacute;i ri&ecirc;ng, ch&uacute;ng t&ocirc;i đ&atilde; v&agrave; sẽ tiếp tục chung tay c&ugrave;ng với l&atilde;nh đạo v&agrave; người d&acirc;n th&agrave;nh phố để đưa Đ&agrave; Nẵng trở th&agrave;nh &ldquo;Th&agrave;nh phố ph&aacute;o hoa mới của ch&acirc;u &Aacute;&rdquo; trong kế hoạch d&agrave;i hạn của DIFF&rdquo;, &ocirc;ng Đặng Minh Trường, Ph&oacute; Chủ tịch HĐQT, Tổng Gi&aacute;m đốc Sun Group cho biết. &nbsp;</p>', '* Đội pháo hoa Marterello (Ý) bảo vệ thành công chức vô địch\r\n\r\nKéo dài suốt 2 tháng (từ 30-4 đến 30-6), tối 30-6, Lễ hội pháo hoa quốc tế Đà Nẵng (DIFF) 2018 đã khép lại sau phần tranh tài mãn nhãn giữa hai đội Atlas PyroVision (Hoa Kỳ) và Marterello (Ý).', '4', '13', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/sUEo6xfhazj4uUuK1exJbn76um9YVB55l4LpaSq4.jpeg', 1, '2018-08-02 02:15:11', '2018-08-03 04:33:08'),
(36, 'Mũi Isabelle ở Đà Nẵng dưới khía cạnh lịch sử và văn hóa', '<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Mũi Isabelle (Hòn Hành) nhìn từ bờ tây-nam vịnh Nam Chơn hiện nay. Ảnh: T.T\" src=\"http://baodanang.vn/dataimages/201806/original/images1449619_Trung_tien_6.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Mũi Isabelle (H&ograve;n H&agrave;nh) nh&igrave;n từ bờ t&acirc;y-nam vịnh Nam Chơn hiện nay. Ảnh: T.T</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>H&ograve;n H&agrave;nh trước thế kỷ XIX c&oacute; t&ecirc;n l&agrave; n&uacute;i Th&ocirc;ng (Th&ocirc;ng sơn: 葱山), tục gọi H&ograve;n H&agrave;nh (H&ograve;n H&agrave;nh: 㞩行), nguy&ecirc;n văn l&agrave; &ldquo;Th&ocirc;ng sơn tục danh H&ograve;n H&agrave;nh&rdquo;: 葱山俗名㞩行. Năm 1823, vua Minh Mạng triều Nguyễn đổi t&ecirc;n n&uacute;i Th&ocirc;ng th&agrave;nh n&uacute;i Định Hải (Định Hải sơn: 定海山), x&acirc;y ph&aacute;o đ&agrave;i ở đ&oacute; gọi l&agrave; ph&aacute;o đ&agrave;i Định Hải (Quốc sử qu&aacute;n triều Nguyễn, Đại Nam nhất thống ch&iacute;,&nbsp;Tu Trai Nguyễn Tạo dịch, Nha Văn h&oacute;a Bộ Quốc gia gi&aacute;o dục xuất bản, quyển 5, S&agrave;i G&ograve;n, 1962, phần H&aacute;n-N&ocirc;m, mục Xuy&ecirc;n Sơn, trang 17).</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Vị trí pháo đài Định Hải dưới tên gọi pháo đài Núm vú (Fort du Mamelon) trên bản đồ chiến sự Pháp ngày 18-11-1859. (Nguồn: Colonel Henri de Ponchalon, sách đã dẫn, trang 217)\" src=\"http://baodanang.vn/dataimages/201806/original/images1449620_Trung_tien_2.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vị tr&iacute; ph&aacute;o đ&agrave;i Định Hải dưới t&ecirc;n gọi ph&aacute;o đ&agrave;i N&uacute;m v&uacute; (Fort du Mamelon) tr&ecirc;n bản đồ chiến sự Ph&aacute;p ng&agrave;y 18-11-1859. (Nguồn: Colonel Henri de Ponchalon, s&aacute;ch đ&atilde; dẫn, trang 217)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong cuộc chiến tranh x&acirc;m lược ở Đ&agrave; Nẵng (1858-1860), do bế tắc trước sức chống trả ki&ecirc;n cường của qu&acirc;n v&agrave; d&acirc;n Việt Nam quanh khu vực hạ lưu s&ocirc;ng H&agrave;n, ng&agrave;y 18-11-1859, li&ecirc;n qu&acirc;n Ph&aacute;p-T&acirc;y Ban Nha quyết định mở cuộc tấn c&ocirc;ng ph&aacute;o đ&agrave;i Định Hải (nằm tr&ecirc;n H&ograve;n H&agrave;nh) v&agrave; đồn Chơn Sảng nằm b&ecirc;n bờ vịnh Nam Chơn (gần ph&aacute;o đ&agrave;i Định Hải) h&ograve;ng l&agrave;m chủ con đường đ&egrave;o Hải V&acirc;n v&agrave; đ&aacute;nh chiếm Hải V&acirc;n quan để chặn đường li&ecirc;n lạc giữa Đ&agrave; Nẵng với Kinh đ&ocirc; Huế.</p>\r\n\r\n<p>Từ 6 giờ s&aacute;ng, hạm đội 8 chiếc gồm hai ph&aacute;o hạm Avalanche v&agrave; Alarme, so&aacute;i hạm N&eacute;m&eacute;sis, t&agrave;u T&acirc;y Ban Nha Jorgo-Juan, chiến hạm hơi nước Phl&eacute;g&eacute;ton, hai t&agrave;u lai dắt Pr&eacute;gent v&agrave; Norzagarai, t&agrave;u vận tải Marne rời vụng t&agrave;u dưới ch&acirc;n b&aacute;n đảo Sơn Tr&agrave; tiến về ph&iacute;a t&acirc;y-bắc vịnh Đ&agrave; Nẵng. Cuộc h&agrave;nh qu&acirc;n bị chậm lại bởi cột buồm ch&iacute;nh của t&agrave;u Pr&eacute;gent gặp sự cố g&atilde;y vỡ.</p>\r\n\r\n<p>Li&ecirc;n qu&acirc;n Ph&aacute;p-T&acirc;y Ban Nha chia l&agrave;m ba c&aacute;nh: c&aacute;nh của Thiếu t&aacute; Palli&egrave;res gồm 1 ph&acirc;n đội c&ocirc;ng binh, 1 đại đội ph&aacute;o binh v&agrave; 6 đại đội thủy qu&acirc;n lục chiến Ph&aacute;p c&oacute; nhiệm vụ chiếm đồn Chơn Sảng nằm tr&ecirc;n đường ngựa trạm; c&aacute;nh l&iacute;nh T&acirc;y Ban Nha c&oacute; nhiệm vụ bắn sập ph&aacute;o đ&agrave;i Định Hải x&acirc;y bằng đ&aacute; nằm tr&ecirc;n H&ograve;n H&agrave;nh v&agrave; khống chế vịnh Nam Chơn c&ugrave;ng con đường ngựa trạm; c&aacute;nh dự bị của Trung t&aacute; Reybaud gồm 5 đại đội thủy qu&acirc;n lục chiến Ph&aacute;p v&agrave; 2 đại đội l&iacute;nh T&acirc;y Ban Nha d&agrave;n qu&acirc;n tr&ecirc;n vịnh cho đến khi kết th&uacute;c cuộc tấn c&ocirc;ng, với mục ti&ecirc;u l&agrave; chiếm khu dịch trạm Nam Chơn nằm b&ecirc;n bờ biển ph&iacute;a trước đồn ch&iacute;nh Chơn Sảng.</p>\r\n\r\n<table align=\"center\" style=\"width:400px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Vị trí pháo đài Định Hải dưới tên gọi Fort Isabelle II trên bản đồ chiến sự Pháp năm 1860 (Thiết lập tiếng Pháp từ bản đồ vịnh Đà Nẵng trên báo “Le Monde Illustré” ra ngày 12-5-1860 tại Paris, Pháp,  trang 312-313).\" src=\"http://baodanang.vn/dataimages/201806/original/images1449621_Trung_tien_3.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vị tr&iacute; ph&aacute;o đ&agrave;i Định Hải dưới t&ecirc;n gọi Fort Isabelle II tr&ecirc;n bản đồ chiến sự Ph&aacute;p năm 1860 (Thiết lập tiếng Ph&aacute;p từ bản đồ vịnh Đ&agrave; Nẵng tr&ecirc;n b&aacute;o &ldquo;Le Monde Illustr&eacute;&rdquo; ra ng&agrave;y 12-5-1860 tại Paris, Ph&aacute;p, trang 312-313).</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trước giờ nổ s&uacute;ng, l&iacute;nh tấn c&ocirc;ng của Ph&aacute;p-T&acirc;y Ban Nha chuyển xuống những chiếc s&agrave; l&uacute;p chuẩn bị để sẵn s&agrave;ng đổ bộ, c&ograve;n lực lượng dự bị ở lại tr&ecirc;n t&agrave;u vận tải Marne. L&uacute;c n&agrave;y qu&acirc;n Ph&aacute;p gọi ph&aacute;o đ&agrave;i Định Hải bằng thuật ngữ ph&aacute;o đ&agrave;i N&uacute;m v&uacute;: Fort du Mamelon (Theo Colonel Henri de Ponchalon, Indo-Chine, souvenirs de voyage et de campagne, 1858-1860, Maison A. Mame et Fils &agrave; Tours, Paris, 1896, trang 215).</p>\r\n\r\n<p>V&agrave;o khoảng 9 giờ, t&agrave;u Ph&aacute;p v&agrave; T&acirc;y Ban Nha bắt đầu khai hỏa cấp tập. Qu&acirc;n nh&agrave; Nguyễn tr&ecirc;n ph&aacute;o đ&agrave;i Định Hải nổ s&uacute;ng phản ph&aacute;o quyết liệt, nhiều quả đại b&aacute;c bay sượt qua đầu c&aacute;c t&agrave;u chiến giặc. Đồn ch&iacute;nh Chơn Sảng cũng nổ s&uacute;ng chống trả, nhưng mới bắn được 4 ph&aacute;t đại b&aacute;c th&igrave; bị tr&uacute;ng đạn tr&aacute;i ph&aacute; từ t&agrave;u T&acirc;y Ban Nha Jorgo-Juan khiến tiếng đại b&aacute;c ở đ&acirc;y giảm dần rồi im bặt.</p>\r\n\r\n<p>Song, quả đại b&aacute;c thứ ba từ đồn ch&iacute;nh Chơn Sảng &ldquo;đ&atilde; cắt đứt đ&ocirc;i th&acirc;n m&igrave;nh Thiếu t&aacute; c&ocirc;ng binh D&eacute;roul&egrave;de đang đứng cạnh Chuẩn Đ&ocirc; đốc Page tr&ecirc;n khoang thượng ph&iacute;a đu&ocirc;i so&aacute;i hạm N&eacute;m&eacute;sis, l&agrave;m Page bị lấm m&aacute;u khắp người; quả đạn đ&oacute; c&ograve;n bay tiếp giết chết th&ecirc;m một hạ sĩ thủy thủ đ&agrave;i chỉ huy, cắt đứt d&acirc;y n&eacute;o cột buồm l&aacute;i v&agrave; l&agrave;m bị thương hai chuẩn &uacute;y hải qu&acirc;n c&ugrave;ng một số thuỷ thủ kh&aacute;c&rdquo; (Theo Colonel Henri de Ponchalon, s&aacute;ch đ&atilde; dẫn, trang 216).</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Vị trí pháo đài Định Hải trên bản đồ của quân Nguyễn năm 1859 (Thiết lập thêm tiếng Việt từ bản đồ chiến sự Đà Nẵng theo nguồn: https://vi.wikipedia.org/wiki/(1858-1859)/media/File:Carte_de_Tourane_1859.jpg)\" src=\"http://baodanang.vn/dataimages/201806/original/images1449622_Trung_tien_1.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vị tr&iacute; ph&aacute;o đ&agrave;i Định Hải tr&ecirc;n bản đồ của qu&acirc;n Nguyễn năm 1859 (Thiết lập th&ecirc;m tiếng Việt từ bản đồ chiến sự Đ&agrave; Nẵng theo nguồn: https://vi.wikipedia.org/wiki/(1858-1859)/media/File:Carte_de_Tourane_1859.jpg)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ụ s&uacute;ng bắn đ&aacute; của đồn Chơn Sảng cũng tham gia chiến đấu, nhưng bắn trả kh&ocirc;ng nhiều bởi những quả đạn tr&aacute;i ph&aacute; từ c&aacute;c t&agrave;u giặc l&agrave;m ch&aacute;y b&ugrave;ng đồn Chơn Sảng v&agrave; tan n&aacute;t ph&aacute;o đ&agrave;i Định Hải; những kho thuốc s&uacute;ng nổ tung l&ecirc;n, khiến qu&acirc;n nh&agrave; Nguyễn do Ph&oacute; vệ &uacute;y L&ecirc; H&ograve;a, Th&agrave;nh thủ &uacute;y T&ocirc;n Thất Mưu chỉ huy phải bỏ đồn v&agrave; ph&aacute;o đ&agrave;i r&uacute;t qu&acirc;n để bảo to&agrave;n lực lượng.</p>\r\n\r\n<p>Qu&acirc;n Ph&aacute;p-T&acirc;y Ban Nha d&ugrave;ng s&agrave; l&uacute;p đổ bộ v&agrave;o bờ, nhưng kh&ocirc;ng bắt gặp ai tại đồn ch&iacute;nh v&agrave; ụ s&uacute;ng bắn đ&aacute; của đồn Chơn Sảng, thu được 3 đại b&aacute;c d&ugrave;ng cho thủy qu&acirc;n cỡ n&ograve;ng 12cm v&agrave; v&agrave;i s&uacute;ng bắn đ&aacute;. C&ograve;n tại ph&aacute;o đ&agrave;i Định Hải, qu&acirc;n T&acirc;y Ban Nha bắt sống 3 l&iacute;nh nh&agrave; Nguyễn, chiếm được 5 khẩu đại b&aacute;c cỡ n&ograve;ng 24cm bằng sắt đ&atilde; hơi hoen rỉ. Khu l&agrave;ng Chơn Sảng c&ograve;n nguy&ecirc;n vẹn nhưng liền bị li&ecirc;n qu&acirc;n đốt rụi.</p>\r\n\r\n<p>V&agrave;o giữa trưa, những to&aacute;n l&iacute;nh Ph&aacute;p kh&aacute;c tiến sang chiếm đ&oacute;ng dịch trạm Nam Chơn, l&iacute;nh T&acirc;y Ban Nha giữ ph&aacute;o đ&agrave;i Định Hải, c&ograve;n c&aacute;nh qu&acirc;n dự bị quay lại Đ&agrave; Nẵng.</p>\r\n\r\n<p>Trong thời gian chiếm đ&oacute;ng ph&aacute;o đ&agrave;i Định Hải, l&iacute;nh T&acirc;y Ban Nha kh&ocirc;ng d&ugrave;ng t&ecirc;n ph&aacute;o đ&agrave;i N&uacute;m v&uacute; do người Ph&aacute;p gọi trước đ&oacute;, m&agrave; lấy t&ecirc;n nữ ho&agrave;ng đương triều của m&igrave;nh l&agrave; Isabelle Đệ nhị, tức Isabelle II nếu viết theo tiếng Ph&aacute;p, c&ograve;n tiếng T&acirc;y Ban Nha l&agrave; Isabel II (1830-1904, t&ecirc;n đầy đủ: Mar&iacute;a Isabel Luisa de Borb&oacute;n y Borb&oacute;n-Dos Sicilias) để đặt t&ecirc;n nơi đ&acirc;y th&agrave;nh ph&aacute;o đ&agrave;i Isabelle II (Theo Colonel Henri de Ponchalon, s&aacute;ch đ&atilde; dẫn, trang 231). T&ecirc;n ph&aacute;o đ&agrave;i Isabelle II c&ograve;n được đưa l&ecirc;n bản đồ qu&acirc;n sự từ thời điểm n&agrave;y.</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Tranh pháo đài Định Hải bị liên quân Pháp-Tây Ban Nha tấn công ngày 18-11-1859. (Nguồn: Báo “L’Illustration, Journal Universel” ra ngày 21-1-1860 tại Paris, Pháp, trang 41)\" src=\"http://baodanang.vn/dataimages/201806/original/images1449623_Trung_tien_4.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tranh ph&aacute;o đ&agrave;i Định Hải bị li&ecirc;n qu&acirc;n Ph&aacute;p-T&acirc;y Ban Nha tấn c&ocirc;ng ng&agrave;y 18-11-1859. (Nguồn: B&aacute;o &ldquo;L&rsquo;Illustration, Journal Universel&rdquo; ra ng&agrave;y 21-1-1860 tại Paris, Ph&aacute;p, trang 41)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Sau hơn ba th&aacute;ng kể từ ng&agrave;y chiếm đ&oacute;ng nhưng vẫn bế tắc v&igrave; lu&ocirc;n thất bại trong nỗ lực đ&aacute;nh chiếm đường đ&egrave;o Hải V&acirc;n, v&agrave;o 7 giờ s&aacute;ng 29-2-1860, li&ecirc;n qu&acirc;n Ph&aacute;p-T&acirc;y Ban Nha đ&aacute;nh m&igrave;n giật sập ph&aacute;o đ&agrave;i Định Hải, đồn Chơn Sảng v&agrave; dịch trạm Nam Chơn rồi r&uacute;t về những tiền đồn ở ch&acirc;n n&uacute;i Sơn Tr&agrave;, trước khi di tản to&agrave;n bộ khỏi Đ&agrave; Nẵng v&agrave;o 23-3-1860.</p>\r\n\r\n<p>Như vậy, tại H&ograve;n H&agrave;nh, từ t&ecirc;n ph&aacute;o đ&agrave;i Định Hải, trước khi chiếm đ&oacute;ng qu&acirc;n Ph&aacute;p gọi l&agrave; ph&aacute;o đ&agrave;i N&uacute;m v&uacute;, song qu&acirc;n T&acirc;y Ban Nha đổi t&ecirc;n lại l&agrave; ph&aacute;o đ&agrave;i Isabelle II sau khi chiếm giữ.</p>\r\n\r\n<p>Trải qua thời gian d&agrave;i, người Việt đ&atilde; lược giản t&ecirc;n Nữ ho&agrave;ng Isabelle II th&agrave;nh Isabelle theo quy luật giản &acirc;m của ng&ocirc;n ngữ, tạo th&agrave;nh th&oacute;i quen cho đến ng&agrave;y nay, để c&ugrave;ng với nghĩa địa Y Pha Nho l&agrave; hai địa danh để lại dấu t&iacute;ch ng&ocirc;n ngữ T&acirc;y Ban Nha trong cuộc chiến m&agrave; họ li&ecirc;n qu&acirc;n với Ph&aacute;p x&acirc;m lược Đ&agrave; Nẵng 160 năm trước.</p>\r\n\r\n<p>T&ecirc;n mũi Isabelle hiện nay kh&ocirc;ng c&ograve;n thể hiện nội h&agrave;m nguy&ecirc;n nghĩa, nhưng tri thức về nguồn gốc t&ecirc;n gọi Isabelle vẫn rất cần thiết được phổ biến tr&ecirc;n b&aacute;o ch&iacute;, s&aacute;ch vở v&agrave; trong đời sống x&atilde; hội; bởi nếu kh&ocirc;ng nắm r&otilde; những gi&aacute; trị lịch sử v&agrave; văn h&oacute;a k&egrave;m theo, địa danh chỉ c&ograve;n l&agrave; sự trống rỗng, v&ocirc; hồn v&agrave; xa lạ với x&atilde; hội hiện đại.</p>', 'Hiện nay, trong văn bản hành chính các cấp từ Trung ương đến địa phương hay trên các website du lịch và cả trong ngôn ngữ thường nhật của người Đà Nẵng, mũi đất phía đông-bắc vịnh Nam Chơn gắn liền địa danh Hòn Hành được đồng nhất với tên gọi mũi Isabelle. Tuy nhiên, nếu đặt câu hỏi vì sao địa danh này mang tên là mũi Isabelle, chắc hẳn không phải ai cũng có thể đưa ra câu trả lời thỏa đáng.', '4', '13', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/QGNWA1TcfEozqeFAvec1WTakQDC9r4Z91feb6nhm.jpeg', 0, '2018-08-02 02:18:19', '2018-08-03 01:32:31'),
(37, 'Choáng ngợp với vẻ đẹp lung linh, huyền ảo của đèo Hải Vân', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Vịnh Lăng Cô nằm dưới chân đèo Hải Vân thuộc địa phận tỉnh Thừa Thiên-Huế. (Ảnh: Hồ Cầu/TTXVN)\" src=\"http://baodanang.vn/dataimages/201706/original/images1372963_ttxvn_haivan1.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vịnh Lăng C&ocirc; nằm dưới ch&acirc;n đ&egrave;o Hải V&acirc;n thuộc địa phận tỉnh Thừa Thi&ecirc;n-Huế. (Ảnh: Hồ Cầu/TTXVN)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Di tích quốc gia Hải Vân Quan nằm trên đỉnh đèo Hải Vân. (Ảnh: Hồ Cầu/TTXVN)\" src=\"http://baodanang.vn/dataimages/201706/original/images1372965_ttxvn_haivan2.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Di t&iacute;ch quốc gia Hải V&acirc;n Quan nằm tr&ecirc;n đỉnh đ&egrave;o Hải V&acirc;n. (Ảnh: Hồ Cầu/TTXVN)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Khúc cua chữ C trên đèo Hải Vân thuộc địa phận Đà Nẵng. (Ảnh: Hồ Cầu/TTXVN)\" src=\"http://baodanang.vn/dataimages/201706/original/images1372967_ttxvn_haivan3.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kh&uacute;c cua chữ C tr&ecirc;n đ&egrave;o Hải V&acirc;n thuộc địa phận Đ&agrave; Nẵng. (Ảnh: Hồ Cầu/TTXVN)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"4 / 9  Vịnh Đà Nẵng nằm dưới chân đèo Hải Vân. (Ảnh: Hồ Cầu/TTXVN)\" src=\"http://baodanang.vn/dataimages/201706/original/images1372968_ttxvn_haivan4.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vịnh Đ&agrave; Nẵng nằm dưới ch&acirc;n đ&egrave;o Hải V&acirc;n. (Ảnh: Hồ Cầu/TTXVN)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Cung đường uốn lượn trên đèo Hải Vân thuộc địa phận tỉnh Thừa Thiên-Huế nhìn từ Hải Vân Quan. (Ảnh: Hồ Cầu/TTXVN)\" src=\"http://baodanang.vn/dataimages/201706/original/images1372971_ttxvn_haivan5.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cung đường uốn lượn tr&ecirc;n đ&egrave;o Hải V&acirc;n thuộc địa phận tỉnh Thừa Thi&ecirc;n-Huế nh&igrave;n từ Hải V&acirc;n Quan. (Ảnh: Hồ Cầu/TTXVN)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'Đèo Hải Vân nằm giữa tỉnh Thừa Thiên-Huế và Thành phố Đà Nẵng với chiều dài 21km, đỉnh đèo nằm ở độ cao 496m so với mực nước biển.', '4', '13', '2', 'http://localhost/theDaNangNews/local/storage/app/sihao91@gmail.com/Z6AYdD8aCN4FMRYruQBVRnVoTQVcPEK6g6GQEEEJ.jpeg', 1, '2018-08-02 02:20:04', '2018-08-03 04:33:04'),
(38, 'Tuyến đường giao cắt dễ xảy ra tai nạn giao thông', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Đoạn đường Tôn Đản nối dài giao với đầu nhánh tuyến đường bê-tông dẫn vào khu dân cư của tổ 27 phường Hòa Thọ Tây chưa được cắm biển báo nên nhiều nguy cơ xảy ra tai nạn giao thông. \" src=\"http://baodanang.vn/dataimages/201808/original/images1462984_TON_DAN_1.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đoạn đường T&ocirc;n Đản n&ocirc;́i dài giao với đ&acirc;̀u nhánh tuy&ecirc;́n đường b&ecirc;-t&ocirc;ng d&acirc;̃n vào khu d&acirc;n cư của t&ocirc;̉ 27 phường Hòa Thọ T&acirc;y chưa được cắm bi&ecirc;̉n báo n&ecirc;n nhiều nguy cơ xảy ra tai nạn giao th&ocirc;ng.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo tìm hi&ecirc;̉u của chúng t&ocirc;i, tuy&ecirc;́n đường mà người d&acirc;n phản ánh là đường b&ecirc;-t&ocirc;ng r&ocirc;̣ng 2m chưa có t&ecirc;n. Đi&ecirc;̉m đ&acirc;̀u tuy&ecirc;́n đường giao với đường T&ocirc;n Đản n&ocirc;́i dài, đi&ecirc;̉m cu&ocirc;́i cạnh l&ocirc;́i d&acirc;̃n ra c&acirc;y xăng d&acirc;̀u s&ocirc;́ 10 (tr&ecirc;n đường Trường Sơn), thu&ocirc;̣c khu vực t&ocirc;̉ 27 và t&ocirc;̉ 28 phường Hòa Thọ T&acirc;y. Con đường hẹp, có nhi&ecirc;̀u đi&ecirc;̉m cua, g&acirc;́p khúc, ngoằn ngoèo và bị hạn ch&ecirc;́ v&ecirc;̀ t&acirc;̀m nhìn khi tham gia giao th&ocirc;ng.</p>\r\n\r\n<p>&Ocirc;ng Tr&acirc;̀n C&ocirc;ng Thu&acirc;́n, T&ocirc;̉ trưởng T&ocirc;̉ d&acirc;n ph&ocirc;́ (TDP) s&ocirc;́ 27 và &ocirc;ng Nguy&ecirc;̃n Đức Thắng, T&ocirc;̉ trưởng TDP s&ocirc;́ 28, phường Hòa Thọ T&acirc;y, cùng xác nh&acirc;̣n n&ocirc;̣i dung người d&acirc;n phản ánh là chính xác, đồng thời cho bi&ecirc;́t th&ecirc;m, mặc dù các tuy&ecirc;́n ki&ecirc;̣t, hẻm tr&ecirc;n địa bàn đ&ecirc;̀u cơ bản được b&ecirc;-t&ocirc;ng hóa nhưng nhi&ecirc;̀u tuy&ecirc;́n đường có những đoạn cua g&acirc;́p khúc, bị che khu&acirc;́t t&acirc;̀m nhìn.</p>\r\n\r\n<p>Theo &ocirc;ng Thu&acirc;́n, khi tuy&ecirc;́n đường T&ocirc;n Đản n&ocirc;́i dài được đưa vào sử dụng từ tháng 3-2018 đến nay, lượng phương ti&ecirc;̣n qua lại khu vực này ngày m&ocirc;̣t đ&ocirc;ng đúc. Đ&acirc;̀u tuy&ecirc;́n đường - từ đoạn giao giữa đường T&ocirc;n Đản n&ocirc;́i dài với đường nhánh d&acirc;̃n vào KDC t&ocirc;̉ 27 v&acirc;̃n chưa được cắm bi&ecirc;̉n báo giao th&ocirc;ng.</p>\r\n\r\n<p>&ldquo;Đường cong queo, dọc tuy&ecirc;́n đường có 2 đèn chi&ecirc;́u sáng đ&ecirc;̀u bị hỏng, còn 3 trụ đi&ecirc;̣n cũng chưa được lắp đèn chi&ecirc;́u sáng n&ecirc;n r&acirc;́t nguy hi&ecirc;̉m cho việc đi lại, nh&acirc;́t là vào ban đ&ecirc;m. Cũng may những vụ TNGT xảy ra chỉ l&agrave; va quẹt nhẹ. Người d&acirc;n r&acirc;́t mong cơ quan chức năng sớm lắp đặt bi&ecirc;̉n báo giao th&ocirc;ng cũng như b&ocirc;̉ sung h&ecirc;̣ th&ocirc;́ng đèn chi&ecirc;́u sáng c&ocirc;ng c&ocirc;̣ng nơi đ&acirc;y&rdquo;, &ocirc;ng Thu&acirc;́n cho hay.</p>\r\n\r\n<p>Trao đ&ocirc;̉i th&ecirc;m v&ecirc;̀ v&acirc;́n đ&ecirc;̀ này, Thi&ecirc;́u tá Đặng Hoàng Phi, Phó Trưởng C&ocirc;ng an phường Hòa Thọ T&acirc;y cho bi&ecirc;́t, từ đ&acirc;̀u năm 2018 đến nay, tr&ecirc;n địa bàn phường nói chung, địa bàn khu vực t&ocirc;̉ 27 và t&ocirc;̉ 28 nói ri&ecirc;ng chưa ghi nh&acirc;̣n trường hợp TNGT nghi&ecirc;m trọng nào.</p>\r\n\r\n<p>&ldquo;Tuy nhi&ecirc;n, tr&ecirc;n địa bàn phường chỉ có 5 tuy&ecirc;́n đường lớn đã có t&ecirc;n, còn lại chủ y&ecirc;́u là đường d&acirc;n sinh, ki&ecirc;̣t, hẻm nhỏ hẹp. Mặc dù c&aacute;c kiệt, hẻm đã được b&ecirc;-t&ocirc;ng hóa nhưng hạ t&acirc;̀ng giao th&ocirc;ng về cơ bản còn nhi&ecirc;̀u hạn ch&ecirc;́. Các tuy&ecirc;́n ki&ecirc;̣t, hẻm có quá nhi&ecirc;̀u ch&ocirc;̃ cua g&acirc;́p khúc, g&ocirc;̀ gh&ecirc;̀ n&ecirc;n dễ xảy ra va chạm giao th&ocirc;ng&rdquo;, Thi&ecirc;́u tá Phi n&oacute;i.</p>\r\n\r\n<p>Theo &ocirc;ng Nguy&ecirc;̃n Quang Vinh, Chủ tịch UBND phường Hòa Thọ T&acirc;y, đ&ocirc;́i với phản ánh của người d&acirc;n li&ecirc;n quan h&ecirc;̣ th&ocirc;́ng đèn chi&ecirc;́u sáng c&ocirc;ng c&ocirc;̣ng bị hỏng, hoặc c&acirc;̀n b&ocirc;̉ sung vị trí nào, đại di&ecirc;̣n TDP chỉ c&acirc;̀n báo cáo và đ&ecirc;̀ xu&acirc;́t cụ th&ecirc;̉; sau đó phường sẽ ki&ecirc;̉m tra thực t&ecirc;́ và xử lý ngay vì v&acirc;́n đ&ecirc;̀ này thu&ocirc;̣c th&acirc;̉m quy&ecirc;̀n của địa phương.</p>\r\n\r\n<p>Về hi&ecirc;̣n trạng cơ sở hạ t&acirc;̀ng tr&ecirc;n địa bàn phường, theo &ocirc;ng Vinh, mặc dù thời gian qua đã có sự thay đ&ocirc;̉i nhưng đa ph&acirc;̀n trong các KDC đ&ecirc;̀u là đường ki&ecirc;̣t, hẻm nhỏ, c&oacute; quá nhi&ecirc;̀u đoạn cua khúc khuỷu. &ldquo;Đ&ocirc;́i với đ&ecirc;̀ xu&acirc;́t cắm bi&ecirc;̉n báo giao th&ocirc;ng tại nhánh đường đ&acirc;̀u ki&ecirc;̣t d&acirc;̃n vào t&ocirc;̉ 27, với trách nhi&ecirc;̣m của phường, chúng t&ocirc;i ghi nh&acirc;̣n và sẽ báo cáo, ki&ecirc;́n nghị l&ecirc;n qu&acirc;̣n đ&ecirc;̉ có hướng xử lý.</p>\r\n\r\n<p>Còn đ&ocirc;́i với các ki&ecirc;̣t, hẻm c&oacute; những đoạn cua che khu&acirc;́t t&acirc;̀m nhìn, v&acirc;́n đ&ecirc;̀ này do lịch sử đ&ecirc;̉ lại và li&ecirc;n quan tới vi&ecirc;̣c c&acirc;́p gi&acirc;́y chứng nh&acirc;̣n quy&ecirc;̀n sử dụng đ&acirc;́t của các h&ocirc;̣ gia đình. Mu&ocirc;́n chỉnh trang cho r&ocirc;̣ng rãi, c&acirc;̀n sự đ&ocirc;̀ng thu&acirc;̣n của các h&ocirc;̣ và v&acirc;́n đ&ecirc;̀ này phải tri&ecirc;̉n khai từng bước theo đúng quy trình&rdquo;, &ocirc;ng Vinh cho bi&ecirc;́t th&ecirc;m.</p>', 'Báo Đà Nẵng nhận được phản ánh của người dân khu vực tổ 27 và 28 phường Hòa Thọ Tây (quận Cẩm Lệ) về việc tuyến đường giao cắt với đường Tôn Đản (nối dài) có lượng xe cộ qua lại nhiều nhưng chưa cắm biển báo giao thông; đèn chiếu sáng công cộng trong khu dân cư (KDC) cũng chưa đầy đủ; chưa kể nhiều tuyến đường kiệt, hẻm quanh co, gấp khúc, ảnh hưởng tới tầm nhìn, dễ xảy ra tai nạn giao thông (TNGT).', '2', '11', '2', 'http://localhost/theDaNangNews/local/storage/app/namvc_qn@gmail.com/X1yy8Y0U4dVZzE9fqARgX7lb8AVq8xRnleRyu0Rh.jpeg', 0, '2018-08-03 03:18:35', '2018-08-03 03:34:06'),
(39, 'Khuyến khích đầu tư bãi đỗ xe công cộng', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Các đơn vị trưng bày và giới thiệu sản phẩm khi tham gia hội nghị kết nối cung - cầu sáng 3-8. Ảnh: KHÁNH HÒA\" src=\"http://baodanang.vn/dataimages/201808/original/images1463293_KHANHHOA1.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>C&aacute;c đơn vị trưng b&agrave;y v&agrave; giới thiệu sản phẩm khi tham gia hội nghị kết nối cung - cầu s&aacute;ng 3-8. Ảnh: KH&Aacute;NH H&Ograve;A</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Lần thứ ba c&oacute; mặt tại hội nghị kết nối cung - cầu do th&agrave;nh phố Đ&agrave; Nẵng tổ chức, b&agrave; Nguyễn Thị H&agrave;, Ph&oacute; Gi&aacute;m đốc C&ocirc;ng ty TNHH Sản xuất, chế biến v&agrave; thương mại H&agrave; Trung (tỉnh Ph&uacute; Y&ecirc;n) cho biết, qua c&aacute;c lần tham gia kết nối, c&ocirc;ng ty đ&atilde; k&yacute; được thỏa thuận hợp t&aacute;c với nhiều DN ở c&aacute;c tỉnh miền Trung; qua đ&oacute;, sản phẩm của c&ocirc;ng ty đ&atilde; &ldquo;l&ecirc;n kệ&rdquo; ở nhiều si&ecirc;u thị, cửa h&agrave;ng b&aacute;n lẻ lớn tr&ecirc;n cả nước. &ldquo;Một năm t&ocirc;i tham gia nhiều hội nghị kết nối cung - cầu, v&igrave; vừa giới thiệu được sản phẩm vừa t&igrave;m kiếm được c&aacute;c bạn h&agrave;ng tin cậy.</p>\r\n\r\n<p>Tại Đ&agrave; Nẵng, sau những lần kết nối trước, đến nay sản phẩm b&ograve; &ldquo;1 nắng 2 sương&rdquo;, c&aacute; cơm chi&ecirc;n gi&ograve;n... của ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; mặt tại một số cửa h&agrave;ng như: Đặc sản 3 miền, Thi&ecirc;n Ph&uacute;... Năm nay, ch&uacute;ng t&ocirc;i li&ecirc;n kết với C&ocirc;ng ty CP N&ocirc;ng nghiệp c&ocirc;ng nghệ cao Davina mở chi nh&aacute;nh ph&acirc;n phối ch&iacute;nh thức c&aacute;c sản phẩm của H&agrave; Trung ra thị trường Đ&agrave; Nẵng v&agrave; khu vực miền Trung&rdquo;, b&agrave; H&agrave; n&oacute;i.</p>\r\n\r\n<p>Gian h&agrave;ng trưng b&agrave;y c&aacute;c sản phẩm rượu vang nho nguy&ecirc;n chất, đậu phụng rang ch&aacute;y tỏi, mật nho nguy&ecirc;n chất, mứt rong sụn... của C&ocirc;ng ty TNHH Sản xuất - thương mại v&agrave; dịch vụ Hưng Ph&aacute;t T&agrave;i (Ninh Thuận) thu h&uacute;t kh&aacute; đ&ocirc;ng kh&aacute;ch tham quan.</p>\r\n\r\n<p>&Ocirc;ng Nguyễn Ho&agrave;i Linh, Gi&aacute;m đốc c&ocirc;ng ty cho biết: &ldquo;Ch&uacute;ng t&ocirc;i đ&aacute;nh gi&aacute; cao c&aacute;c hoạt động kết nối cung - cầu m&agrave; th&agrave;nh phố Đ&agrave; Nẵng tổ chức; bởi qua đ&oacute; gi&uacute;p những DN nhỏ v&agrave; vừa t&igrave;m kiếm bạn h&agrave;ng, ti&ecirc;u thụ sản phẩm, gi&uacute;p c&aacute;c mặt h&agrave;ng nội địa t&igrave;m được chỗ đứng tr&ecirc;n thị trường&rdquo;.</p>\r\n\r\n<p>D&ugrave; đ&atilde; th&acirc;n quen với thị trường Đ&agrave; Nẵng cũng như một số tỉnh, th&agrave;nh phố miền Trung trong v&agrave;i năm trở lại đ&acirc;y, nhưng theo &ocirc;ng Phạm Ngọc Thạch, Chủ tịch HĐQT Hợp t&aacute;c x&atilde; Xu&acirc;n Ph&uacute;c (Đ&agrave; Lạt), mỗi lần tham gia kết nối cung - cầu, DN lại t&igrave;m th&ecirc;m được những bạn h&agrave;ng mới.</p>\r\n\r\n<p>&ldquo;Ngo&agrave;i việc cung ứng sản phẩm, ch&uacute;ng t&ocirc;i mong muốn li&ecirc;n kết với c&aacute;c đơn vị c&ugrave;ng ng&agrave;nh h&agrave;ng để lấy nguồn h&agrave;ng cung ứng tiếp cho c&aacute;c cửa h&agrave;ng của ch&uacute;ng t&ocirc;i đang trải d&agrave;i tr&ecirc;n cả nước&rdquo;, &ocirc;ng Thạch n&oacute;i.</p>\r\n\r\n<p>B&agrave; Nguyễn Thị Thu Hằng, Ph&oacute; Gi&aacute;m đốc C&ocirc;ng ty TNHH Thương mại v&agrave; Kiến tr&uacute;c &Aacute; Ch&acirc;u (Đ&agrave; Nẵng) b&agrave;y tỏ, qua 4 lần tham gia c&aacute;c hội nghị kết nối, đơn vị đ&atilde; c&oacute; được những bạn h&agrave;ng lớn như: C&ocirc;ng ty TNHH Nh&agrave; m&aacute;y bia Heineken Việt Nam tại Đ&agrave; Nẵng, C&ocirc;ng ty CP Prime Đại Quang... Ngay trong s&aacute;ng 3-8, c&ocirc;ng ty đăng k&yacute; sản lượng cung ứng 12.000 tấn sản phẩm/năm cho C&ocirc;ng ty TNHH Shin Heung Vina với gi&aacute; trị khoảng 100 tỷ đồng/năm.</p>\r\n\r\n<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Các đơn vị trưng bày và giới thiệu sản phẩm khi tham gia hội nghị kết nối cung - cầu sáng 3-8. 	      Ảnh: KHÁNH HÒA\" src=\"http://baodanang.vn/dataimages/201808/original/images1463292_KHANHHOA.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>C&aacute;c đơn vị trưng b&agrave;y v&agrave; giới thiệu sản phẩm khi tham gia hội nghị kết nối cung - cầu s&aacute;ng 3-8. Ảnh: KH&Aacute;NH H&Ograve;A</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của nhiều DN v&agrave; Sở C&ocirc;ng thương c&aacute;c tỉnh, th&agrave;nh phố, sản phẩm v&ugrave;ng, miền hiện nay tuy nhiều nhưng sản lượng &iacute;t, quy m&ocirc; sản xuất nhỏ, dẫn tới chưa đủ khả năng cung ứng khi bạn h&agrave;ng y&ecirc;u cầu với số lượng lớn.</p>\r\n\r\n<p>&ldquo;Quảng Ninh c&oacute; lượng kh&aacute;ch du lịch đến rất đ&ocirc;ng, sản phẩm đặc sắc, nhưng quy m&ocirc; sản xuất c&ograve;n hạn chế, kh&ocirc;ng đủ cung ứng cho thị trường, l&acirc;u dần cũng mất đi thương hiệu&rdquo;, &ocirc;ng Nguyễn Mạnh H&agrave;, Gi&aacute;m đốc Sở C&ocirc;ng thương tỉnh Quảng Ninh n&ecirc;u hạn chế của địa phương.</p>\r\n\r\n<p>Với g&oacute;c nh&igrave;n của DN, b&agrave; Nguyễn Thị H&agrave; đề xuất c&aacute;c đơn vị, địa phương khi tổ chức hội nghị kết nối cung - cầu cần mời th&ecirc;m c&aacute;c đại diện của chuỗi cửa h&agrave;ng kinh doanh lớn, c&aacute;c kh&aacute;ch sạn, si&ecirc;u thị nhằm gi&uacute;p DN c&oacute; thể dễ d&agrave;ng t&igrave;m kiếm đầu ra cho sản phẩm của m&igrave;nh.</p>\r\n\r\n<p>&Ocirc;ng Nguyễn H&agrave; Bắc, Ph&oacute; Gi&aacute;m đốc Sở C&ocirc;ng thương cũng nh&igrave;n nhận, việc nhập v&agrave; cung ứng sản phẩm giữa c&aacute;c đơn vị c&oacute; số lượng lớn c&ograve;n kh&aacute; khi&ecirc;m tốn, hơn nữa li&ecirc;n kết hợp t&aacute;c mới chỉ dừng lại ở c&aacute;c DN sản xuất v&agrave; thương mại, chưa c&oacute; sự hợp t&aacute;c giữa c&aacute;c DN sản xuất; một số bi&ecirc;n bản ghi nhớ, thỏa thuận hợp t&aacute;c chưa được thực hiện hiệu quả&hellip;</p>\r\n\r\n<p>V&igrave; vậy, để kết nối hiệu quả hơn, Sở C&ocirc;ng thương sẽ t&igrave;m nhiều giải ph&aacute;p trong thời gian tới. Hiện Sở C&ocirc;ng thương đ&atilde; c&oacute; đại diện tại một số tỉnh Nam L&agrave;o, c&aacute;c DN c&oacute; nhu cầu kết nối để t&igrave;m kiếm, mở rộng thị trường c&oacute; thể li&ecirc;n hệ để nhận sự hỗ trợ kịp thời.</p>', 'Hơn 170 đơn vị, doanh nghiệp (DN) của 24 tỉnh, thành phố trên cả nước có mặt tại Trung tâm Hội chợ-Triển lãm quốc tế Đà Nẵng để tham dự Hội nghị kết nối cung – cầu năm 2018 do Sở Công thương thành phố tổ chức vào sáng 3-8 bày tỏ sự quan tâm đến việc liên kết hợp tác, quảng bá sản phẩm, học hỏi kinh nghiệm, tìm kiếm thị trường trong và ngoài nước.', '5', '5', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/KNN0LEjMIxTQBoqUCXWAplqIdlhq8NPO8CUF3Ep7.jpeg', 0, '2018-08-05 19:54:17', '2018-08-05 21:20:55'),
(40, 'Bộ GTVT không coi Grab như taxi và không phải gắn mào', '<p>Bộ Giao th&ocirc;ng Vận tải (GTVT) vừa tr&igrave;nh Ch&iacute;nh phủ dự thảo Nghị định thay thế Nghị định 86 về kinh doanh v&agrave; điều kiện kinh doanh vận tải bằng &ocirc;t&ocirc;, sau hai năm soạn thảo.</p>\r\n\r\n<table align=\"center\" style=\"width:490px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Theo dự thảo mà Bộ GTVT vừa trình Chính phủ, loại hình vận tải Grab không phải gắn hộp đèn trên nóc xe như một số đề xuất trước đó. Ảnh KT.\" src=\"http://baodanang.vn/dataimages/201808/original/images1463486_grab4_qcpb.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Theo dự thảo m&agrave; Bộ GTVT vừa tr&igrave;nh Ch&iacute;nh phủ, loại h&igrave;nh vận tải Grab kh&ocirc;ng phải gắn hộp đ&egrave;n tr&ecirc;n n&oacute;c xe như một số đề xuất trước đ&oacute;. Ảnh KT.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Với việc bổ sung loại h&igrave;nh giao dịch điện tử cho ph&ugrave; hợp với xu thế mới, dự thảo cho rằng Grab car được coi l&agrave; xe hợp đồng điện tử, phải ni&ecirc;m yết &quot;XE HỢP ĐỒNG ĐIỆN TỬ&quot; v&agrave; c&aacute;c th&ocirc;ng tin tr&ecirc;n xe theo nội dung quy định. Việc n&agrave;y sẽ dễ quản l&yacute; v&agrave; tạo sự cạnh tranh b&igrave;nh đẳng giữa taxi truyền thống v&agrave; taxi c&ocirc;ng nghệ.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, theo dự thảo m&agrave; Bộ GTVT vừa tr&igrave;nh Ch&iacute;nh phủ, loại h&igrave;nh vận tải n&agrave;y kh&ocirc;ng phải gắn hộp đ&egrave;n tr&ecirc;n n&oacute;c xe như một số đề xuất trước đ&oacute;.</p>\r\n\r\n<p>Theo giải tr&igrave;nh của Bộ Giao th&ocirc;ng, việc ứng dụng phần mềm kết nối hoạt động vận tải kh&aacute;ch theo hợp đồng đ&atilde; tạo điều kiện cho h&agrave;nh kh&aacute;ch trong việc đi lại như lựa chọn phương tiện, biết th&ocirc;ng tin l&aacute;i xe, biết gi&aacute; cước, tăng khả năng t&igrave;m lại h&agrave;nh l&yacute;, t&agrave;i sản...&nbsp;</p>\r\n\r\n<table align=\"center\" style=\"width:490px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Cả nước hiện có hơn 36.000 xe thí điểm hợp đồng điện tử. Ảnh KT.\" src=\"http://baodanang.vn/dataimages/201808/original/images1463487_grab2_yknd.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cả nước hiện c&oacute; hơn 36.000 xe th&iacute; điểm hợp đồng điện tử. Ảnh KT.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Dự thảo cũng bổ sung loại h&igrave;nh taxi t&iacute;nh tiền qua phần mềm gọi l&agrave; taxi điện tử b&ecirc;n cạnh loại h&igrave;nh taxi. Xe n&agrave;y được gắn hộp đ&egrave;n với chữ &quot;TAXI ĐIỆN TỬ&quot;. Tr&ecirc;n xe c&oacute; thiết bị phần mềm kết nối để giao dịch với h&agrave;nh kh&aacute;ch; cung cấp cho h&agrave;nh kh&aacute;ch th&ocirc;ng tin về doanh nghiệp, l&aacute;i xe, xe, gi&aacute; cước v&agrave; số tiền...</p>\r\n\r\n<p>Với c&aacute;c xe gắn m&agrave;o taxi điện tử, người d&acirc;n c&oacute; thể gọi xe theo nhiều c&aacute;ch như tr&ecirc;n đường, qua tổng đ&agrave;i.</p>\r\n\r\n<p>Bộ GTVT cho rằng, việc bổ sung quy định xe taxi t&iacute;nh tiền th&ocirc;ng qua phần mềm l&agrave; &ldquo;Taxi điện tử&rdquo; nhằm khuyến kh&iacute;ch ph&aacute;t triển ứng dụng c&ocirc;ng nghệ trong hoạt động vận tải; đồng thời tạo m&ocirc;i trường kinh doanh l&agrave;nh mạnh, b&igrave;nh đẳng giữa c&aacute;c đơn vị taxi truyền thống v&agrave; taxi c&ocirc;ng nghệ.</p>\r\n\r\n<p>Về &yacute; kiến của c&aacute;c hiệp hội taxi cần quy định xe kinh doanh vận tải dưới 9 chỗ&nbsp; l&agrave; taxi, đại diện Ban soạn thảo cho rằng quy định như vậy g&acirc;y kh&oacute; cho người d&acirc;n v&igrave; nhiều khi họ muốn thu&ecirc; xe hợp đồng dưới 9 chỗ m&agrave; kh&ocirc;ng muốn đi taxi.</p>\r\n\r\n<p>&ldquo;Điều kiện n&agrave;y nhằm đảm bảo c&ocirc;ng bằng xe hợp đồng xe taxi, do đối tượng n&agrave;y c&oacute; t&iacute;nh chất, phạm vi hoạt động, sức chứa phương tiện giống xe taxi. Đồng thời, sau 2 năm th&iacute; điểm hoạt động của xe hợp đồng điện tử đ&atilde; ph&aacute;t sinh vấn đề, điều kiện kinh doanh chưa tương đồng, dẫn tới đấu tranh, khiếu nại giữa c&aacute;c đơn vị. Do đ&oacute;, dự thảo đưa điều kiện 2 loại h&igrave;nh tương đương nhau, để đảm bảo c&ocirc;ng bằng&rdquo;, Bộ GTVT n&ecirc;u quan điểm về dự thảo.</p>\r\n\r\n<table align=\"center\" style=\"width:490px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Hiệp hội Taxi truyền thống cho rằng, xe Grab bản chất là hoạt động kinh doanh vận tải nên phải được coi là taxi. Như vậy mới tạo cơ chế cạnh tranh bình đẳng. Ảnh KT.\" src=\"http://baodanang.vn/dataimages/201808/original/images1463488_grab1_ledi.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Hiệp hội Taxi truyền thống cho rằng, xe Grab bản chất l&agrave; hoạt động kinh doanh vận tải n&ecirc;n phải được coi l&agrave; taxi. Như vậy mới tạo cơ chế cạnh tranh b&igrave;nh đẳng. Ảnh KT.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ngo&agrave;i ra, Dự thảo Nghị định tr&igrave;nh Thủ tướng cũ ng bổ sung quy định mới đối với việc quản l&yacute;, sử dụng hợp đồng vận tải điện tử; quy định về tr&aacute;ch nhiệm của c&aacute;c tổ chức, đơn vị v&agrave; c&aacute; nh&acirc;n c&oacute; li&ecirc;n quan đến việc thực hiện ứng dụng hợp đồng vận tải điện tử; khuyến kh&iacute;ch c&aacute;c doanh nghiệp, hợp t&aacute;c x&atilde; kinh doanh vận tải h&agrave;nh kh&aacute;ch, h&agrave;ng h&oacute;a đổi mới c&ocirc;ng nghệ, tăng cường ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin để giảm chi ph&iacute; vận tải.</p>\r\n\r\n<p>Được biết, bản Dự thảo của Bộ GTVT mới tr&igrave;nh chưa nhận được đồng t&igrave;nh của c&aacute;c hiệp hội taxi.</p>\r\n\r\n<p>Theo &ocirc;ng Nguyễn C&ocirc;ng H&ugrave;ng - Chủ tịch Hiệp hội Taxi H&agrave; Nội, xe Grab bản chất l&agrave; hoạt động kinh doanh vận tải n&ecirc;n phải được coi l&agrave; taxi.</p>\r\n\r\n<p>&ldquo;C&aacute;c xe n&agrave;y phải c&oacute; bộ nhận diện ri&ecirc;ng như gắn biển m&agrave;u v&agrave;ng để ph&acirc;n biệt với xe gia đ&igrave;nh, c&aacute;c cơ quan chức năng dễ quản l&yacute;&rdquo;, &ocirc;ng H&ugrave;ng n&ecirc;u quan điểm.</p>\r\n\r\n<p>Thống k&ecirc; cho thấy, cả nước c&oacute; 866 đơn vị vận tải với hơn 36.800 phương tiện tham gia th&iacute; điểm hợp đồng điện tử. Trong đ&oacute; TP HCM c&oacute; 506 doanh nghiệp, ba nh&agrave; cung cấp phần mềm, 21.600 xe. H&agrave; Nội c&oacute; 354 đơn vị vận tải, 7 nh&agrave; cung cấp phần mềm với hơn 15.000 xe.</p>\r\n\r\n<p>Thời gian qua, nhiều &yacute; kiến tranh c&atilde;i nảy sinh giữa c&aacute;c cơ quan nh&agrave; nước v&agrave; doanh nghiệp taxi về loại h&igrave;nh Grab car được xếp v&agrave;o loại h&igrave;nh xe hợp đồng hay taxi. Một số đề xuất đ&atilde; được đưa ra để ph&acirc;n biệt loại h&igrave;nh n&agrave;y như gắn hộp đ&egrave;n tr&ecirc;n n&oacute;c, gắn biển m&agrave;u v&agrave;ng...</p>\r\n\r\n<p>(Bai viet da chinh sua !)</p>', 'Bộ GTVT cho rằng loại hình Grab car là xe hợp đồng điện tử, không cần thiết phải gắn hộp đèn trên nóc như một số đề xuất trước đó.', '5', '5', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/5j7hQeEEYamxFlibVjWUN2VJuigdRazUIhOt0Tdf.jpeg', 0, '2018-08-05 20:22:16', '2018-08-05 21:21:29');
INSERT INTO `post_lists` (`id`, `title`, `content`, `quotes_content`, `author_id`, `post_category_id`, `status`, `image_avatar`, `view`, `created_at`, `updated_at`) VALUES
(41, 'Ngày 2-9: Hạn cuối thông xe cao tốc Đà Nẵng-Quảng Ngãi', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"A\" src=\"http://baodanang.vn/dataimages/201808/original/images1463115_A3.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bộ trưởng Bộ GTVT Nguyễn Văn Thể (<em>thứ hai, phải sang</em>) kiểm tra tiến độ thi c&ocirc;ng cao tốc Đ&agrave; Nẵng-Quảng Ng&atilde;i.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Được biết, hiện tr&ecirc;n tuyến cao tốc đi qua địa phận Quảng Nam v&agrave; Quảng Ng&atilde;i vẫn c&ograve;n một số vướng mắc li&ecirc;n quan đến đường gom d&acirc;n sinh, c&ocirc;ng t&aacute;c đền b&ugrave; do thi c&ocirc;ng cao tốc l&agrave;m nứt nh&agrave; d&acirc;n&hellip;</p>\r\n\r\n<p>Ri&ecirc;ng tại th&agrave;nh phố Đ&agrave; Nẵng, c&ograve;n 31 trường hợp cần sớm đền b&ugrave; để thi c&ocirc;ng đường nối với quốc lộ 14B.</p>', 'ĐNO - Sáng 3-8, tại buổi làm việc với lãnh đạo các tỉnh Quảng Nam, Quảng Ngãi và thành phố Đà Nẵng về dự án đường cao tốc Đà Nẵng-Quảng Ngãi, Bộ trưởng Bộ GTVT Nguyễn Văn Thể yêu cầu: “Đến ngày 2-9 thông xe và đưa vào vận hành khai thác cao tốc Đà Nẵng-Quảng Ngãi. Nếu các đơn vị làm không xong, không đưa vào vận hành đúng dịp 2-9 thì phải chịu trách nhiệm với bộ”', '5', '5', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/yhlwBKpjGzKAEmnVMFHLupxhCKJQCb91YdN4GfsL.jpeg', 0, '2018-08-05 20:24:56', '2018-08-05 21:21:19'),
(42, 'Quản lý thanh toán trực tuyến của khách du lịch nước ngoài', '<p>Đ&acirc;y l&agrave; th&aacute;ch thức đối với hoạt động du lịch hiện nay, đ&ograve;i hỏi cơ quan chức năng cần tăng cường c&aacute;c biện ph&aacute;p để kiểm tra, gi&aacute;m s&aacute;t nhằm bảo đảm m&ocirc;i trường kinh doanh l&agrave;nh mạnh, c&ocirc;ng bằng, hạn chế thất thu ng&acirc;n s&aacute;ch địa phương.</p>\r\n\r\n<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Cần tăng cường công tác quản lý hoạt động thanh toán trực tuyến của khách du lịch . Trong ảnh: Du khách mua hàng lưu niệm tại Đà Nẵng.  (Ảnh mang tính minh họa)\" src=\"http://baodanang.vn/dataimages/201808/original/images1462981_Cho_Han.gif\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cần tăng cường c&ocirc;ng t&aacute;c quản l&yacute; hoạt động thanh to&aacute;n trực tuyến của kh&aacute;ch du lịch .&nbsp;Trong ảnh: Du kh&aacute;ch mua h&agrave;ng lưu niệm tại Đ&agrave; Nẵng. (Ảnh mang t&iacute;nh minh họa)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>Xu thế thanh to&aacute;n bằng trực tuyến</strong></p>\r\n\r\n<p>Theo &ocirc;ng L&ecirc; Sơn Phong, Ph&oacute; Gi&aacute;m đốc Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng (TT&amp;TT), phương thức giao dịch v&iacute; điện tử (VIMO), thanh to&aacute;n trực tuyến l&agrave; xu thế tr&ecirc;n thế giới hiện nay. C&aacute;c h&atilde;ng lớn tr&ecirc;n thế giới đ&atilde; &aacute;p dụng phổ biến h&igrave;nh thức thanh to&aacute;n n&agrave;y.</p>\r\n\r\n<p>Một số nước Đ&ocirc;ng Nam &Aacute; như Malaysia cho ph&eacute;p WeChat Pay hay Alipay (của Alibaba, Trung Quốc) hoạt động hợp ph&aacute;p. Ở Việt Nam, nếu giao dịch th&ocirc;ng qua VIMO th&igrave; việc thanh to&aacute;n bằng WeChat Pay vẫn hợp ph&aacute;p.</p>\r\n\r\n<p>&ldquo;Kh&aacute;ch du lịch Trung Quốc sang Việt Nam d&ugrave;ng h&igrave;nh thức thanh to&aacute;n điện tử c&oacute; thể xảy ra 2 trường hợp: Một l&agrave;, thanh to&aacute;n th&ocirc;ng qua đơn vị thanh to&aacute;n điện tử của Việt Nam. V&iacute; dụ, người d&ugrave;ng Trung Quốc sử dụng c&ocirc;ng cụ thanh to&aacute;n WeChat Pay của Trung Quốc v&agrave; thanh to&aacute;n qua đơn vị thanh to&aacute;n VIMO của Việt Nam, đ&acirc;y l&agrave; thanh to&aacute;n hợp ph&aacute;p.</p>\r\n\r\n<p>Trường hợp thứ hai, du kh&aacute;ch Trung Quốc v&agrave; cả người b&aacute;n Việt Nam đều sử dụng WeChat Pay để x&aacute;c thực việc mua v&agrave; b&aacute;n. Đ&acirc;y l&agrave; h&agrave;nh vi bất hợp ph&aacute;p v&agrave; Nh&agrave; nước kh&ocirc;ng thu được thuế do tiền Việt Nam kh&ocirc;ng được sử dụng&rdquo;, &ocirc;ng Phong cho hay.</p>\r\n\r\n<p>Với kinh nghiệm l&acirc;u năm trong kinh doanh lữ h&agrave;nh, một ph&oacute; gi&aacute;m đốc doanh nghiệp du lịch tr&ecirc;n địa b&agrave;n th&agrave;nh phố nh&igrave;n nhận, đến nay, d&ugrave; Đ&agrave; Nẵng chưa ph&aacute;t hiện vụ việc n&agrave;o li&ecirc;n quan đến việc kh&aacute;ch du lịch thanh to&aacute;n bất hợp ph&aacute;p bằng ứng dụng thanh to&aacute;n trực tuyến, nhưng kh&ocirc;ng c&oacute; nghĩa sẽ kh&ocirc;ng xảy ra. Thực tế, ở một số địa phương&hellip; đ&atilde; xuất hiện t&igrave;nh trạng kh&aacute;ch du lịch Trung Quốc sử dụng ứng dụng WeChat Pay để thanh to&aacute;n khi mua h&agrave;ng.</p>\r\n\r\n<p>Khi du kh&aacute;ch chọn được đồ muốn mua, chủ cửa h&agrave;ng liền mở ứng dụng n&agrave;y c&oacute; c&agrave;i đặt trong điện thoại, qu&eacute;t m&atilde; thanh to&aacute;n QR. Chỉ 30 gi&acirc;y, việc thanh to&aacute;n ho&agrave;n tất. Tất cả số tiền n&agrave;y nhanh ch&oacute;ng được chuyển từ t&agrave;i khoản của kh&aacute;ch du lịch Trung Quốc sang t&agrave;i khoản của chủ cửa h&agrave;ng cũng được mở tại một ng&acirc;n h&agrave;ng Trung Quốc v&agrave; kh&ocirc;ng cần th&ocirc;ng qua bất kỳ ng&acirc;n h&agrave;ng hay cổng trung gian thanh to&aacute;n n&agrave;o của Việt Nam. Đ&acirc;y ch&iacute;nh l&agrave; lỗ hổng g&acirc;y thất thu thuế cho Nh&agrave; nước.</p>\r\n\r\n<p>&Ocirc;ng V&otilde; Minh, Gi&aacute;m đốc Chi nh&aacute;nh Ng&acirc;n h&agrave;ng Nh&agrave; nước Việt Nam (NHNN) tại Đ&agrave; Nẵng cho biết: &ldquo;Chuỗi thanh to&aacute;n trực tuyến tr&aacute;i ph&eacute;p xuy&ecirc;n bi&ecirc;n giới gồm 3 &ldquo;mắt x&iacute;ch&rdquo;: kh&aacute;ch du lịch nước ngo&agrave;i, c&ocirc;ng ty lữ h&agrave;nh v&agrave; c&aacute;c cửa h&agrave;ng chấp nhận thanh to&aacute;n.</p>\r\n\r\n<p>Để ngăn chặn, ch&uacute;ng ta phải quản l&yacute; được c&aacute;c cửa h&agrave;ng v&agrave; c&aacute;c c&ocirc;ng ty du lịch. Điều n&agrave;y cần sự v&agrave;o cuộc của hệ thống c&aacute;c cơ quan chức năng, kh&ocirc;ng thể một m&igrave;nh ng&agrave;nh ng&acirc;n h&agrave;ng l&agrave;m được&rdquo;.</p>\r\n\r\n<p>Theo t&igrave;m hiểu, từ v&agrave;i năm trở lại đ&acirc;y, NHNN Việt Nam - Chi nh&aacute;nh Đ&agrave; Nẵng phối hợp với Sở T&agrave;i ch&iacute;nh đẩy mạnh đề &aacute;n &ldquo;Hạn chế thanh to&aacute;n bằng tiền mặt&rdquo; v&agrave; đ&atilde; triển khai lắp đặt hệ thống c&aacute;c m&aacute;y POS thanh to&aacute;n tiền qua thẻ tại c&aacute;c trung t&acirc;m thương mại, c&aacute;c điểm mua sắm tr&ecirc;n địa b&agrave;n th&agrave;nh phố.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, theo đ&aacute;nh gi&aacute; từ ph&iacute;a NHNN Việt Nam - Chi nh&aacute;nh Đ&agrave; Nẵng, hoạt động của m&aacute;y POS n&agrave;y chưa thực sự hiệu quả, đa số người d&acirc;n vẫn c&oacute; th&oacute;i quen d&ugrave;ng tiền mặt. Mới đ&acirc;y, NHNN Việt Nam c&oacute; c&ocirc;ng văn gửi c&aacute;c chi nh&aacute;nh NHNN tỉnh, th&agrave;nh phố đề nghị c&aacute;c chi nh&aacute;nh n&agrave;y b&aacute;o c&aacute;o UBND c&aacute;c địa phương để th&agrave;nh lập c&aacute;c đội thanh tra li&ecirc;n ng&agrave;nh; đồng thời nghi&ecirc;n cứu c&aacute;c giải ph&aacute;p sớm tr&igrave;nh Thủ tướng Ch&iacute;nh phủ trong thời gian tới.</p>\r\n\r\n<p><strong>&Aacute;p dụng c&ocirc;ng nghệ r&agrave; qu&eacute;t, tr&iacute;ch xuất c&aacute;c giao dịch</strong></p>\r\n\r\n<p>Qua c&ocirc;ng t&aacute;c điều tra, mặc d&ugrave; Đ&agrave; Nẵng chưa ph&aacute;t hiện vụ việc n&agrave;o li&ecirc;n quan đến kh&aacute;ch du lịch sử dụng m&aacute;y t&iacute;nh tiền bất hợp ph&aacute;p, nhưng đại diện Ph&ograve;ng An ninh Kinh tế (C&ocirc;ng an th&agrave;nh phố) khuyến c&aacute;o, với một lượng kh&aacute;ch du lịch tăng trưởng ng&agrave;y c&agrave;ng đ&ocirc;ng, việc tăng cường c&ocirc;ng t&aacute;c quản l&yacute; hoạt động thanh to&aacute;n trong mua b&aacute;n l&agrave; cần thiết.</p>\r\n\r\n<p>Về mặt quản l&yacute; c&ocirc;ng nghệ, &ocirc;ng L&ecirc; Sơn Phong cho rằng, vấn đề quản l&yacute; nằm ở chỗ l&agrave;m sao ngăn chặn sự th&ocirc;ng đồng giữa người b&aacute;n v&agrave; người mua c&ugrave;ng sử dụng h&igrave;nh thức bất hợp ph&aacute;p để qua mặt ph&aacute;p luật Việt Nam, g&acirc;y thất thu thuế.</p>\r\n\r\n<p>X&eacute;t ở g&oacute;c độ l&yacute; thuyết, c&oacute; thể sử dụng c&aacute;c c&ocirc;ng cụ CNTT tr&iacute;ch th&ocirc;ng tin từ g&oacute;i IP để c&oacute; địa chỉ nguồn v&agrave; đ&iacute;ch (v&iacute; dụ, địa chỉ IP từ Việt Nam giao dịch đến địa chỉ website đ&iacute;ch ng&acirc;n h&agrave;ng nước ngo&agrave;i) v&agrave; Port dịch vụ (ở lớp ứng dụng-App) để chặn việc giao dịch n&agrave;y.</p>\r\n\r\n<p>&ldquo;Sở TT&amp;TT sẽ phản &aacute;nh l&ecirc;n Bộ TT&amp;TT việc n&agrave;y v&agrave; đề nghị &aacute;p dụng c&aacute;c biện ph&aacute;p c&ocirc;ng nghệ để lọc v&agrave; chặn c&aacute;c giao dịch bất hợp ph&aacute;p. Tại Đ&agrave; Nẵng, sở sẽ phối hợp với c&aacute;c doanh nghiệp viễn th&ocirc;ng khoanh v&ugrave;ng c&aacute;c v&ugrave;ng c&oacute; nhiều giao dịch với người nước ngo&agrave;i để d&ograve; qu&eacute;t c&aacute;c giao dịch đến c&aacute;c m&aacute;y chủ ng&acirc;n h&agrave;ng ở nước ngo&agrave;i, ph&aacute;t hiện c&aacute;c giao dịch khả nghi bất hợp ph&aacute;p v&agrave; phối hợp với ng&agrave;nh c&ocirc;ng c&ocirc;ng an kiểm tra&rdquo;, &ocirc;ng Phong nhấn mạnh.</p>\r\n\r\n<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Cần tăng cường công tác quản lý hoạt động thanh toán trực tuyến của khách du lịch . Trong ảnh: Du khách mua hàng lưu niệm tại Đà Nẵng.  (Ảnh mang tính minh họa)\" src=\"http://baodanang.vn/dataimages/201808/original/images1462988_Mua_s_m.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cần tăng cường c&ocirc;ng t&aacute;c quản l&yacute; hoạt động thanh to&aacute;n trực tuyến của kh&aacute;ch du lịch .&nbsp;Trong ảnh: Du kh&aacute;ch mua h&agrave;ng lưu niệm tại Đ&agrave; Nẵng. (Ảnh mang t&iacute;nh minh họa)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Li&ecirc;n quan đến hoạt động của tour gi&aacute; rẻ, trong đ&oacute; c&oacute; tiềm ẩn về h&igrave;nh thức thanh to&aacute;n bất hợp ph&aacute;p, &ocirc;ng Nguyễn Xu&acirc;n B&igrave;nh, Ph&oacute; Gi&aacute;m đốc Sở Du lịch b&agrave;y tỏ, quan điểm của sở l&agrave; để quản l&yacute; tốt cần phải c&ocirc;ng khai, minh bạch dịch vụ v&agrave; gi&aacute; tour để kh&aacute;ch hiểu được gi&aacute; trị của dịch vụ tương ứng với gi&aacute; trị chi trả.</p>\r\n\r\n<p>Ngo&agrave;i ra, cần kiểm so&aacute;t chất lượng dịch vụ v&agrave; kh&acirc;u thanh to&aacute;n, chống thất thu thuế, bảo vệ quyền lợi của kh&aacute;ch du lịch v&agrave; h&igrave;nh ảnh điểm đến cũng như duy tr&igrave; sự ph&aacute;t triển ổn định của thị trường.</p>\r\n\r\n<p>Trong khi đ&oacute;, đại diện NHNN-Chi nh&aacute;nh Đ&agrave; Nẵng khẳng định, sẽ tăng cường c&aacute;c biện ph&aacute;p quản l&yacute; v&agrave; kiểm so&aacute;t hoạt động giao dịch ngoại hối, thanh to&aacute;n bằng m&aacute;y POS hoặc c&aacute;c ứng dụng thanh to&aacute;n tr&ecirc;n thiết bị di động (Alipay, Wechat Pay...) tại c&aacute;c cơ sở mua sắm.</p>\r\n\r\n<p>Ở g&oacute;c nh&igrave;n kh&aacute;c, &ocirc;ng Nguyễn H&agrave; Bắc, Ph&oacute; Gi&aacute;m đốc Sở C&ocirc;ng thương n&ecirc;u quan điểm, Đ&agrave; Nẵng l&agrave; th&agrave;nh phố du lịch, tất cả c&aacute;c cửa h&agrave;ng, cửa hiệu đều c&oacute; quyền phục vụ kh&aacute;ch. Hiện, th&agrave;nh phố c&oacute; khoảng v&agrave;i chục cửa h&agrave;ng chuy&ecirc;n b&aacute;n cho c&aacute;c tour kh&aacute;ch du lịch (c&oacute; kh&aacute;ch nước ngo&agrave;i) với số lượng h&agrave;ng h&oacute;a lớn.</p>\r\n\r\n<p>&ldquo;Về mặt quản l&yacute; Nh&agrave; nước của Sở C&ocirc;ng thương, thời gian qua, ch&uacute;ng t&ocirc;i đ&atilde; chỉ đạo tập trung v&agrave;o kiểm tra c&aacute;c dịch vụ chở kh&aacute;ch, c&aacute;c cửa h&agrave;ng, cửa hiệu kinh doanh, b&aacute;n h&agrave;ng c&oacute; ni&ecirc;m yết gi&aacute;, b&aacute;n đ&uacute;ng gi&aacute; ni&ecirc;m yết, h&agrave;ng h&oacute;a c&oacute; nguồn gốc xuất xứ. Qua đ&oacute;, ch&uacute;ng t&ocirc;i đ&atilde; ph&aacute;t hiện v&agrave; xử phạt một số đơn vị vi phạm về c&aacute;c quy định tr&ecirc;n&rdquo;, &ocirc;ng Bắc n&oacute;i.</p>\r\n\r\n<p>Đối với cơ quan thuế, theo &ocirc;ng Nguyễn Đ&igrave;nh &Acirc;n, Cục trưởng Cục Thuế th&agrave;nh phố, qua kiểm tra, đ&atilde; ph&aacute;t hiện v&agrave; xử l&yacute; một số đơn vị, cửa h&agrave;ng li&ecirc;n quan đến kh&aacute;ch nước ngo&agrave;i c&oacute; dấu hiệu sai phạm, chủ yếu b&aacute;n h&agrave;ng kh&ocirc;ng xuất h&oacute;a đơn, k&ecirc; khai doanh thu kh&ocirc;ng đ&uacute;ng thực tế&hellip; Cục Thuế đ&atilde; chỉ đạo c&aacute;c chi cục thuế đặc biệt ch&uacute; &yacute; đến c&ocirc;ng t&aacute;c kiểm tra, kiểm so&aacute;t để bảo đảm tu&acirc;n thủ luật thuế.</p>', 'Sự phát triển ngày càng mạnh của loại hình tour du lịch giá rẻ (tour 0 đồng) kéo theo một số vấn đề nảy sinh như thanh toán bằng ngoại tệ hay du khách và người bán tự thỏa thuận với nhau bằng hình thức thanh toán trực tuyến, không thông qua các ngân hàng của Việt Nam…', '5', '5', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/9WPwc8D9EbRstenOr7pQdToGUExV9CmZE033jQoZ.gif', 0, '2018-08-05 20:26:59', '2018-08-05 21:21:38'),
(43, 'Học thuyết trình để gọi vốn khởi nghiệp', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Một dự án khởi nghiệp thuyết trình trước các chuyên gia và nhà đầu tư tại Hội nghị và Triển lãm Khởi nghiệp Đà Nẵng SURF 2018.\" src=\"http://baodanang.vn/dataimages/201808/original/images1462784_Hoc_thuyet_khoi_nghiep.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Một dự &aacute;n khởi nghiệp thuyết tr&igrave;nh trước c&aacute;c chuy&ecirc;n gia v&agrave; nh&agrave; đầu tư tại Hội nghị v&agrave; Triển l&atilde;m Khởi nghiệp Đ&agrave; Nẵng SURF 2018.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tại cuộc thi &ldquo;Thuyết tr&igrave;nh khởi nghiệp&rdquo; trong khu&ocirc;n khổ Hội nghị v&agrave; Triển l&atilde;m Khởi nghiệp SURF 2017 tại Đ&agrave; Nẵng, dự &aacute;n Homecares (chăm s&oacute;c sức khỏe tiện &iacute;ch) gi&agrave;nh được giải thưởng của S&aacute;ng kiến Kinh doanh Mekong thuộc Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute;. Đ&acirc;y cũng ch&iacute;nh l&agrave; bước ngoặt gi&uacute;p dự &aacute;n n&agrave;y tiếp tục chinh phục c&aacute;c nh&agrave; đầu tư; đồng thời, mở rộng mạng lưới đối t&aacute;c đến c&aacute;c bệnh viện, ph&ograve;ng kh&aacute;m tr&ecirc;n to&agrave;n địa b&agrave;n th&agrave;nh phố.</p>\r\n\r\n<p>Chị Nguyễn Thị V&acirc;n Anh, Gi&aacute;m đốc C&ocirc;ng ty CP Sức khỏe Homecares chia sẻ, kỹ năng thuyết tr&igrave;nh của những người s&aacute;ng lập đ&oacute;ng vai tr&ograve; lớn trong mỗi chặng đường của dự &aacute;n. Bắt đầu bằng việc tham gia những cuộc thi khởi nghiệp của địa phương, chị cho rằng, nếu c&oacute; sản phẩm tốt m&agrave; kh&ocirc;ng biết c&aacute;ch diễn đạt th&igrave; rất dễ bị đ&aacute;nh gi&aacute; thấp v&agrave; tuột mất cơ hội nhận được đầu tư.</p>\r\n\r\n<p>&ldquo;Homecares đ&atilde; từng tham gia nhiều cuộc thi, nhưng lần n&agrave;o đi thi về cũng cảm thấy m&igrave;nh l&agrave;m chưa đủ tốt. Mỗi lần như vậy, cả nh&oacute;m ngồi xem lại thật kỹ từ đầu đến cuối phần thuyết tr&igrave;nh v&agrave; trả lời c&acirc;u hỏi của m&igrave;nh để tự r&uacute;t kinh nghiệm&rdquo;, chị Anh n&oacute;i.</p>\r\n\r\n<p>Theo chị Anh, b&agrave;i thuyết tr&igrave;nh trong c&aacute;c cuộc thi khởi nghiệp thường đi theo một khung cơ bản, bao gồm việc giới thiệu bản th&acirc;n, giới thiệu dự &aacute;n, n&ecirc;u l&yacute; do khởi nghiệp, c&aacute;c vấn đề m&agrave; dự &aacute;n sẽ giải quyết, giải ph&aacute;p dự &aacute;n đưa ra, c&aacute;c th&ocirc;ng tin về thị trường, định hướng ph&aacute;t triển&hellip;</p>\r\n\r\n<p>V&igrave; c&oacute; khung sẵn n&ecirc;n dự &aacute;n n&agrave;o đi thi cũng chuẩn bị kỹ c&agrave;ng cho phần thuyết tr&igrave;nh, ri&ecirc;ng phần hỏi - đ&aacute;p lại kh&oacute; chuẩn bị nhất, bởi kh&ocirc;ng biết gi&aacute;m khảo sẽ hỏi c&acirc;u n&agrave;o. Hơn nữa, thời lượng trả lời mỗi c&acirc;u hỏi thường chỉ từ 30 gi&acirc;y đến 1 ph&uacute;t, nếu kh&ocirc;ng biết sắp xếp th&igrave; sẽ dễ rơi v&agrave;o t&igrave;nh trạng c&oacute; qu&aacute; nhiều &yacute; để n&oacute;i, m&agrave; chọn tới chọn lui kh&ocirc;ng n&oacute;i được &yacute; n&agrave;o.</p>\r\n\r\n<p>Muốn vượt qua được thử th&aacute;ch n&agrave;y, kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c ngo&agrave;i việc phải hiểu thật kỹ về dự &aacute;n của m&igrave;nh, chuẩn bị trước những c&acirc;u hỏi c&oacute; thể được hỏi.</p>\r\n\r\n<p>Kỹ năng thuyết tr&igrave;nh thường mang t&iacute;nh quyết định trong c&aacute;c cuộc thi khởi nghiệp. Trong cuộc thi Startup Runway 2018 được tổ chức v&agrave;o th&aacute;ng 6 vừa qua tại Đ&agrave; Nẵng, dự &aacute;n JustOrder của một nh&oacute;m sinh vi&ecirc;n năm 1, Trường ĐH Kinh tế (ĐH Đ&agrave; Nẵng) xuất sắc vượt qua 5 dự &aacute;n kh&aacute;c đến từ c&aacute;c tỉnh, th&agrave;nh phố miền Trung để gi&agrave;nh giải qu&aacute;n qu&acirc;n.</p>\r\n\r\n<p>Trương Nguy&ecirc;n Thảo, Trưởng nh&oacute;m dự &aacute;n, được đ&aacute;nh gi&aacute; g&acirc;y ấn tượng nhất cuộc thi khi thực hiện b&agrave;i thuyết tr&igrave;nh thuyết phục, tự tin bằng tiếng Anh trước ban gi&aacute;m khảo, g&oacute;p phần kh&ocirc;ng nhỏ gi&uacute;p cả nh&oacute;m đạt th&agrave;nh t&iacute;ch cao.</p>\r\n\r\n<p>Thảo b&agrave;y tỏ: &ldquo;Trong thời gian chuẩn bị cho v&ograve;ng chung kết, nh&oacute;m đ&atilde; tập luyện thuyết tr&igrave;nh rất nhiều. Để soạn b&agrave;i tr&igrave;nh chiếu, em phải t&igrave;m ra được điểm khiến m&igrave;nh th&iacute;ch về dự &aacute;n rồi tập trung v&agrave;o điểm đ&oacute;. Bởi v&igrave;, muốn khiến người kh&aacute;c hứng th&uacute; với điều m&igrave;nh n&oacute;i th&igrave; bản th&acirc;n m&igrave;nh phải hứng th&uacute; trước đ&atilde;. Sau đ&oacute;, ng&agrave;y n&agrave;o em cũng mở b&agrave;i ra, canh thời gian rồi n&oacute;i đi n&oacute;i lại nhiều lần, chỉnh sửa c&acirc;u từ sao cho gợi nhớ nhất&rdquo;.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ thuyết tr&igrave;nh trong c&aacute;c cuộc thi, c&aacute;c nh&agrave; khởi nghiệp c&ograve;n phải r&egrave;n luyện kỹ năng của m&igrave;nh khi tương t&aacute;c trực tiếp với c&aacute;c nh&agrave; đầu tư. Tr&ecirc;n thực tế, kh&oacute; c&oacute; nh&agrave; đầu tư n&agrave;o quyết định r&oacute;t vốn ngay sau một cuộc thi thuyết tr&igrave;nh c&ocirc;ng khai.</p>\r\n\r\n<p>Thuyết tr&igrave;nh trong cuộc thi chỉ l&agrave; bước đầu, gi&uacute;p dự &aacute;n khởi nghiệp g&acirc;y được ấn tượng. Sau đ&oacute;, nh&agrave; đầu tư thường sẽ hẹn gặp ri&ecirc;ng người s&aacute;ng lập dự &aacute;n để tiếp tục t&igrave;m hiểu. C&aacute;c buổi trao đổi chuy&ecirc;n s&acirc;u như vậy cũng rất cần đến kỹ năng &ldquo;ăn n&oacute;i&rdquo;.</p>\r\n\r\n<p>Với kinh nghiệm từ những lần gọi vốn của Homecares, chị V&acirc;n Anh cho biết, trước khi đến gặp nh&agrave; đầu tư, cần t&igrave;m hiểu kỹ lịch sử v&agrave; c&aacute;c hạng mục đầu tư của những người c&oacute; khả năng r&oacute;t vốn cho dự &aacute;n của m&igrave;nh.</p>\r\n\r\n<p>Tại c&aacute;c buổi trao đổi chuy&ecirc;n s&acirc;u n&agrave;y, nh&agrave; đầu tư sẽ hỏi những c&acirc;u thực tế, t&igrave;m hiểu kỹ về cả điểm mạnh, điểm yếu lẫn những th&ocirc;ng tin kh&ocirc;ng c&ocirc;ng khai của dự &aacute;n. Điều quan trọng l&agrave; phải hiểu rất r&otilde; m&igrave;nh đang l&agrave;m những việc g&igrave;, đang cần c&aacute;i g&igrave;, v&agrave; phải trung thực.</p>\r\n\r\n<p>Trong buổi trao đổi với c&aacute;c dự &aacute;n khởi nghiệp tại Vườn ươm S&ocirc;ng H&agrave;n &ndash; Đ&agrave; Nẵng, Gi&aacute;m đốc điều h&agrave;nh Traptap Bryce North cho hay, đ&ocirc;i l&uacute;c, c&aacute;c nh&agrave; khởi nghiệp chỉ c&oacute; một v&agrave;i gi&acirc;y ph&uacute;t ngắn ngủi khi tham gia c&aacute;c sự kiện kết nối, hay thậm ch&iacute; l&agrave; đứng cạnh nh&agrave; đầu tư trong thang m&aacute;y. Trong ho&agrave;n cảnh đ&oacute;, phải c&oacute; kỹ năng nắm bắt cơ hội, trong thời gian ngắn nhất đưa ra đầy đủ th&ocirc;ng tin thuyết phục nh&agrave; đầu tư.</p>\r\n\r\n<p>&Ocirc;ng Bryce North gợi &yacute;: &ldquo;H&atilde;y hiểu kỹ về dự &aacute;n của m&igrave;nh, tập n&oacute;i về t&ecirc;n dự &aacute;n, sản phẩm của dự &aacute;n, điểm nổi bật của sản phẩm, nh&oacute;m sẽ thực hiện dự &aacute;n l&agrave; những ai v&agrave; họ sẽ l&agrave;m g&igrave; trong dự &aacute;n, những viện dẫn thực tế chứng minh t&iacute;nh khả thi của dự &aacute;n, một v&agrave;i lưu &yacute; cho nh&agrave; đầu tư nếu chọn hay kh&ocirc;ng chọn dự &aacute;n&hellip; Tất cả chỉ trong khoảng thời gian một chiếc thang m&aacute;y đi từ tầng 1 l&ecirc;n tầng 5 chẳng hạn&rdquo;.</p>\r\n\r\n<p>Phụ tr&aacute;ch mảng đầu tư v&agrave; thị trường của C&ocirc;ng ty TNHH MTV Ylinkee, anh Phạm Đức Linh (đồng s&aacute;ng lập c&ocirc;ng ty) cho biết, lỗi thường gặp của c&aacute;c bạn trẻ khởi nghiệp khi thuyết tr&igrave;nh với nh&agrave; đầu tư l&agrave; qu&aacute; tập trung v&agrave;o t&iacute;nh năng của sản phẩm dự &aacute;n, m&agrave; kh&ocirc;ng n&ecirc;u được sản phẩm n&agrave;y sẽ giải quyết nhu cầu n&agrave;o của thị trường, đ&aacute;nh v&agrave;o đối tượng kh&aacute;ch h&agrave;ng n&agrave;o, chiến lược marketing, kế hoạch t&agrave;i ch&iacute;nh ra sao. Ngo&agrave;i ra, cần hiểu rằng đầu tư l&agrave; một sự hợp t&aacute;c, v&igrave; vậy, cần t&igrave;m hiểu r&otilde; nh&agrave; đầu tư l&agrave; ai, họ c&oacute; thể gi&uacute;p được g&igrave; cho dự &aacute;n của m&igrave;nh ngo&agrave;i vấn đề t&agrave;i ch&iacute;nh.</p>\r\n\r\n<p>&ldquo;Người khởi nghiệp phải l&agrave; người hiểu r&otilde; dự &aacute;n của m&igrave;nh hơn ai hết. Họ biết cốt l&otilde;i của dự &aacute;n nằm ở đ&acirc;u, nguồn lực hiện c&oacute; của dự &aacute;n, nguồn lực họ đang k&ecirc;u gọi. Kh&ocirc;ng n&ecirc;n thổi phồng c&aacute;c con số của m&igrave;nh, bởi đa phần c&aacute;c nh&agrave; đầu tư đều l&agrave; những người c&oacute; kinh nghiệm v&agrave; sẽ nhận ra sự kh&ocirc;ng trung thực&rdquo;, anh Linh n&oacute;i.</p>', 'Đối với những người khởi nghiệp, kỹ năng thuyết trình rất quan trọng và cần được rèn luyện, bởi chính từ câu chuyện và cách kể chuyện sẽ kêu gọi được vốn từ nhà đầu tư.', '5', '5', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/GFhUwRJFQa3EsKwpGdIY64wRM3fVYLqjGMebBvBi.jpeg', 0, '2018-08-05 20:28:07', '2018-08-05 21:21:47'),
(44, 'Đẩy nhanh tiến độ thi công trường học phục vụ năm học mới', '<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Phó Chủ tịch Thường trực UBND thành phố Đặng Việt Dũng (thứ hai, từ trái qua) kiểm tra thực tế, đôn đốc tiến độ thi công các công trình trường học.                     Ảnh: THU THẢO\" src=\"http://baodanang.vn/dataimages/201808/original/images1463513_Dang_Viet_Dung_1.gif\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ph&oacute; Chủ tịch Thường trực UBND th&agrave;nh phố Đặng Việt Dũng (thứ hai, từ tr&aacute;i qua) kiểm tra thực tế, đ&ocirc;n đốc tiến độ thi c&ocirc;ng c&aacute;c c&ocirc;ng tr&igrave;nh trường học. Ảnh: THU THẢO</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Tại c&aacute;c Trường THCS Hồ Nghinh (quận Hải Ch&acirc;u), Trường tiểu học T&ocirc;n Đức Thắng (quận Cẩm Lệ), Trường mầm non, khu vui chơi v&agrave; s&acirc;n thể thao khu vực Kh&aacute;nh Sơn (quận Li&ecirc;n Chiểu) v&agrave; Trường THCS Đỗ Đăng Tuyển (quận Thanh Kh&ecirc;), Ph&oacute; Chủ tịch Thường trực Đặng Việt Dũng kiểm tra cơ sở vật chất, đặc biệt l&agrave; c&aacute;c ph&ograve;ng bộ m&ocirc;n, ph&ograve;ng chức năng, ph&ograve;ng vệ sinh; đồng thời nghe c&aacute;c trường b&aacute;o c&aacute;o về số lượng tuyển sinh, những kh&oacute; khăn, vướng mắc, kiến nghị trong qu&aacute; tr&igrave;nh thi c&ocirc;ng, giao mặt bằng...</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Thường trực Đặng Việt Dũng y&ecirc;u cầu c&aacute;c trường v&agrave; UBND c&aacute;c phường, quận, Ph&ograve;ng GD&amp;ĐT đẩy nhanh tiến độ để kịp phục vụ khai giảng năm học mới. Đối với c&aacute;c c&ocirc;ng tr&igrave;nh trường học đ&atilde; ho&agrave;n th&agrave;nh, ngo&agrave;i cơ sở vật chất, c&aacute;c trường cần chuẩn bị đầy đủ trang thiết bị, đồ d&ugrave;ng dạy học, ch&uacute; trọng c&ocirc;ng t&aacute;c tuyển sinh, kiểm tra chất lượng gi&aacute;o vi&ecirc;n, chuẩn bị chương tr&igrave;nh giảng dạy... để bảo đảm tốt nhất cho năm học mới.</p>\r\n\r\n<p>&Ocirc;ng Đặng Việt Dũng đề nghị Sở GD&amp;ĐT hướng dẫn, chỉ đạo c&aacute;c Ph&ograve;ng GD&amp;ĐT tập trung x&acirc;y dựng c&aacute;c trường mầm non, đặc biệt l&agrave; trường cho trẻ dưới 24 th&aacute;ng tuổi, giảm c&aacute;c lớp học gia đ&igrave;nh. Sau ng&agrave;y 5-9, c&aacute;c trường, Ph&ograve;ng GD&amp;ĐT c&aacute;c quận phải b&aacute;o c&aacute;o tổng số tuyển sinh theo chỉ ti&ecirc;u, nắm t&igrave;nh h&igrave;nh học sinh thừa, thiếu để c&oacute; kế hoạch cụ thể trong việc x&acirc;y dựng trường. Sở GD&amp;ĐT cần tăng cường kiểm tra bữa ăn b&aacute;n tr&uacute; của học sinh, bảo đảm bữa ăn đầy đủ dinh dưỡng v&agrave; an to&agrave;n vệ sinh thực phẩm.</p>\r\n\r\n<p>Theo Sở GD&amp;ĐT, trong năm 2018, to&agrave;n th&agrave;nh phố c&oacute; 98 c&ocirc;ng tr&igrave;nh trường học được x&acirc;y dựng, sửa chữa. Trong đ&oacute;, Sở GD&amp;ĐT l&agrave;m chủ đầu tư 45 c&ocirc;ng tr&igrave;nh (đ&atilde; ho&agrave;n th&agrave;nh 16 c&ocirc;ng tr&igrave;nh, đang triển khai 29 c&ocirc;ng tr&igrave;nh). UBND c&aacute;c quận, huyện l&agrave;m chủ đầu tư 46 c&ocirc;ng tr&igrave;nh (đ&atilde; ho&agrave;n th&agrave;nh 26 c&ocirc;ng tr&igrave;nh, đang triển khai 20 c&ocirc;ng tr&igrave;nh).</p>\r\n\r\n<p>C&aacute;c ban quản l&yacute; dự &aacute;n th&agrave;nh phố l&agrave;m chủ đầu tư 7 c&ocirc;ng tr&igrave;nh (đ&atilde; ho&agrave;n th&agrave;nh 1 c&ocirc;ng tr&igrave;nh, đang triển khai 6 c&ocirc;ng tr&igrave;nh). Tổng kinh ph&iacute; đầu tư 266 tỷ đồng.</p>\r\n\r\n<p>Năm học 2018-2019, c&oacute; 5 trường được th&agrave;nh lập mới gồm: Trường mầm non Ban Mai Xanh (huyện H&ograve;a Vang), Trường mầm non H&ograve;a Kh&ecirc; (quận Thanh Kh&ecirc;), Trường tiểu học L&ecirc; Lai 2 (quận Ngũ H&agrave;nh Sơn), Trường THCS Nguyễn Văn Thoại v&agrave; Trường THPT Sơn Tr&agrave; (quận Sơn Tr&agrave;).</p>', 'Ngày 5-8, Phó Chủ tịch Thường trực UBND thành phố Đặng Việt Dũng kiểm tra thực tế các công trình trường học trên địa bàn thành phố.', '5', '6', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/oI1SjDhRp8hgirjbJdxgJxQQpJxbOigF4abx1UlX.gif', 0, '2018-08-05 21:23:31', '2018-08-05 21:37:31'),
(45, 'Những điều thí sinh cần biết sau khi biết điểm chuẩn đại học năm 2018', '<p>Sau khi biết điểm chuẩn x&eacute;t tuyển v&agrave;o c&aacute;c ng&agrave;nh của trường đại học (ĐH), th&iacute; sinh cần lưu &yacute; những việc l&agrave;m cần thiết sau:</p>\r\n\r\n<p>Từ ng&agrave;y 7-8 đến trước 17h ng&agrave;y 12-8: Th&iacute; sinh x&aacute;c nhận nhập học đợt 1 ở c&aacute;c trường đại học. Th&iacute; sinh cần xem kỹ hướng dẫn của c&aacute;c trường để x&aacute;c nhận nhập học đ&uacute;ng thời hạn. Đặc biệt, một số trường cho ph&eacute;p x&aacute;c nhận nhập học trực tuyến qua website. Do đ&oacute;, th&iacute; sinh ch&uacute; &yacute; hướng dẫn nhập học sau khi x&aacute;c nhận nhập học th&agrave;nh c&ocirc;ng.&nbsp;</p>\r\n\r\n<table align=\"center\" style=\"width:490px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Sau khi biết điểm chuẩn xét tuyển vào các ngành của trường đại học, thí sinh cần lưu ý xác nhận nhập học đúng thời hạn (ảnh minh họa)\" src=\"http://baodanang.vn/dataimages/201808/original/images1463493_nhap_hoc_vov_iekn.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Sau khi biết điểm chuẩn x&eacute;t tuyển v&agrave;o c&aacute;c ng&agrave;nh của trường đại học, th&iacute; sinh cần lưu &yacute; x&aacute;c nhận nhập học đ&uacute;ng thời hạn (ảnh minh họa)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trước 17h ng&agrave;y 16-8: C&aacute;c trường ĐH, Cao đẳng sư phạm, Trung cấp sư phạm cập nhật th&ocirc;ng tin th&iacute; sinh x&aacute;c nhận nhập học v&agrave;o cơ sở dữ liệu của Cổng th&ocirc;ng tin tuyển sinh của Bộ GD-ĐT.</p>\r\n\r\n<p>Từ ng&agrave;y 22-8: C&aacute;c trường ĐH, Cao đẳng sư phạm, Trung cấp sư phạm x&eacute;t tuyển bổ sung nếu c&ograve;n chỉ ti&ecirc;u (c&aacute;c trường chủ động c&ocirc;ng bố lịch tuyển sinh bổ sung trước 10 ng&agrave;y so với ng&agrave;y x&eacute;t tuyển).</p>\r\n\r\n<p>Th&iacute; sinh cần lưu &yacute; xem th&ocirc;ng tin x&eacute;t tuyển bổ sung tr&ecirc;n website của c&aacute;c trường. Th&iacute; sinh chỉ đăng k&yacute; x&eacute;t tuyển bổ sung khi chưa x&aacute;c nhận nhập học v&agrave;o c&aacute;c trường kh&aacute;c ở đợt&nbsp;</p>', 'Sau khi biết điểm chuẩn xét tuyển vào các ngành của trường đại học, thí sinh cần lưu ý xác nhận nhập học đúng thời hạn.', '5', '6', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/4Rz6TLtt4K1shYduCmmCv6rnHCjREpsu0Ij1QgaD.jpeg', 0, '2018-08-05 21:24:53', '2018-08-05 21:37:39'),
(46, 'Công bố điểm trúng tuyển vào các trường thành viên Đại học Đà Nẵng', '<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Thí sinh tìm hiểu thông tin về các trường thuộc Đại học Đà Nẵng để đăng ký xét tuyển\" src=\"http://baodanang.vn/dataimages/201808/original/images1463498_a.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Th&iacute; sinh t&igrave;m hiểu th&ocirc;ng tin về c&aacute;c trường thuộc Đại học Đ&agrave; Nẵng để đăng k&yacute; x&eacute;t tuyển</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ở Trường ĐH Sư phạm, điểm chuẩn c&aacute;c ng&agrave;nh từ 15,5 - 22,25 điểm, giảm từ 0,5 đến 2 điểm so với năm trước.</p>\r\n\r\n<p>Cao nhất l&agrave; ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin đặc th&ugrave; với 22,25 điểm. Ng&agrave;nh Địa l&yacute; học, Việt Nam học, C&ocirc;ng t&aacute;c x&atilde; hội, C&ocirc;ng nghệ sinh học, Ho&aacute; học, C&ocirc;ng nghệ th&ocirc;ng tin đều c&oacute; điểm chuẩn l&agrave; 15 điểm, thấp nhất trong c&aacute;c ng&agrave;nh.</p>\r\n\r\n<p>Ở Trường ĐH Ngoại ngữ, điểm chuẩn c&aacute;c ng&agrave;nh từ 16,69 - 21,95 điểm, giảm từ 2 đến 3 điểm so với năm trước.</p>\r\n\r\n<p>Cụ thể, cao nhất ở ng&agrave;nh Sư phạm tiếng Anh với 21,95 điểm. Thấp nhất l&agrave; ng&agrave;nh Ng&ocirc;n ngữ Nga với 16,69 điểm.</p>\r\n\r\n<p>Ở Trường ĐH B&aacute;ch khoa, điểm chuẩn c&aacute;c ng&agrave;nh từ 15 - 23 điểm, giảm từ 1 đến 3 điểm so với năm trước. Trong đ&oacute; cao nhất l&agrave; ng&agrave;nh Kỹ thuật x&acirc;y dựng c&ocirc;ng tr&igrave;nh giao th&ocirc;ng chất lượng cao v&agrave; c&ocirc;ng nghệ th&ocirc;ng tin với 23 điểm, thấp nhất l&agrave; ng&agrave;nh Kỹ thuật cơ kh&iacute; (chuy&ecirc;n ng&agrave;nh Cơ kh&iacute; động lực-Chất lượng cao), C&ocirc;ng nghệ dầu kh&iacute; v&agrave; khai th&aacute;c dầu (Chất lượng cao) lấy 15 điểm.</p>\r\n\r\n<p>Ở Trường ĐH Kinh tế, điểm chuẩn c&aacute;c ng&agrave;nh từ 17,5 - 21,25 điểm, giảm từ 2,5 đến 3 điểm so với năm trước. Ng&agrave;nh Kinh doanh quốc tế c&oacute; điểm chuẩn tr&uacute;ng tuyển cao nhất với 21,25 điểm. Ng&agrave;nh Quản l&yacute; nh&agrave; nước, Hệ thống th&ocirc;ng tin quản l&yacute;, Thống k&ecirc; kinh tế c&oacute; điểm chuẩn 17,5 điểm, thấp nhất trong c&aacute;c ng&agrave;nh.</p>\r\n\r\n<p>Ở Trường ĐH Sư phạm kỹ thuật, điểm chuẩn c&aacute;c ng&agrave;nh từ 14 - 17,50 điểm. Ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin, C&ocirc;ng nghệ kỹ thuật &ocirc;-t&ocirc; c&oacute; điểm chuẩn tuyển cao nhất với 17,50 điểm. Ng&agrave;nh C&ocirc;ng nghệ kỹ thuật m&ocirc;i trường, Kỹ thuật thực phẩm, Kỹ thuật Cơ sở hạ tầng c&oacute; điểm chuẩn l&agrave; 14 điểm, thấp nhất trong c&aacute;c ng&agrave;nh.</p>\r\n\r\n<p>Khoa Y dược thuộc Đại học Đ&agrave; Nẵng năm nay đ&agrave;o tạo 5 ng&agrave;nh với mức điểm chuẩn l&agrave; Y khoa (22,15 điểm) v&agrave; Điều dưỡng (18,15 điểm), Răng-H&agrave;m-Mặt (22,05 điểm), Dược học tổ hợp B00 (21,45 điểm), Dược học tổ hợp A00 (21,80 điểm).</p>\r\n\r\n<p>Viện Nghi&ecirc;n cứu v&agrave; Đ&agrave;o tạo Việt - Anh đ&agrave;o tạo 3 ng&agrave;nh với mức điểm chuẩn l&agrave; Khoa học y sinh (15,55 điểm), Khoa học kỹ thuật m&aacute;y t&iacute;nh (15,35 điểm) v&agrave; Quản trị v&agrave; kinh doanh quốc tế (16 điểm).</p>\r\n\r\n<p>Ở Ph&acirc;n hiệu Đại học Đ&agrave; Nẵng tại Kon Tum, điểm chuẩn c&aacute;c ng&agrave;nh từ 14 - 20 điểm.</p>\r\n\r\n<p>Khoa C&ocirc;ng nghệ th&ocirc;ng tin v&agrave; truyền th&ocirc;ng thuộc Đại học Đ&agrave; Nẵng năm nay tuyển sinh 4 ng&agrave;nh với điểm chuẩn cụ thể: C&ocirc;ng nghệ th&ocirc;ng tin (17 điểm), C&ocirc;ng nghệ th&ocirc;ng tin đ&agrave;o tạo đặc th&ugrave; (16,65 điểm), C&ocirc;ng nghệ kỹ thuật m&aacute;y t&iacute;nh (15,50 điểm), Quản trị kinh doanh (16 điểm).</p>\r\n\r\n<p>Phụ huynh v&agrave; học sinh c&oacute; thể xem chi tiết điểm chuẩn c&aacute;c ng&agrave;nh của c&aacute;c trường&nbsp;<a href=\"http://baodanang.vn/upload/others/201808/17213_Cong_bo_diem_trung_tuyen_CQ_dot_1_CHINH_THUC_V2.xls\">TẠI Đ&Acirc;Y</a>&nbsp; &nbsp;</p>\r\n\r\n<p>*&nbsp;<strong>Sau khi biết điểm chuẩn x&eacute;t tuyển v&agrave;o c&aacute;c ng&agrave;nh của trường đại học, th&iacute; sinh cần lưu &yacute; x&aacute;c nhận nhập học đ&uacute;ng thời hạn; lưu &yacute; những việc cần thiết sau:</strong></p>\r\n\r\n<p>Từ ng&agrave;y 7-8 đến trước 17 giờ ng&agrave;y 12-8: Th&iacute; sinh x&aacute;c nhận nhập học đợt 1 ở c&aacute;c trường đại học. Th&iacute; sinh cần xem kỹ hướng dẫn của c&aacute;c trường để x&aacute;c nhận nhập học đ&uacute;ng thời hạn. Đặc biệt, một số trường cho ph&eacute;p x&aacute;c nhận nhập học trực tuyến qua website. Do đ&oacute;, th&iacute; sinh ch&uacute; &yacute; hướng dẫn nhập học sau khi x&aacute;c nhận nhập học th&agrave;nh c&ocirc;ng.&nbsp;</p>\r\n\r\n<p>Trước 17 giờ ng&agrave;y 16-8: C&aacute;c trường ĐH, Cao đẳng sư phạm, Trung cấp sư phạm cập nhật th&ocirc;ng tin th&iacute; sinh x&aacute;c nhận nhập học v&agrave;o cơ sở dữ liệu của Cổng th&ocirc;ng tin tuyển sinh của Bộ GD-ĐT.</p>\r\n\r\n<p>Từ ng&agrave;y 22-8: C&aacute;c trường ĐH, Cao đẳng sư phạm, Trung cấp sư phạm x&eacute;t tuyển bổ sung nếu c&ograve;n chỉ ti&ecirc;u (c&aacute;c trường chủ động c&ocirc;ng bố lịch tuyển sinh bổ sung trước 10 ng&agrave;y so với ng&agrave;y x&eacute;t tuyển).</p>\r\n\r\n<p>Th&iacute; sinh cần lưu &yacute; xem th&ocirc;ng tin x&eacute;t tuyển bổ sung tr&ecirc;n website của c&aacute;c trường. Th&iacute; sinh chỉ đăng k&yacute; x&eacute;t tuyển bổ sung khi chưa x&aacute;c nhận nhập học v&agrave;o c&aacute;c trường kh&aacute;c ở đợt 1.</p>', 'ĐNO - Ngày 5-8, các trường thành viên của Đại học (ĐH) Đà Nẵng công bố điểm trúng tuyển năm 2018 theo kết quả thi THPT quốc gia. Nhìn chung, điểm xét tuyển ở các ngành đều thấp hơn năm ngoái từ 0,5 đến 3 điểm.', '5', '6', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/3CzDz0iaNQshZ1cTjICUQWf6ARlt5LraiNOXEzM3.jpeg', 0, '2018-08-05 21:26:19', '2018-08-05 21:38:30'),
(47, 'Madonna gây quỹ vì trẻ em Malawi', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Ca sĩ Madonna mừng sinh nhật lần thứ 12 của David Banda, một người con gốc Malawi của bà. Ảnh: Footwearnews\" src=\"http://baodanang.vn/dataimages/201808/original/images1463086_tre_em.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ca sĩ Madonna mừng sinh nhật lần thứ 12 của David Banda, một người con gốc Malawi của b&agrave;. Ảnh: Footwearnews</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Chỉ trong 24 giờ đầu sau khi nữ nghệ sĩ đa t&agrave;i k&ecirc;u gọi ủng hộ tr&ecirc;n trang Facebook c&aacute; nh&acirc;n v&agrave;o dịp kỷ niệm sinh nhật lần thứ 60 (ng&agrave;y 16-8), quỹ tr&ecirc;n nhận được số tiền quy&ecirc;n g&oacute;p gần 11.000 USD từ hơn 200 nh&agrave; hảo t&acirc;m. Chiến dịch g&acirc;y quỹ sẽ k&eacute;o d&agrave;i trong cả th&aacute;ng 8.</p>\r\n\r\n<p>Danh ca của album&nbsp;&ldquo;Rebel Heart&rdquo;&nbsp;(Con tim nổi loạn) nhận nu&ocirc;i 4 trẻ nhỏ mang quốc tịch Malawi trong 10 năm qua. B&agrave; cũng cam kết 100% số tiền quy&ecirc;n g&oacute;p sẽ trực tiếp được d&agrave;nh cho trại trẻ mồ c&ocirc;i ở n&ocirc;ng th&ocirc;n mang t&ecirc;n Ng&ocirc;i nh&agrave; Hy vọng, thuộc tổ chức từ thiện Raising Malawi của b&agrave;. Số tiền n&agrave;y sẽ được chi cho c&aacute;c bữa ăn, chi ph&iacute; học tập, đồng phục v&agrave; chăm s&oacute;c y tế cho c&aacute;c trẻ nhỏ bất hạnh tại đ&acirc;y.</p>\r\n\r\n<p>Năm 2016, Madonna th&agrave;nh lập tổ chức phi lợi nhuận Raising Malawi nhằm mang c&aacute;c chương tr&igrave;nh chăm s&oacute;c sức khỏe v&agrave; gi&aacute;o dục đặc biệt d&agrave;nh cho c&aacute;c b&eacute; g&aacute;i c&oacute; ho&agrave;n cảnh kh&oacute; khăn. Năm ngo&aacute;i, b&agrave; nhận nu&ocirc;i hai b&eacute; g&aacute;i sinh đ&ocirc;i 4 tuổi người Malawi l&agrave; Esther v&agrave; Stella, đồng thời mở một bệnh viện nhi tại Blantyre, th&agrave;nh phố lớn thứ hai ở quốc gia Đ&ocirc;ng Phi n&agrave;y.&nbsp;Gia đ&igrave;nh nữ nghệ sĩ c&ograve;n c&oacute; hai người con đẻ Lourdes v&agrave; Rocco c&ugrave;ng hai người con gốc Malawi kh&aacute;c l&agrave; David Banda v&agrave; Mercy James.</p>\r\n\r\n<p>D&ugrave; đ&atilde; qua thời kỳ đỉnh cao nhưng ca sĩ Madonna vẫn l&agrave; ứng cử vi&ecirc;n đứng đầu trong mọi cuộc chơi &acirc;m nhạc. B&agrave; c&oacute; nhiều ca kh&uacute;c th&agrave;nh c&ocirc;ng vang dội đưa t&ecirc;n tuổi b&agrave; đến với c&ocirc;ng ch&uacute;ng to&agrave;n thế giới. Trong đ&oacute; phải kể đến ca kh&uacute;c &ldquo;Like A Virgin&rdquo;.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; một trong những ca kh&uacute;c nổi tiếng nhất của b&agrave; nằm trong single chủ đạo của album c&ugrave;ng t&ecirc;n ra đời năm 1984. Ca kh&uacute;c từng dẫn đầu nhiều bảng xếp hạng suốt thời gian d&agrave;i v&agrave; l&agrave; sản phẩm tạo n&ecirc;n t&ecirc;n tuổi, dấu ấn của Madonna. Tiếp theo đ&oacute; l&agrave; ca kh&uacute;c &ldquo;Crazy For You&rdquo;, chỉ đứng sau ca kh&uacute;c nổi tiếng &ldquo;We Are The World&rdquo;.</p>\r\n\r\n<p>Trong sự nghiệp ca h&aacute;t của m&igrave;nh, Madonna l&agrave;m thay đổi nền văn h&oacute;a đại ch&uacute;ng bởi sự t&aacute;o bạo trong &acirc;m nhạc v&agrave; h&igrave;nh tượng nữ quyền. B&agrave; c&oacute; gần 40 ca kh&uacute;c đứng trong top 10 bảng xếp hạng Billboard, trong đ&oacute; c&oacute; 12 ca kh&uacute;c đứng vị tr&iacute; thứ nhất.</p>\r\n\r\n<p>Cho đến thời điểm n&agrave;y, vẫn &iacute;t ai c&oacute; thể so s&aacute;nh được biểu tượng văn h&oacute;a Madonna. Ngo&agrave;i ra, b&agrave; c&ograve;n được mệnh danh l&agrave; diva nhạc pop người Mỹ nằm trong danh s&aacute;ch những ca sĩ solo đi tour kiếm tiền giỏi nhất khi bỏ t&uacute;i 1,31 tỷ USD trong những năm qua.</p>\r\n\r\n<p>Theo thống k&ecirc;, tour diễn Rebel Heart v&agrave;o năm 2016 b&aacute;n được hơn 1 triệu v&eacute; tr&ecirc;n to&agrave;n thế giới v&agrave; đem về khoản lợi nhuận 170 triệu USD. Trong tour diễn n&agrave;y, b&agrave; thực hiện 82 chương tr&igrave;nh tại 4 lục địa lớn nhưng chủ yếu tổ chức tại Canada v&agrave; Mỹ.</p>\r\n\r\n<p>B&agrave; cũng d&agrave;nh thời gian biểu diễn tại một số nước ch&acirc;u &Acirc;u v&agrave; ch&acirc;u &Aacute;. Trong đ&oacute;, Nhật Bản l&agrave; quốc gia c&oacute; số lượng người h&acirc;m mộ đ&oacute;n nhận lớn nhất. Mức doanh thu tr&ecirc;n cũng g&oacute;p phần đ&aacute;ng kể v&agrave;o con số hơn 1,31 tỷ USD m&agrave; nữ ca sĩ kiếm được nhờ đi tour từ năm 1990.</p>', '“Không món quà sinh nhật nào tuyệt vời hơn việc kết nối gia đình toàn cầu của tôi với đất nước Malawi tươi đẹp và với những em nhỏ cần sự trợ giúp”. Đó là chia sẻ của nữ ca sĩ nhạc pop Madonna sau khi phát động chiến dịch gây quỹ từ thiện 60.000 USD cho trẻ em tại Malawi, một quốc gia ở Đông Phi.', '5', '8', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/boecSIol2iQpKx4BLYsCxEhjW6r7rp5s1Ssjqt4M.jpeg', 2, '2018-08-05 21:28:27', '2018-08-05 21:45:55'),
(48, 'Di tích Chăm tiêu biểu của Đà Nẵng', '<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Đầu rắn Laga được phát hiện tại di tích Chăm Phong Lệ trong đợt khai quật lần này.\" src=\"http://baodanang.vn/dataimages/201808/original/images1463294_1.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đầu rắn Laga được ph&aacute;t hiện tại di t&iacute;ch Chăm Phong Lệ trong đợt khai quật lần n&agrave;y.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong đợt n&agrave;y, đo&agrave;n khảo cổ Khoa Lịch sử, Trường Đại học Khoa học X&atilde; hội v&agrave; Nh&acirc;n văn (KHXH&amp;NV) H&agrave; Nội tiến h&agrave;nh khai quật tr&ecirc;n diện t&iacute;ch hơn 300m2 tại di t&iacute;ch n&agrave;y. Đến nay, đo&agrave;n ph&aacute;t hiện nhiều hiện vật đ&aacute; c&oacute; gi&aacute; trị như: tượng sư tử Sinha, đầu chim thần Garuda, bệ thờ voi, c&ugrave;ng hiện vật trang tr&iacute; riềng m&aacute;i của th&aacute;p c&oacute; h&igrave;nh rắn Laga v&agrave; kiểu d&aacute;ng hoa văn đặc sắc, gạch x&acirc;y đền th&aacute;p...</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; sơ bộ của Th.S Nguyễn Hữu Mạnh, Bộ m&ocirc;n Khảo cổ học, Khoa Lịch sử, Trường Đại học KHXH&amp;NV H&agrave; Nội, tượng sư tử Sinha l&agrave; một tượng tr&ograve;n, c&oacute; k&iacute;ch thước kh&aacute; lớn, được trang tr&iacute; tinh xảo, đặc biệt ở phần bụng v&agrave; đầu. Đ&acirc;y l&agrave; hiện vật nằm trong địa tầng của hố khai quật n&ecirc;n rất qu&yacute; gi&aacute;, bổ sung v&agrave;o hiện vật trưng b&agrave;y cũng như nghi&ecirc;n cứu về văn h&oacute;a Chăm ở Bảo t&agrave;ng Đi&ecirc;u khắc Chăm.</p>\r\n\r\n<p>Cũng theo &ocirc;ng Mạnh, qua lần khai quật n&agrave;y c&ugrave;ng hai đợt khai quật trước v&agrave;o năm 2011 v&agrave; 2012, c&aacute;c nh&agrave; khảo cổ đ&atilde; xuất lộ hai mặt bằng kiến tr&uacute;c. Kiến tr&uacute;c thứ nhất c&oacute; thể gọi l&agrave; th&aacute;p ch&iacute;nh, trong l&ograve;ng th&aacute;p c&oacute; c&aacute;i hố m&agrave; c&aacute;c nh&agrave; khoa học Việt Nam thường gọi l&agrave; hố thi&ecirc;ng phục vụ t&iacute;n ngưỡng của người Chăm, v&agrave; l&agrave; một kiểu kiến tr&uacute;c được ph&aacute;t hiện rất &iacute;t tại c&aacute;c phế t&iacute;ch, di t&iacute;ch Chăm.</p>\r\n\r\n<p>Hiện tượng n&agrave;y chỉ c&oacute; ở Cấm M&iacute;t (x&atilde; H&ograve;a Phong, huyện H&ograve;a Vang) v&agrave; th&aacute;p Ponagar (tỉnh Kh&aacute;nh H&ograve;a). Kiến tr&uacute;c thứ hai đang khai quật c&oacute; nền m&oacute;ng kiến tr&uacute;c khoảng 16,5 m&eacute;t v&agrave; c&aacute;c nh&agrave; khảo cổ đặt ra giả thiết rằng đ&acirc;y l&agrave; th&aacute;p cổng dẫn v&agrave;o th&aacute;p ch&iacute;nh.</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng t&ocirc;i đ&atilde; tiến h&agrave;nh khảo s&aacute;t v&agrave; nhận ra di t&iacute;ch Chăm Phong Lệ c&ugrave;ng một loạt phế t&iacute;ch kh&aacute;c nằm dọc theo s&ocirc;ng Cẩm Lệ, từ đ&oacute; đưa đến kết luận rằng, khu vực Phong Lệ c&ugrave;ng với khu vực xung quanh đ&atilde; từng tồn tại một tổ hợp c&aacute;c đền th&aacute;p, đ&oacute;ng vai tr&ograve; đặc biệt quan trọng của văn h&oacute;a, lịch sử v&ugrave;ng đất Đ&agrave; Nẵng xưa n&oacute;i ri&ecirc;ng, của miền Trung Việt Nam n&oacute;i chung&rdquo;, &ocirc;ng Mạnh chia sẻ.</p>\r\n\r\n<p>&Ocirc;ng Hồ Tấn Tuấn, Gi&aacute;m đốc Bảo t&agrave;ng Đi&ecirc;u khắc Chăm cũng cho biết, tại bảo t&agrave;ng hiện c&oacute; khu vực ri&ecirc;ng trưng b&agrave;y hiện vật Chăm Phong Lệ c&oacute; gi&aacute; trị như: Linga, b&ograve; thần Nandin (vật cưỡi của thần Siva), tượng sư tử, tượng voi, tượng thần Visnu...</p>\r\n\r\n<p>Hơn 100 năm trước, &ocirc;ng C. Paris, chủ đồn điền Phong Lệ đ&atilde; thu gom những hiện vật n&agrave;y v&agrave; đưa về tập trung tại C&ocirc;ng vi&ecirc;n Tourane (Đ&agrave; Nẵng). Sau đ&oacute;, những t&aacute;c phẩm đi&ecirc;u khắc n&agrave;y được đưa v&agrave;o trưng b&agrave;y tại Bảo t&agrave;ng H. Parmentier (nay l&agrave; Bảo t&agrave;ng Đi&ecirc;u khắc Chăm).</p>\r\n\r\n<p>Nhiều năm sau, di t&iacute;ch n&agrave;y hoang phế v&agrave; cư d&acirc;n về đ&acirc;y sinh sống, x&acirc;y nh&agrave; cửa ngay tr&ecirc;n nền th&aacute;p Chăm. Sau hai lần khai quật v&agrave;o năm 2011 v&agrave; 2012, đến đợt khai quật thứ 3 n&agrave;y, với những hiện vật được ph&aacute;t hiện, &ocirc;ng Hồ Tấn Tuấn cho rằng, điều n&agrave;y c&agrave;ng khẳng định Đ&agrave; Nẵng vẫn c&ograve;n khu phế t&iacute;ch th&aacute;p Chăm c&oacute; gi&aacute; trị đặc biệt, ni&ecirc;n đại tr&ecirc;n 1.000 năm. Bảo t&agrave;ng Đi&ecirc;u khắc Chăm đ&atilde; đề nghị Sở Văn h&oacute;a-Thể thao phải bảo tồn nguy&ecirc;n trạng phế t&iacute;ch n&agrave;y để l&agrave;m điểm tham quan cho người d&acirc;n v&agrave; du kh&aacute;ch.</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng t&ocirc;i sẽ đề nghị xếp hạng di t&iacute;ch Chăm Phong Lệ, trước hết l&agrave; di t&iacute;ch cấp th&agrave;nh phố, sau đ&oacute; nghi&ecirc;n cứu đề xuất cấp quốc gia. Di t&iacute;ch n&agrave;y phải được bảo tồn nguy&ecirc;n trạng theo đề &aacute;n &ldquo;Khảo cổ v&agrave; ph&aacute;t huy gi&aacute; trị khu di t&iacute;ch Chăm Phong Lệ&rdquo;, tr&ecirc;n cơ sở lấy khu vực đền th&aacute;p l&agrave;m trung t&acirc;m v&agrave; mở rộng c&aacute;c khu vực xung quanh l&agrave;m khu trưng b&agrave;y&rdquo;, &ocirc;ng Tuấn cho biết th&ecirc;m.</p>', 'Đến thời điểm hiện tại, các hiện vật được phát hiện tại di tích Chăm Phong Lệ thuộc địa phận tổ 3, phường Hòa Thọ Đông, quận Cẩm Lệ trong đợt khai quật lần thứ 3 (từ ngày 18-7 đến 20-8) được các nhà nghiên cứu đánh giá cao, càng khẳng định giá trị của nền văn hóa Chăm pa từng tồn tại ở vùng đất này.', '5', '8', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/sawPx9eizyNCx9HYfV6GMzeXZeSqx9aYLZgjgV7T.jpeg', 1, '2018-08-05 21:30:02', '2018-08-05 21:46:01'),
(49, 'Đấu giá tranh ủng hộ chương trình \'Thiện Nhân và những người bạn\'', '<table align=\"center\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Tranh vẽ của một số bạn nhỏ tham gia chương trình. (Ảnh: BTC)\" src=\"http://baodanang.vn/dataimages/201808/original/images1462926_Ve_len_co_tich_1.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tranh vẽ của một số bạn nhỏ tham gia chương tr&igrave;nh. (Ảnh: BTC)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>To&agrave;n bộ số tiền thu được sẽ d&agrave;nh tặng Quỹ Thiện Nh&acirc;n v&agrave; những người bạn (chương tr&igrave;nh kh&aacute;m, tư vấn v&agrave; phẫu thuật miễn ph&iacute; t&aacute;i tạo bộ phận sinh dục cho trẻ em tr&ecirc;n khắp cả nước).</p>\r\n\r\n<p>Những bức tranh được đấu gi&aacute; lần n&agrave;y l&agrave; t&aacute;c phẩm của 40 họa sỹ nh&iacute; thực hiện trong buổi vẽ tranh diễn ra ng&agrave;y 22-7 vừa qua tại Trung t&acirc;m s&aacute;ch Kim Đồng. B&ecirc;n cạnh đ&oacute;, một số bạn nhỏ kh&ocirc;ng c&oacute; điều kiện tham dự đ&atilde; gửi tặng những bức tranh đẹp nhất của m&igrave;nh để trưng b&agrave;y v&agrave; b&aacute;n đấu gi&aacute; trong chương tr&igrave;nh sắp tới.</p>\r\n\r\n<table align=\"center\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Họa sỹ Đỗ Hương hướng dẫn c&aacute;c bạn nhỏ &quot;Vẽ l&ecirc;n cổ t&iacute;ch.&quot; (Ảnh: BTC)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đ&acirc;y l&agrave; năm thứ ba chương tr&igrave;nh &ldquo;Vẽ l&ecirc;n cổ t&iacute;ch&rdquo; được thực hiện với th&ocirc;ng điệp &ldquo;Thay v&igrave; ngồi chờ điều kỳ diệu kh&ocirc;ng biết bao giờ xuất hiện, trẻ em ho&agrave;n to&agrave;n c&oacute; thể c&ugrave;ng g&oacute;p sức tạo n&ecirc;n những c&acirc;u chuyện cổ t&iacute;ch đầy t&igrave;nh người.&rdquo;</p>\r\n\r\n<p>&ldquo;Qua hai năm tổ chức, chương tr&igrave;nh đ&atilde; quy&ecirc;n g&oacute;p được 150 triệu đồng từ việc b&aacute;n đấu gi&aacute; t&aacute;c phẩm của những họa sỹ nh&iacute;. C&aacute;c em nhỏ đ&atilde; g&oacute;p phần &lsquo;Vẽ l&ecirc;n cổ t&iacute;ch&rsquo; cho nhiều bạn đồng trang lứa k&eacute;m may mắn, bị khiếm khuyết bộ phận sinh dục,&rdquo; b&agrave; Trần Mai Anh - người đồng s&aacute;ng lập v&agrave; quản l&yacute; Quỹ Thiện Nh&acirc;n v&agrave; những người bạn chia sẻ.</p>\r\n\r\n<p>Từ th&aacute;ng 8-2011, quỹ đ&atilde; phẫu thuật miễn ph&iacute; hơn 200 ca, kh&aacute;m v&agrave; tư vấn miễn ph&iacute; cho hơn 800 trẻ em kh&ocirc;ng may bị khiếm khuyết bộ phận sinh dục. B&ecirc;n cạnh c&aacute;c hoạt động kh&aacute;m chữa bệnh, chương tr&igrave;nh &ldquo;Thiện Nh&acirc;n v&agrave; những người bạn&rdquo; đ&atilde; phối hợp với c&aacute;c bệnh viện tổ chức hội thảo khoa học, trao đổi kinh nghiệm trong lĩnh vực tiết niệu v&agrave; t&aacute;i tạo bộ phận sinh dục.</p>', 'Chương trình đấu giá tranh thiếu nhi “Vẽ lên cổ tích” gây quỹ từ thiện sẽ diễn ra vào sáng 5-8 tại Trung tâm sách Kim Đồng (số 241 Cống Quỳnh, Thành phố Hồ Chí Minh).', '5', '8', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/8s4X3y7DQuVDBfNte50IgE1UelTYKFT165lku4HX.jpeg', 0, '2018-08-05 21:31:27', '2018-08-05 21:38:14');
INSERT INTO `post_lists` (`id`, `title`, `content`, `quotes_content`, `author_id`, `post_category_id`, `status`, `image_avatar`, `view`, `created_at`, `updated_at`) VALUES
(50, 'Ích mẫu hạ áp điều kinh', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"“Ích mẫu hoạt huyết điều kinh/ Lại thêm hạ áp, kháng sinh, giảm phù”. Ảnh: P.C.T\" src=\"http://baodanang.vn/dataimages/201808/original/images1463075_ich_mau.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&ldquo;&Iacute;ch mẫu hoạt huyết điều kinh/ Lại th&ecirc;m hạ &aacute;p, kh&aacute;ng sinh, giảm ph&ugrave;&rdquo;. Ảnh: P.C.T</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&Iacute;ch mẫu, c&ograve;n c&oacute; t&ecirc;n Sung &uacute;y, C&acirc;y ch&oacute;i đ&egrave;n, t&ecirc;n khoa học&nbsp;Leonurus japonicus Houtt (đồng nghĩa L. heterophyllus&nbsp;Sweet), thuộc họ Hoa m&ocirc;i - Lamiaceae.</p>\r\n\r\n<p>Theo Từ điển c&acirc;y thuốc Việt Nam, &Iacute;ch mẫu l&agrave; c&acirc;y thảo sống hằng năm cao hơn 1m. Th&acirc;n vu&ocirc;ng, &iacute;t ph&acirc;n nh&aacute;nh. L&aacute; mọc đối, c&oacute; gốc gần như tr&ograve;n, m&eacute;p c&oacute; răng cưa rộng, c&aacute;c l&aacute; giữa d&agrave;i, xẻ th&ugrave;y, c&aacute;c l&aacute; ở ngọn ngắn, &iacute;t xẻ hoặc nguy&ecirc;n. Hoa trắng hồng hoặc t&iacute;m hồng xếp th&agrave;nh v&ograve;ng d&agrave;y đặc ở n&aacute;ch l&aacute;. Quả nhỏ, c&oacute; 3 cạnh, c&oacute; m&agrave;u n&acirc;u x&aacute;m. C&acirc;y ra hoa th&aacute;ng 3-5, c&oacute; quả th&aacute;ng 6-7. D&ugrave;ng to&agrave;n c&acirc;y l&agrave;m thuốc gọi l&agrave; &Iacute;ch mẫu thảo, nếu lấy ri&ecirc;ng quả gọi l&agrave; Sung &uacute;y tử.</p>\r\n\r\n<p>&Iacute;ch mẫu l&agrave; lo&agrave;i li&ecirc;n nhiệt đới mọc tự nhi&ecirc;n ở v&ugrave;ng đồng bằng v&agrave; trung du, &iacute;t gặp ở v&ugrave;ng cao. C&acirc;y c&oacute; thể trồng quanh năm nhưng tốt nhất l&agrave; v&agrave;o đầu m&ugrave;a mưa. C&acirc;y ưa đất tơi xốp, tho&aacute;t nước nhưng kh&ocirc;ng k&eacute;n đất n&ecirc;n c&oacute; thể trồng nhiều. Cần xử l&yacute; hạt giống trước khi gieo. Sau khi trồng được 3-4 th&aacute;ng, khi c&acirc;y bắt đầu ra hoa th&igrave; cắt để lại c&aacute;c chồi gốc để c&acirc;y tiếp tục ph&aacute;t triển. Thu hoạch c&acirc;y v&agrave;o l&uacute;c trời nắng, rửa sạch, d&ugrave;ng tươi, hay phơi trong r&acirc;m để h&eacute;o đem nấu cao, hoặc phơi kh&ocirc; để d&ugrave;ng dần.</p>\r\n\r\n<p>Ph&acirc;n t&iacute;ch th&agrave;nh phần h&oacute;a học to&agrave;n c&acirc;y &Iacute;ch mẫu chứa leonurin, atachydrin, leonuridin. &Iacute;ch mẫu Việt Nam chứa 3 alcaloid (trong đ&oacute; c&oacute; alcaloid c&oacute; N bậc 4), 3 flavonosid (trong đ&oacute; c&oacute; rutin), 1 glucosid c&oacute; khung steroid. Hạt chứa leonurin.</p>\r\n\r\n<p>Theo Đ&ocirc;ng y, &nbsp;&Iacute;ch mẫu c&oacute; vị hơi đắng, cay, t&iacute;nh hơi h&agrave;n, c&oacute; t&aacute;c dụng hoạt huyết điều kinh, khử ứ chỉ thống, lợi thủy ti&ecirc;u thũng. Quả &Iacute;ch mẫu c&oacute; vị cay, đắng, t&iacute;nh hơi h&agrave;n, c&oacute; t&aacute;c dụng hoạt huyết điều kinh, l&agrave;m m&aacute;t gan, s&aacute;ng mắt.</p>\r\n\r\n<p>C&aacute;c hoạt chất của &Iacute;ch mẫu c&oacute; t&aacute;c dụng tr&ecirc;n tử cung, huyết &aacute;p, tim mạch, hệ thần kinh, kh&aacute;ng sinh đối với một số vi tr&ugrave;ng; ngo&agrave;i ra c&oacute; t&aacute;c dụng đối với vi&ecirc;m thận v&agrave; ph&ugrave; thũng cấp.</p>\r\n\r\n<p>&Iacute;ch mẫu thường được d&ugrave;ng chữa kinh nguyệt bế tắc, m&aacute;u ứ t&iacute;ch tụ sau khi sinh đẻ, trước khi thấy kinh đau bụng hoặc kinh ra qu&aacute; nhiều, l&agrave;m an thai, giảm đau, l&agrave;m dễ đẻ; c&ograve;n chữa vi&ecirc;m thận, ph&ugrave; thũng, giảm niệu, đ&aacute;i ra m&aacute;u, cao huyết &aacute;p. Hạt d&ugrave;ng v&agrave;o thuốc phụ khoa, l&agrave;m cho dạ con mau co lại, co tử cung, l&agrave;m thuốc lợi tiểu v&agrave; s&aacute;ng mắt. Liều d&ugrave;ng 9-30g c&acirc;y (th&acirc;n l&aacute;) hoặc d&ugrave;ng 4,5-9g hạt, sắc nước uống. Cũng c&oacute; thể d&ugrave;ng c&acirc;y nấu cao. D&ugrave;ng ri&ecirc;ng hoặc phối hợp với Ngải cứu, Hương phụ, Nghệ đen. D&ugrave;ng ngo&agrave;i lấy c&acirc;y tươi gi&atilde; đặp trị mụn nhọt, vi&ecirc;m da, lở ngứa, sưng v&uacute; v&agrave; chốc đầu.</p>\r\n\r\n<p><strong>Đơn thuốc:</strong></p>\r\n\r\n<p>1. Kinh nguyệt kh&ocirc;ng đều, thấy kinh trước kỳ (chu kỳ ngắn), kinh &iacute;t, đau bụng trước khi thấy kinh: D&ugrave;ng 20g th&acirc;n l&aacute; sắc uống 10 ng&agrave;y kể từ ng&agrave;y thứ 14 sau kỳ kinh. Hoặc d&ugrave;ng cao &Iacute;ch mẫu 6-8g mỗi ng&agrave;y.</p>\r\n\r\n<p>2. Vi&ecirc;m thận cấp v&agrave; ph&ugrave; thũng: &Iacute;ch mẫu tươi 180-240g, nấu với 700ml nước v&agrave; c&ocirc; lại c&ograve;n 300ml, chia 2 lần uống trong ng&agrave;y.</p>\r\n\r\n<p>3. Chữa sau khi đẻ ph&ugrave; thũng, hoặc c&oacute; thai đi đứng nhiều, xuống m&aacute;u ch&acirc;n: D&ugrave;ng &Iacute;ch mẫu 20g, Ngưu tất, Rau dừa nước mỗi vị 15g sắc uống.</p>\r\n\r\n<p>4. Suy nhược to&agrave;n th&acirc;n ở phụ nữ: &Iacute;ch mẫu 30-60g, nấu với trứng g&agrave; hay thịt g&agrave; m&agrave; ăn.</p>\r\n\r\n<p>5. Chữa can nhiệt, mắt đỏ sưng đau: Quả &Iacute;ch mẫu, C&uacute;c hoa, hạt Muỗng, hạt M&agrave;o g&agrave; trắng, Sinh địa, mỗi vị 10g, sắc nước uống.</p>\r\n\r\n<p>6. Cao Hương ngải: Đ&acirc;y l&agrave; b&agrave;i thuốc của GS.TS. Đỗ Tất Lợi b&agrave;o chế, d&ugrave;ng &Iacute;ch mẫu, Ngải cứu, Hương phụ, Bạch đồng nữ mỗi thứ 1-2g, sắc c&ocirc; đặc c&ograve;n 10ml, th&ecirc;m đường đ&oacute;ng ống hấp tiệt tr&ugrave;ng. Ngo&agrave;i c&ocirc;ng dụng chữa bệnh phụ khoa (kinh nguyệt kh&ocirc;ng đều, thấy kinh đau bụng, kh&oacute; sinh nở, kh&iacute; hư bạch đới) c&ograve;n c&oacute; t&aacute;c dụng hạ huyết &aacute;p n&ecirc;n về sau đổi t&ecirc;n l&agrave; thuốc ống HA1.</p>\r\n\r\n<p>Nếu kh&ocirc;ng c&oacute; thuốc đ&oacute;ng ống sẵn, c&oacute; thể d&ugrave;ng b&agrave;i thuốc th&agrave;nh phần như tr&ecirc;n với liều 4-6g mỗi vị, sắc 300ml nước giữ s&ocirc;i trong nửa giờ, th&ecirc;m ch&uacute;t đường, chia uống 2 lần trong ng&agrave;y.</p>', 'Một nhà thơ từng ví von tác phẩm “Những cây thuốc và vị thuốc Việt Nam” của GS.TS.Đỗ Tất Lợi là một áng Kiều trong y học. Cuốn sách dược liệu này có một cách phân loại độc đáo khi đưa phần các cây thuốc và vị thuốc chữa bệnh phụ nữ lên hàng đầu, trong đó vị trí đầu tiên dành cho cây Ích mẫu.', '5', '9', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/IPS3GivsPybfEJBzM07YKJilhsnrOQnZezVDQNDs.jpeg', 2, '2018-08-05 21:32:48', '2018-08-05 21:49:03'),
(51, 'Phát hiện đối tượng chuyên trộm mỹ phẩm cao cấp', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Lực lượng an ninh hàng không lập biên bản ban đầu trước khi giao cho Công an thành phố điều tra.\" src=\"http://baodanang.vn/dataimages/201808/original/images1462584_a5.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Lực lượng an ninh h&agrave;ng kh&ocirc;ng lập bi&ecirc;n bản ban đầu trước khi giao cho C&ocirc;ng an th&agrave;nh phố điều tra.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trước đ&oacute;, tối 27-7, Bai Xinxing c&ugrave;ng nh&oacute;m bạn đến s&acirc;n bay l&agrave;m thủ tục đi T&acirc;y An (Trung Quốc) tr&ecirc;n chuyến bay VietJet 8906.</p>\r\n\r\n<p>Trước khi l&ecirc;n m&aacute;y bay, nh&oacute;m người n&agrave;y đến quầy h&agrave;ng mỹ phẩm cao cấp rồi giả vờ hỏi gi&aacute; để che tầm nh&igrave;n nh&acirc;n vi&ecirc;n cũng như camera an ninh để Bai Xinxing th&ograve; tay lấy một loại sản phẩm hiệu Clarins - m&atilde; sản phẩm 2064835147185 c&oacute; gi&aacute; hơn 1.000 USD (khoảng 23 triệu đồng). Sau đ&oacute;, Bai Xinxing bỏ sản phẩm trộm được v&agrave;o t&uacute;i x&aacute;ch rồi bước ra ngo&agrave;i.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, h&agrave;nh vi trộm cắp của Bai Xinxing đ&atilde; bị một nh&acirc;n vi&ecirc;n kh&aacute;c trong quầy ph&aacute;t hiện, tri h&ocirc;. Ngay sau đ&oacute;, lực lượng l&agrave;m nhiệm vụ trong s&acirc;n bay y&ecirc;u cầu Bai Xinxing cho kiểm tra t&uacute;i x&aacute;ch v&agrave; lấy lại sản phẩm. Ban đầu, đối tượng chối tội, nhưng trước chứng cứ c&oacute; được, Bai Xinxing đ&atilde; thừa nhận h&agrave;nh vi của m&igrave;nh.</p>\r\n\r\n<p>Theo An ninh h&agrave;ng kh&ocirc;ng Đ&agrave; Nẵng, sau khi tr&iacute;ch xuất h&igrave;nh ảnh camera, trước đ&oacute;, lực lượng chức năng đ&atilde; ph&aacute;t hiện Bai Xinxing nhiều lần thực hiện h&agrave;nh vi trộm cắp tương tự. Hiện cơ quan C&ocirc;ng an đang tiếp tục điều tra mở rộng.</p>', 'ĐNO - Trung tâm An ninh hàng không Đà Nẵng cho biết, đã bàn giao đối tượng Bai Xinxing (SN 1986, quốc tịch Trung Quốc) cho Công an thành phố để tiếp tục điều tra theo quy định của pháp luật.', '5', '10', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/kHs7mrpYPjHm2Bir9qyxNClhiwGMYspGCMjWrDCj.jpeg', 0, '2018-08-05 21:34:13', '2018-08-05 21:37:57'),
(52, 'Chờ bổ sung quy hoạch dự án kênh Khe Cạn', '<table align=\"center\" style=\"width:660px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Tuyến kênh hở Khe Cạn đoạn qua tổ 26 phường Thanh Khê Tây luôn trong tình trạng ô nhiễm.\" src=\"http://baodanang.vn/dataimages/201807/original/images1462230_29_7__Khe_Can.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tuyến k&ecirc;nh hở Khe Cạn đoạn qua tổ 26 phường Thanh Kh&ecirc; T&acirc;y lu&ocirc;n trong t&igrave;nh trạng &ocirc; nhiễm.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Dự &aacute;n được triển khai từ th&aacute;ng 8-2016 nhưng đến nay mới thực hiện khoảng 1.500 m&eacute;t cống hộp, c&ograve;n khoảng 500 m&eacute;t k&ecirc;nh hở k&eacute;o d&agrave;i từ hồ B&agrave;u Trảng đến s&ocirc;ng Ph&uacute; Lộc lu&ocirc;n trong t&igrave;nh trạng &ocirc; nhiễm, nước chuyển sang m&agrave;u đen, bốc m&ugrave;i h&ocirc;i thối, ảnh hưởng nghi&ecirc;m trọng đến đời sống của người d&acirc;n dọc tuyến k&ecirc;nh.</p>\r\n\r\n<p>T&igrave;nh trạng &ocirc; nhiễm diễn ra v&agrave;i năm nay, người d&acirc;n phản &aacute;nh, b&aacute;o ch&iacute; l&ecirc;n tiếng, ch&iacute;nh quyền v&agrave;o cuộc, dự &aacute;n k&ecirc;nh Khe Cạn được triển khai nhưng hiện dang dở v&igrave; c&oacute; gần 300 hồ sơ vướng mắc về giải ph&oacute;ng mặt bằng, li&ecirc;n quan đến t&iacute;nh ph&aacute;p l&yacute; của c&aacute;c hồ sơ thu hồi đất.</p>\r\n\r\n<p>Trước thực tế n&agrave;y, l&atilde;nh đạo th&agrave;nh phố chỉ đạo C&ocirc;ng ty Tho&aacute;t nước v&agrave; xử l&yacute; nước thải Đ&agrave; Nẵng xử l&yacute;, gom nguồn nước thải k&ecirc;nh Khe Cạn, đoạn từ đường Y&ecirc;n Thế - Bắc Sơn, Hồng Th&aacute;i - T&acirc;n Tr&agrave;o về xử l&yacute; tại Trạm xử l&yacute; nước thải (XLNT) Ph&uacute; Lộc, trước khi thải ra m&ocirc;i trường. Tuy nhi&ecirc;n, do vướng giải ph&oacute;ng mặt bằng n&ecirc;n việc n&agrave;y vẫn chưa được thực hiện.</p>\r\n\r\n<p>&Ocirc;ng Hồng Vinh Hiển, Ph&oacute; Gi&aacute;m đốc Ban quản l&yacute; c&aacute;c dự &aacute;n hạ tầng Đ&agrave; Nẵng - đơn vị được giao triển khai dự &aacute;n k&ecirc;nh Khe Cạn cho biết, c&ocirc;ng tr&igrave;nh tuyến cống tho&aacute;t nước Khe Cạn (giai đoạn 1 từ chợ Chiều, kiệt 341 T&ocirc;n Đản đến s&ocirc;ng Ph&uacute; Lộc) c&oacute; chiều d&agrave;i 2.156 m&eacute;t.</p>\r\n\r\n<p>Về l&yacute; do c&ocirc;ng tr&igrave;nh chưa ho&agrave;n th&agrave;nh, &ocirc;ng Hiển khẳng định một số đoạn dang dở do vướng c&ocirc;ng t&aacute;c giải ph&oacute;ng mặt bằng. Đơn cử, đoạn từ chợ Chiều đến đường sắt (khu vực ng&atilde; ba Huế) đ&atilde; thi c&ocirc;ng cơ bản ho&agrave;n th&agrave;nh 1.616m/1.656m), c&ograve;n 40 m&eacute;t chưa triển khai do vướng 2 hộ (hộ &ocirc;ng V&otilde; Dũng - số hồ sơ 184; hộ &ocirc;ng Nguyễn Thanh Vũ - số hồ sơ 190BS) chưa b&agrave;n giao mặt bằng.</p>\r\n\r\n<p>Đoạn 500 m&eacute;t k&ecirc;nh hở, từ đường sắt chạy qua địa phận phường Thanh Kh&ecirc; T&acirc;y, c&oacute; tổng diện t&iacute;ch quy hoạch 64.834m2, tổng hồ sơ số thửa bị ảnh hưởng giải tỏa 375 hồ sơ, ri&ecirc;ng trong phạm vi thi c&ocirc;ng tuyến cống tho&aacute;t nước Khe Cạn l&agrave; 107 hồ sơ. Đến nay, cơ quan chức năng đ&atilde; kiểm định, x&eacute;t t&iacute;nh ph&aacute;p l&yacute; của 299 hồ sơ nhưng chưa triển khai giải ph&oacute;ng mặt bằng do vướng đền b&ugrave;, giải tỏa. &nbsp;</p>\r\n\r\n<p>Trước đ&oacute;, tại C&ocirc;ng văn số 206/VP-QLĐTư ng&agrave;y 26-1-2018, UBND th&agrave;nh phố đ&atilde; giao cho Ban quản l&yacute; dự &aacute;n thực hiện khơi th&ocirc;ng d&ograve;ng chảy, đồng thời nghi&ecirc;n cứu phương &aacute;n thu gom nước thải dọc tuyến k&ecirc;nh Khe Cạn để giảm thiểu &ocirc; nhiễm m&ocirc;i trường tại khu vực, gửi Sở X&acirc;y dựng kiểm tra, b&aacute;o c&aacute;o UBND th&agrave;nh phố xem x&eacute;t, quyết định.</p>\r\n\r\n<p>UBND th&agrave;nh phố ban h&agrave;nh C&ocirc;ng văn số 1494/VP-QLĐTư ng&agrave;y 11-6-2018 cho ph&eacute;p bổ sung hạng mục thu gom v&agrave; chuyển tải nước thải về Trạm XLNT Ph&uacute; Lộc; về l&acirc;u d&agrave;i vẫn thực hiện x&acirc;y dựng đoạn cống Khe Cạn theo quy hoạch v&agrave; thiết kế được duyệt.</p>\r\n\r\n<p>Cũng theo &ocirc;ng Hồng Vinh Hiển, Ban quản l&yacute; dự &aacute;n đang chỉ đạo đơn vị tư vấn triển khai lập hồ sơ thiết kế tuyến ống thu gom, chuyển tải nước thải về Trạm XLNT Ph&uacute; Lộc theo chủ trương của UBND th&agrave;nh phố. C&ocirc;ng tr&igrave;nh dự kiến ho&agrave;n th&agrave;nh ph&ecirc; duyệt hồ sơ thiết kế trong th&aacute;ng 9-2018, khởi c&ocirc;ng v&agrave;o cuối th&aacute;ng 9-2018 v&agrave; ho&agrave;n th&agrave;nh trong qu&yacute; IV-2018.</p>', 'Kênh Khe Cạn chảy qua khu vực các phường Hòa An, Hòa Phát (quận Cẩm Lệ) và phường Thanh Khê Tây (quận Thanh Khê) được quy hoạch thành tuyến kênh khép kín thuộc “Dự án phát triển bền vững” của thành phố.', '5', '11', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/YS5YOhn6ZbyOi97U9c4q3Ol78yrs3NoA1b1u0Vhg.jpeg', 0, '2018-08-05 21:35:32', '2018-08-05 21:37:49'),
(53, 'Cầu lông Đà Nẵng: Vượt khó để giữ phong trào', '<table align=\"center\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Để phong trào Cầu lông Đà Nẵng giữ vững và phát triển tốt hơn, chắc chắn, chỉ có sự nỗ lực của DBF là không đủ. 		  Ảnh: ANH VŨ\" src=\"http://baodanang.vn/dataimages/201808/original/images1463536_Badminton.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Để phong tr&agrave;o Cầu l&ocirc;ng Đ&agrave; Nẵng giữ vững v&agrave; ph&aacute;t triển tốt hơn, chắc chắn, chỉ c&oacute; sự nỗ lực của DBF l&agrave; kh&ocirc;ng đủ. Ảnh: ANH VŨ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong khi đ&oacute;, Li&ecirc;n đo&agrave;n Cầu l&ocirc;ng Đ&agrave; Nẵng (DBF) ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; kinh ph&iacute; để ph&aacute;t triển phong tr&agrave;o. Kh&ocirc;ng những thế, phong tr&agrave;o Cầu l&ocirc;ng của th&agrave;nh phố cũng chững lại v&agrave; c&oacute; dấu hiệu đi xuống do nhiều yếu tố kh&aacute;ch quan.</p>\r\n\r\n<p>Bắt nguồn từ c&aacute;c chi ph&iacute; cho cầu l&ocirc;ng (trang phục, cầu, vợt&hellip;) tăng dần, khiến niềm đam m&ecirc; của những người y&ecirc;u th&iacute;ch bộ m&ocirc;n n&agrave;y bị ảnh hưởng. Mặt kh&aacute;c, việc c&oacute; nhiều sự lựa chọn hoạt động thể thao như: chạy bộ, đạp xe đạp, tập dưỡng sinh, bơi&hellip; ảnh hưởng nhất định đến hoạt động của phong tr&agrave;o.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, d&ugrave; đối tượng trẻ tham gia tập luyện cầu l&ocirc;ng tăng, song hầu hết l&agrave; học sinh v&agrave; chỉ tập trung chơi trong dịp h&egrave;, thời gian c&ograve;n lại th&igrave; phong tr&agrave;o lắng xuống. Kh&ocirc;ng ngẫu nhi&ecirc;n khi thời điểm hiện tại, số lượng CLB Cầu l&ocirc;ng tr&ecirc;n địa b&agrave;n th&agrave;nh phố giảm từ 55 CLB xuống c&ograve;n khoảng 40 CLB v&agrave; số người tập luyện cầu l&ocirc;ng giảm khoảng 20% so với trước.</p>\r\n\r\n<p>Đ&acirc;y ch&iacute;nh l&agrave; nỗi trăn trở rất lớn của DBF. Kh&oacute; khăn cũng l&agrave; động lực để DBF quyết t&acirc;m tổ chức giải Cầu l&ocirc;ng truyền thống c&aacute;c CLB th&agrave;nh phố lần thứ 23 (2018) với mục ti&ecirc;u lớn nhất l&agrave; duy tr&igrave; phong tr&agrave;o.</p>\r\n\r\n<p>Thường trực DBF đ&atilde; k&ecirc;u gọi sự đ&oacute;ng g&oacute;p, hỗ trợ, t&agrave;i trợ từ nhiều nguồn với mục ti&ecirc;u kh&ocirc;ng để giải Cầu l&ocirc;ng truyền thống c&aacute;c CLB th&agrave;nh phố bị gi&aacute;n đoạn. Sự tham gia đ&uacute;ng l&uacute;c của nh&agrave; t&agrave;i trợ New Star (Bắc Giang) phần n&agrave;o giải tỏa những kh&oacute; khăn m&agrave; DBF phải đối mặt.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, C&ocirc;ng ty CP Dệt - may 29-3 tiếp tục l&agrave; một trong những đơn vị t&agrave;i trợ ch&iacute;nh cho giải. Tuy nhi&ecirc;n, theo Ph&oacute; Chủ tịch ki&ecirc;m Tổng Thư k&yacute; DBF L&ecirc; Nguyễn Tường L&acirc;n, để giải quyết những kh&oacute; khăn về kinh ph&iacute;, c&aacute;c hoạt động tuy&ecirc;n truyền trực quan sẽ phải giảm thiểu, c&aacute;c nội dung thi đấu r&uacute;t gọn từ 10 nh&oacute;m tuổi c&ograve;n 5 nh&oacute;m tuổi (18 tuổi, 19-30 tuổi, 31-40 tuổi, 41-50 tuổi v&agrave; 51 tuổi trở l&ecirc;n). Mặt kh&aacute;c, số lượng CLB kh&aacute;ch mời c&oacute; thể giảm so với những giải trước.</p>\r\n\r\n<p>Nỗ lực của DBF với phong tr&agrave;o Cầu l&ocirc;ng th&agrave;nh phố rất đ&aacute;ng tr&acirc;n trọng. Nhưng chắc chắn, với nỗ lực tự th&acirc;n của một tổ chức x&atilde; hội nghề nghiệp về thể thao tự nguyện như DBF cũng sẽ kh&ocirc;ng đủ, nếu thiếu sự hỗ trợ của ng&agrave;nh TDTT cũng như mối quan t&acirc;m của to&agrave;n x&atilde; hội d&agrave;nh cho bộ m&ocirc;n n&agrave;y.</p>', 'Chuẩn bị bước vào tuổi 23, nhưng giải Cầu lông truyền thống các CLB Đà Nẵng gặp quá nhiều khó khăn, thậm chí có thể tạm hoãn bởi kinh phí tổ chức gần như “trắng”. Đáng kể nhất là Hãng Cầu 999, đơn vị gắn bó với Cầu lông Đà Nẵng cũng như giải Cầu lông truyền thống các CLB thành phố suốt 22 năm qua không thể tiếp tục đồng hành, do khó khăn của đơn vị này.', '5', '12', '2', 'http://localhost/theDaNangNews/local/storage/app/author@gmail.com/Kg3neG2VRp0oFodgfp8ePBiKzV2yV8PZyw5OIhcN.jpeg', 0, '2018-08-05 21:36:44', '2018-08-05 21:37:22'),
